style.css:
@-moz-document domain("overrustle.com") {
input.form-control,
select.form-control,
.btn-group>.navbar-btn,
.input-group-btn>.btn,
#channelUsername {
    background-color: #222;
    color: #FFFFFF;
    border-color: #f20000;
}
input.form-control::-webkit-input-placeholder {
    color: #FFFFFF;
}
input.form-control::-moz-placeholder {
    color: #FFFFFF;
}
input.form-control:-ms-input-placeholder {
    color: #FFFFFF;
}
input.form-control:-moz-placeholder {
    color: #FFFFFF;
}
}
