style.css:
@-moz-document domain("zestedesavoir.com") {
    .header-container {
        margin-top: 0px;
    }
    .header-container header {
        position: fixed;
        width: 100%;
        top: 0;
        height: 40px;
        z-index: 100;
    }
    .header-container header .header-logo-link {
        height: 40px;
    }
    #menu {
        height: 40px;
    }
    .header-menu-list {
        height: 40px;
    }
    #menu .mobile-menu-link {
        height: 40px;
        line-height: 40px;
    }
    .sub-header, .flexpage-header {
        margin-top: 43px !important;
    }
    .header-container .logbox.header-right {
        height: 40px;
    }
    .header-right .notifs-links a.ico-link {
        height: 40px !important;
    }
    .header-right .avatar {
        height: 40px;
        width: 40px;
        margin-left: 10px;
    }
    .dropdown {
        top: 40px;
    }
    .home-header {
        margin-top: 40px;
    }

    .logbox.header-right.unlogged {
        height: 40px;
    }

    .logbox.header-right.unlogged a {
        height: 40px;
        line-height: 40px;
    }

    .logbox.header-right {
        width: 200px;
    }

    #my-account {
        width: 40px;
        height: 40px;
    }

    #my-account img {
        margin-left: 0px;
    }
}
