style.css:
@-moz-document domain("zooqle.com") {
* {
    text-shadow: none !important;
    transition: color .25s, background-color .25s !important;
}
body {
    font-family: Tahoma, Verdana, Lucida Grande, Lucida Sans, Arial, sans-serif !important;
    color: #999 !important;
    background-color: #111 !important;
}
a,
h4 {
    color: #999 !important;
    font-weight: bold !important;
}
a:hover,
.col-md-12,
.rm-trigger:hover {
    color: #666 !important;
}
.panel-body,
#inputSearchTop,
.list-group-item {
    color: #ddd !important;
    border: none !important;
    background-color: #222 !important;
}
.navbar-default {
    border: none !important;
    background-color: #333 !important;
    background-image: none !important;
}
#tvbox,
#movbox {
    border: none !important;
    background-color: #222 !important;
    background-image: none !important;
}
#tvbox a:not(.btn),
#movbox a,
.table-torrents a,
.list-group-item a
/*, #dlPanel a*/

{
    color: #999 !important;
}
#tvbox a:hover:not(.btn),
#movbox a:hover,
.table-torrents a:hover,
.navbar-brand:hover,
footer a:hover,
.text-center a:hover,
#tvlist a:hover,
.list-group-item a:hover
/*, #dlPanel a:hover*/

{
    color: #ccc !important;
}
.table-hover>tbody>tr:hover {
    color: #666 !important;
    background-color: #ddd !important;
}
.text-nowrap
/*, .nav>li>a:hover*/

{
    background: none !important;
}
.cell {
    border: none !important;
    border-radius: 10px !important;
}
.nav-pills.compact>li.active>a {
    background-color: #ccc !important;
    color: #333 !important;
}
.badge-green {
    background: #666 !important;
}
.btn-success {
    color: #999 !important;
    background: #fff !important;
    border-color: #ccc !important;
}
.trans60,
.badge.trans50 {
    opacity: 1 !important;
}
.imdb_widget:hover span {
    color: inherit !important;
}
.table-torrents tr td {
    border-color: #333 !important;
}
.modal-header,
.modal-footer,
.navbar .divider-vertical {
    border: none !important;
}
#inputSearchTop {
    border-top-left-radius: 4px !important;
    border-bottom-left-radius: 4px !important;
}
.navbar-header .btn,
.navbar-header .form-control,
.modal-content {
    background-color: #222 !important;
}
.btn-primary {
    border-color: #333 !important;
    background-color: #555 !important;
    background-image: none !important;
}
.btn-primary:hover {
    background-color: #444 !important;
}
.cell:hover,
.nav-stacked a:hover,
.pnltabs a:hover,
.table-hover>tbody>tr:hover {
    background-color: #111 !important;
}
.table-objects tbody tr,
.table-objects thead tr {
    border-color: #ddd !important;
}
.btn-default.active,
.pagination .active a {
    background-color: #222 !important;
    border-color: #444 !important;
    color: #5697ff !important;
}
.btn-default,
.pagination a,
.rm-trigger:hover {
    background-color: #222 !important;
    border-color: #444 !important;
}
.dropdown-menu {
    background-color: #222 !important;
}
.dropdown-menu a.small:hover,
.dropdown.open a:hover {
    background-color: #444 !important;
    background-image: none !important;
    color: #888 !important;
}
.smaller.trans80 {
    opacity: 1 !important;
}
.nav>li>a:hover,
.nav>li>a:focus {
    background-color: #444 !important;
}
a,
a:hover {
    text-decoration: none !important;
}
.panel-group .panel-heading,
.panel .panel-default .eplist {
    background-color: #333 !important;
}
.list-group.eplist {
    margin-top: 0px !important;
}
.list-group.eplist li {
    margin: 0px !important;
}
#logo {
    background-image: url(data:image/png;base64,);
}
/*[[column]]*/
}
