}
.clipboard-input-inner button.copy:after {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_content_copy_white_24px.svg);
}
.clipboard-input-inner button:hover:after {
    animation: pulse-opacity .38s infinite;
}
.instant-invite .clipboard-input-copied button.copy:after {
    visibility: hidden;
}
/* Voice Channels */

.guild-channels .channel-voice.audio a {
    color: rgba(255, 255, 255, .8);
}
.guild-channels .channel-voice.audio:hover a {
    color: rgba(255, 255, 255, .8) !important;
}
.guild-channels .channel-voice.audio .channel-arrow,
.guild-channels .channel-voice.audio .icon-lock {
    opacity: 0;
}
.guild-channels .channel-voice .channel-arrow {
    transition: opacity .3s, margin .3s;
    opacity: 0;
    margin-right: -16px;
}
.guild-channels .channel-voice:hover .channel-arrow {
    opacity: 1;
    margin-right: 8px;
}
.guild-channels .channel-user-limit {
    background: #111;
}
.guild-channels .channel-voice:not(.full):hover a,
.guild-channels .channel-voice:not(.locked):hover a {
    color: rgba(255, 255, 255, .5);
}
.guild-channels .channel-voice:hover a {
    animation: pulse .38s infinite;
}
.guild-channels .channel-voice .channel-arrow:hover {
    background-color: #111;
}
.guild-channels .channel-voice-states li.speaking {
    color: rgba(255, 255, 255, .8) !important;
}
.guild-channels .channel-voice-states li.speaking:hover {
    animation: pulse .38s infinite;
}
.guild-channels .channel-voice.audio li {
    color: rgba(255, 255, 255, .5);
}
.guild-channels .channel-voice.audio li:hover {
    animation: pulse .38s infinite;
}
.guild-channels .channel-voice-states li {
    color: rgba(255, 255, 255, .5);
}
.guild-channels .channel-voice-states li:hover {
    color: rgba(255, 255, 255, .5);
    animation: pulse .38s infinite;
}
.guild-channels .channel {
    text-transform: lowercase;
}
/* Voice Channel Avatars */

.guild-channels .channel-voice-states li.speaking .avatar-small {
    border: 2px solid #e53232;
}
.guild-channels .channel-voice:hover .channel-voice-states li .avatar-small {
    width: 32px;
    height: 32px;
    background-size: 32px 32px;
}
.guild-channels .channel-voice .channel-voice-states li .avatar-small {
    transition: width .3s ease 1s, height .3s ease 1s, background-size .3s ease 1s;
    transition-delay: 1s;
}
.guild-channels .channel-voice-states li.speaking .avatar-small:hover,
.guild-channels .channel-voice-states li.speaking .draggable:hover .avatar-small {
    border-color: #e53232;
}
/*////////////////*/
/* Profile window */
/*////////////////*/
/* namebox */

.user-popout {
    box-shadow: 0 0 16px rgba(0, 0, 0, .8);
}
.user-popout .header {
    background: #111;
}
.user-popout .avatar-wrapper .avatar-popout {
    border: 4px solid #1c1c1c;
}
.user-popout .avatar-wrapper .avatar-hint {
    color: transparent;
}
.user-popout .avatar-wrapper:hover .avatar-hint {
    transition: .3s;
}
.user-popout .username-wrapper .nickname {
    color: #e53232;
}
.user-popout .username-wrapper.has-nickname .username {
    color: rgba(255, 255, 255, .8);
}
.user-popout .username-wrapper.has-nickname .discriminator {
    color: rgba(255, 255, 255, .5);
    margin-left: 3px;
}
.user-popout .username-wrapper .activity {
    color: rgba(255, 255, 255, .5);
}
.user-popout .username-wrapper .username {
    color: #e53232;
}
.user-popout .username-wrapper .discord-tag {
    display: inline-block;
}
.user-popout .username-wrapper .discord-tag .discriminator {
    display: inline-block;
    color: rgba(255, 255, 255, .0);
    transition: .3s;
    max-width: 0;
}
.user-popout .username-wrapper .discord-tag:hover .discriminator {
    color: rgba(255, 255, 255, .5);
    padding-left: 3px;
    max-width: 40px;
}
.user-popout .username-wrapper .discord-tag .username {
    max-width: 200px;
    transition: .3s;
}
.user-popout .username-wrapper .discord-tag:hover .username {
    max-width: 160px;
}
.bot-tag.bot-tag-invert {
    background: #111;
    color: #e53232 !important;
    margin-left: 3px;
    font-size: 13px;
}
#user-profile-modal .header .activity {
    color: rgba(255, 255, 255, .5);
}
/* rolebox */

.user-popout .body {
    background: #111;
}
.user-popout .body .section .label {
    color: #e53232;
}
.member-roles .member-role {
    background-color: #1c1c1c !important;
    border: 1px solid #1c1c1c;
    color: rgba(255, 255, 255, .8);
    font-weight: 500;
}
.user-popout .footer .quick-message-wrapper .input {
    border: 1px solid #1c1c1c;
    background: #1c1c1c;
}
/* add role */

#autocomplete-popout h3 {
    color: #e53232;
    text-transform: uppercase;
    font-weight: 500;
}
#autocomplete-popout section {
    padding-top: 12px;
}
#autocomplete-popout .row.selected {
    background: #1c1c1c !important;
}
/* footer */

.user-popout .footer .update-notice {
    display: none;
}
.user-popout .footer {
    background: #1c1c1c;
    border-top: 1px solid #111;
}
.user-popout .footer .quick-message-wrapper .input {
    color: rgba(255, 255, 255, .8);
}
.user-popout .footer .quick-message-wrapper .input::-webkit-input-placeholder {
    color: rgba(255, 255, 255, .5);
}
.user-popout .footer .quick-message-wrapper .input.isBlocked {
    background: #1c1c1c;
}
/*/////////*/
/* Profile */
/*/////////*/
/* Namebox */

#user-profile-modal .header {
    background-color: #111;
    background-image: none;
    height: 77px;
}
#user-profile-modal .header .discord-tag .username {
    color: #e53232;
}
#user-profile-modal .header .discord-tag .discriminator {
    color: rgba(255, 255, 255, .8);
    margin-left: 3px;
}
#user-profile-modal .avatar-profile {
    border: 5px solid #1c1c1c;
    top: 19px;
    box-shadow: inset 0 0 0 1px #1c1c1c;
}
#user-profile-modal .sub-header {
    background: #111;
}
#user-profile-modal .btn {
    background-color: #1c1c1c;
    color: rgba(255, 255, 255, .8);
    font-weight: 500;
}
#user-profile-modal .btn:hover {
    background-color: #1c1c1c;
    color: rgba(255, 255, 255, .8);
    animation: pulse .38s infinite;
}
#user-profile-modal .tab-bar {
    background: #111;
    border-bottom: 1px solid #111;
}
#user-profile-modal .guilds .guild {
    font-weight: 500;
}
#user-profile-modal .guilds .guild .discriminator {
    margin-left: 3px;
}
#user-profile-modal .avatar-profile .status {
box-shadow: inset 0 0 0 0px #1c1c1c;
    border: 4px solid #1c1c1c;
}
.tab-bar.TOP .tab-bar-item {
    color: rgba(255, 255, 255, .8);
    font-weight: 500;
}
.tab-bar.TOP .tab-bar-item:hover {
    border-bottom: 2px solid rgba(255, 255, 255, .8);
    color: rgba(255, 255, 255, .8);
    animation: pulse .38s infinite;
}
.tab-bar.TOP .tab-bar-item.selected {
    border-bottom: 2px solid #e53232;
    color: #e53232;
}
.bot-tag {
    background: transparent;
    font-size: 13px;
    font-weight: 600;
    margin-left: 3px;
    color: rgba(255, 255, 255, .8) !important;
}
/* context menu */

.context-menu {
    background: #1c1c1c;
    box-shadow: 0 0 16px rgba(0, 0, 0, .8);
}
.context-menu .item.danger {
    color: rgba(255, 255, 255, .8)!important;
}
.context-menu .item:hover {
    background: #1c1c1c;
}
.context-menu .item.danger:hover {
    color: rgba(255, 255, 255, .8)!important;
    animation: pulse .38s infinite;
}
/* infobox */

#user-profile-modal .scroller {
    background: #111;
    box-shadow: inset 0px 16px 35px -19px rgba(0, 0, 0, .8);
}
#user-profile-modal .guilds .guild {
    color: rgba(255, 255, 255, .8);
}
#user-profile-modal .guilds .guild:hover {
    background: #111;
    color: rgba(255, 255, 255, .8);
    animation: pulse .38s infinite;
}
#user-profile-modal footer {
    border-top: 1px solid #111;
    background: #111;
}
#user-profile-modal footer .btn.add-friend {
    background: #e63c3c;
}
#user-profile-modal footer .btn.add-friend:hover {
    background: #e63c3c;
}
#user-profile-modal .empty {
    background: #111;
}
#user-profile-modal .empty .empty-icon-friends {
    display: none;
}
#user-profile-modal .empty .empty-text {
    color: rgba(255, 255, 255, .5);
    font-weight: 500;
}
#user-profile-modal footer .btn.disabled {
    background: #1c1c1c;
}
#user-profile-modal footer .btn.disabled:hover {
    background: #1c1c1c;
}
/*////////////////*/
/* channel header */
/*////////////////*/
/* header */

.guild-header header {
    background-color: #1c1c1c;
    font-family: Lato;
    color: rgba(255, 255, 255, .8);
    font-weight: 500;
    box-shadow: 0 1px 0 #1c1c1c, inset 0 -1px 0 #1c1c1c;
}
.guild-header header:hover {
    background-color: #1c1c1c;
    animation: pulse .38s infinite;
}
.guild-header header:after {
    background: #1c1c1c;
}
/* dropdown */

.guild-header ul {
    background: #1c1c1c;
    box-shadow: 0 0px 0px rgba(0, 0, 0, .06), inset 0 0px 0 rgba(0, 0, 0, .06);
}
.guild-header ul a {
    color: rgba(255, 255, 255, .5);
    opacity: 1;
}
.guild-header ul li:hover a {
    background-color: #1c1c1c;
}
.guild-header ul a.red-highlight {
    color: #e53232;
}
/*////////////////*/
/* Channel Footer */
/*////////////////*/
/* voice bar */

#voice-connection {
    background: #111;
}
#voice-connection .btn-info {
    border-right: 0px solid #1c1e22;
}
#voice-connection .btn-group {
    border: 0px solid #1c1e22;
}
#voice-connection .btn {
    background: #111;
}
#voice-connection .btn-disconnect {
    box-shadow: inset 0px 0 0 #373b42;
}
#voice-connection .btn-info:after {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_info_white_24px.svg) !important;
    zoom: 2;
}
#voice-connection .btn-disconnect:after {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_call_end_white_24px.svg);
    zoom: 1.3;
}
/* acount bar */

.account .btn-group {
    border: 0px solid #1c1e22;
}
.account {
    background: #111;
}
.account .btn-deafen,
.account .btn-mute {
    border-right: 0px solid #1c1e22;
}
.account .btn {
    background: #111;
}
.account .btn-deafen,
.account .btn-settings {
    box-shadow: inset 0px 0 0 #373b42;
}
.account .btn-settings:after {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_settings_white_24px.svg) !important;
    zoom: 1.2;
}
.account .btn-deafen:after {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_volume_up_white_24px.svg) !important;
    zoom: 1.2;
    opacity: .2;
}
.icon-muted,
.account .btn-mute.disabled:after {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_mic_off_white_24px.svg) !important;
    opacity: .2;
}
.account .btn-mute:after {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_mic_white_24px.svg) !important;
    opacity: 0.2!important;
    zoom: 1.2;
}
.icon-deafened, .account .btn-deafen.disabled:after {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_volume_off_white_24px.svg) !important;
}
/*////////////*/
/* member tab */
/*////////////*/
.theme-dark .channel-members {
    background-color: #111;
    box-shadow: 0 0 16px rgba(0, 0, 0, .8);
}
.theme-dark .channel-members h2 {
    color: rgba(255,255,255,.8);
}
.channel-members .member .member-activity {
    color: rgba(255,255,255,.5);
}
/* notification settings */
/* Server Tab */

.guilds-wrapper .guilds .guild .badge {
    right: 12px;
}
/*/////////*/
/* chatbar */
/*/////////*/
/* type bar */

.theme-dark .channel-textarea-inner {
    border-color: #1c1c1c;
    background-color: #1c1c1c;
}
.theme-dark .chat form {
    background: #111;
    border-top: 0px solid hsla(0, 0%, 100%, .06);
    box-shadow: inset 16px 0px 16px -16px rgba(0, 0, 0, .8);
}
.chat form {
    margin: 0 0px;
    padding-left: 20px;
    padding-right: 20px;
}
.channel-textarea {
    margin: 20px 0 30px;
}
.theme-dark .chat form .typing {
    background-color: #111;
    margin-left: 20px;
}
.channel-textarea-inner .channel-textarea-upload {
    border-radius: 7px !important;
    color: #fff !important;
    border-right-color: rgba(0, 0, 0, 0) !important;
    padding: 3px !important;
    opacity: 0.17;
    background-size: 18px !important;
    transform: scale(1.2) !important;
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_file_upload_white_24px.svg);
    transition: all 100ms ease !important;
}
.theme-dark .channel-textarea-upload:hover {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_file_upload_white_24px.svg);
    background-color: hsla(0,0%,100%,.06);
}
.theme-dark .channel-
textarea.has-results .channel-textarea-inner {
    border-color: rgba(0,0,0,0);
}
.theme-dark .channel-textarea-autocomplete-inner {
    background-color: #1c1c1c;
    border-color: #1c1c1c;
}
.theme-dark .channel-textarea-autocomplete-inner header {
    border-bottom-color: #1c1c1c;
    background-color: #1c1c1c;
    color: #e53232;
}
.theme-dark .channel-textarea-autocomplete-inner ul li.active {
    background: #1c1c1c;
    animation: pulse .38s infinite;
    border-left: 2px solid #e53232;
}
.channel-textarea-autocomplete-inner ul li.active {
    padding-left: 8px;
}
.theme-dark .channel-textarea-autocomplete-inner ul li {
    color: rgba(255,255,255,.8);
}
.theme-dark .channel-textarea-autocomplete-inner:after {
