style.css:
@-moz-document domain("baidu.com") {

/*ad remover*/
.banner-wrap {display: none !important} /*banner between pages*/
.union-ad-bottom {display: none !important} /*banner in the bottom*/
#lastcell-dialog {display: none !important} /*folating ad*/
#ggbtm {display: none !important} /*bottom ad*/
.fengchao-right {display: none !important} /*ad in the right*/
#ft {display: none !important} /*in the ft with ad*/
.search-aside-adWrap{display: none !important} /*searh result ad*/


}

@-moz-document regexp(".*wenku.baidu.com/view.*") {
.banner-wrap.more-btn-banner {display: block !important} /*banner for next page*/
.header-wrap {display: none !important}
.crubms-wrap {display: none !important}
.user-bar {display: none !important} /*user bar*/
.aside {display: none !important} /*side bar*/

#doc-header-test {display: none !important}

.bottom-doc-list {display: none !important}

.doc-comment {display: none !important}


.fix-searchbar-wrap {display: none !important}
.reader-tools-bar-wrap {display: none !important}
.footer {display: none !important}
.mod.pay-page-mod {display: none !important}
#next_doc_box  {display: none !important}
    
}
