/* Layout
 ------------ */
* {
	/* Reset browsers default margin, padding and font sizes */
	margin: 0 !important;
	padding: 0 !important;
}
html {
	font-size: 100% !important;
}
body {
	/* Text-Sizing with ems: http://www.clagnut.com/blog/348/ */
	font-family: "Lucida Grande", Verdana, Helvetica, Arial, sans-serif !important;
	color: #a0a0a0 !important;
	/* body */
	background: #202923 url(http://s6.uploads.ru/yY4lC.gif) !important;
	font-size: 10px !important;
	margin: 0 !important;
	padding: 5px !important;
}
#body-simple {
	background: #1C271E !important;
	padding: 5px !important;
}
/* Misc stuff */
img {    
	border: none !important;
}
.gen {
	margin: 1px 1px !important;
	font-size: 1.2em !important;
}
.genmed {
	margin: 1px 1px !important;
	font-size: 1.1em !important;
}
.gensmall {
    color: #a0a0a0 !important;
	margin: 1px 1px !important;
	font-size: 1em !important;
}
.titles {
	font-family: "Lucida Grande", Helvetica, Arial, sans-serif !important;
	font-weight: bold !important;
	font-size: 1.3em !important;
	text-decoration: none !important;
}
.error, table td .error {
	color: #CAC515 !important;
}
.spacer {
	color: #557F5F !important;
	/* th */
	background: #253329 url(http://s6.uploads.ru/rQHAp.gif) top left repeat-x !important;
	border: solid 1px #353D36 !important;
	padding: 1px !important;
}
hr {
	height: 0px !important;
	border: solid #404A42 0px !important;
	border-top-width: 1px !important;
}
.pagination {
	padding: 4px !important;
	font-size: 1em !important;
	font-weight: bold !important;
}
/* Links
 ------------ */
a {
	color: #63A573 !important;
	text-decoration: none !important;
}
a:active,
a:visited {
	color: #63A573 !important;
	text-decoration: none !important;
}
a:hover {
	color: #CAC515 !important;
	text-decoration: underline !important;
}
a.forumlink {
	font-weight: bold !important;
	font-family: "Lucida Grande", Helvetica, Arial, sans-serif !important;
	font-size: 1.2em !important;
}
a.topictitle {
	margin: 1px 0px !important;
	font-family: "Lucida Grande", Helvetica, Arial, sans-serif !important;
	font-weight: bold !important;
	font-size: 1.2em !important;
}
a.topictitle, .postbody a {
	color: #63A573 !important;
}
a.topictitle:visited, .postbody a:visited {
	color: #579265 !important;
}
a.topictitle:visited:hover, .postbody a:visited:hover {
	color: #71AD80 !important;
}
a.topictitle:hover, .postbody a:hover {
	color: #CAC515 !important;
}
