    background-color: white;
    color: black
}
#toast-container > .toast-type-CommentOnAnswer {
    background-color: #2d2d2d;
}
.AnswerForm-bodyHint .ToolTip {
    opacity: 1;
    background-color: #2d2d2d;
}
.UserProfileUserList-name {
    font-family: "Bree Serif", Georgia, serif;
    color: white;
}
.UserProfileHeader-buttonsContainerMore > .UserProfileHeader-button {
    background-color: #2d2d2d;
    border-left: 1px solid #ccc;
    border-right: 1px solid #ccc;
}
.UserProfileHeader-buttonsContainerFirst > .UserProfileHeader-button {
    float: right;
    background-color: #2d2d2d;
}
.UserProfileHeader-button {
    display: block;
    height: 40px;
    padding: 6px 18px 11px;
    color: white;
    font-weight: bold;
