    cursor: pointer;
}
.UserProfileNavigation ul {
    background-color: #2d2d2d;
}
.Comment-editButtonBlock {
    margin-top: 10px;
    padding: 5px 20px 7px 20px;
    background: #2d2d2d;
    font-weight: bold;
}
.Form-input.Form-input__textangular div[ta-bind] {
    padding: 8px 12px;
    background-color: #2d2d2d;
    color: white
}
.Form-input.formatted-text.Form-input__textangular div >:first-child {
    margin-top: 0;
    background-color: #2d2d2d;
}
.CommentAddForm.ready {
    display: block;
    border-top: solid 4px #2d2d2d;
    border-bottom: solid 4px #2d2d2d;
    background-color: #2d2d2d;
}
#toast-container * {
    -moz-box-sizing: border-box;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    color: white;
}
Element {
    display: block;
    background-color: rgb(45, 45, 45);
}
#toast-container .toast-message {
    padding-right: 40px;
    padding-top: 4px;
    background-color: #2d2d2d;
}
.UserProfileBadge-box {
    box-shadow: 0 4px 9px 0 grey;
    padding: 40px 20px 30px 20px;
    position: absolute;
    background: #2d2d2d;
    z-index: 5;
    min-height: 160px;
    width: 200px;
    text-align: center;
}
#main #content .landing .box.boxThird .slot.experts a .entry .name {
    font-family: "Bree Serif", Georgia, serif;
    color: white;
    font-size: 16px;
    line-height: 20px;
}
#main #content .landing .box.boxThird .slot.experts a {
    padding: 30px;
    display: block;
    background-color: #134c77;
}
.sliderContainer input#toggleLeft[type="radio"]:checked ~ .labelLeft .sliderLeft,
.sliderContainer input#toggleRight[type="radio"]:checked ~ .labelRight .sliderRight {
    cursor: default;
    background-color: #2d2d2d;
}
.sliderContainer label.labelRight .sliderRight {
    right: 0;
    background-color: #2d2d2d;
}
.sliderContainer {
    width: 100%;
    background-color: #2d2d2d;
    overflow: hidden;
    padding: 0 0 20px 0;
    position: relative;
    max-width: 980px;
}
#main #content .landing .block.white {
    background: #2d2d2d;
}
.Tags-tag,
.Tags-moreButton {
    background: #f0ff00;
    border-radius: 1.0em;
    color: white;
    display: block;
    float: left;
    margin: 6px 5px 0 0;
    padding: 4px 15px 5px 15px;
}
.ToolTip-body {
    padding: 25px 20px;
    background-color: #2d2d2d;
}
.ToolTip.ToolTip__user {
    width: 320px;
    min-height: 200px;
    border: #2d2d2d;
    margin: 20px 0 0 0;
    background-color: #2d2d2d;
}
.Tags-tagList > li {
    display: inline-block;
    vertical-align: middle;
    background-color: #2d2d2d;
}
.UserProfileNavigation-menuItem__isActive {
    background-color: #2d2d2d;
    border-bottom: 3px solid #666;
    color: white;
    font-weight: bold;
}
.UserProfileNavigation-menuItem {
    position: relative;
    float: left;
    background-color: #2d2d2d;
    text-align: center;
    height: 100%;
    width: 100%;
    color: white;
    text-decoration: none;
}
.UserProfileNavigation-menuItemContainer__subMenu .UserProfileNavigation-menuItem {
    background-color: #2d2d2d;
}
.UserProfileOverview-fact:last-child {
    border-right: 0;
    width: 33.34%;
    background-color: #2d2d2d;
}
.UserProfileOverview-fact {
    border-left: 1px solid #2d2d2d;
    width: 33.33%;
    display: inline-block;
    background-color: #2d2d2d;
    height: 60px;
    color: white;
    padding: 10px 0 0 14px;
}
.Tags-newTagInput {
    background: #2d2d2d;
    border-radius: 15px 0 0 15px;
    border: 2px solid #009ee0;
    display: inline-block;
    font-size: 15px;
    font-family: Arial, Helvetica, "sans-serif";
    height: 30px;
    width: 150px;
    line-height: 1;
    outline: 0;
    padding: 6px 10px 5px 10px;
    vertical-align: middle;
    color: white;
}
strong {
    font-weight: bold;
    color: #0078ff;
}
.UserProfileNotifications-item {
    position: relative;
    min-height: 100px;
    border-top: 1px solid #e5e5e5;
    padding: 25px 30px 25px 60px;
    cursor: pointer;
    background-color: #2d2d2d;
    box-sizing: border-box;
}
.UserProfileNotifications-item.UserProfileNotifications-item__unread {
    background-color: #2d2d2d;
    font-weight: bold;
    border-left: 5px solid #009ee0;
}
#fUser .subHeadline {
    padding: 0 0 20px 0;
    font-size: 14px;
    font-weight: bold;
    color: #fff;
    display: block;
    background-color: #2d2d2d;
}
#fUser .basicInformation {
    padding: 24px 19px 39px 19px;
    width: 550px;
    overflow: hidden;
    background-color: #2d2d2d;
}
#fUser .furtherInfo {
    border-top: 1px solid white;
    padding: 24px 19px 39px 19px;
    background-color: #2d2d2d;
}
#fUser .furtherInfo #fUser_attribute_about_me,
#fUser .furtherInfo #fUser_attribute_hobby,
#fUser .furtherInfo #fUser_attribute_title_favorite_question,
#fUser .furtherInfo #fUser_attribute_why {
    width: 528px;
    background-color: #2d2d2d;
    color: white;
}
#fUser .submit {
    padding: 30px 19px 39px 19px;
    background-color: #2d2d2d;
}
#fUser .furtherSettings {
    border-top: 1px solid white;
    padding: 24px 19px 0 19px;
    background-color: #2d2d2d;
}
#manageUserTags .subHeadline {
    padding-bottom: 15px;
    background-color: #2d2d2d;
}
.block .subHeadline {
    color: white;
    font-size: 14px;
    font-weight: bold;
}
#manageUserTags .email {
    padding: 19px 19px 30px 24px;
    border-top: 1px solid white;
    background-color: #2d2d2d;
}
#fUser .furtherSettings strong {
    color: white;
    padding-bottom: 10px;
    display: block;
}
.custom-checkbox label {
    display: inline-block;
    zoom: 1;
    position: relative;
    cursor: pointer;
    padding: 0 0 0 23px;
    background-position: 0 -1572px;
    background-repeat: no-repeat;
    color: white;
}
#content .block-special .content {
    color: white;
    line-height: 16px;
}
label {
    font-weight: normal;
    display: block;
    font-size: 12px;
    line-height: 16px;
    color: white;
}
.custom-select-menu li.selected a {
    color: #0171b9;
    text-decoration: underline;
    background-color: #2d2d2d;
}
.custom-select-status {
    float: left;
    line-height: 20px;
    color: white;
    padding: 0 0 0 10px;
}
.FilterBar-filterLink.FilterBar-filterLink__complex {
    font-size: 12px;
    color: white;
}
.Button.Button__circle.is-disabled,
.Button.Button__circle:disabled {
    -webkit-box-shadow: inset 1px 1px 0 0 #e5e5e5, inset -1px 1px 0 0 #e5e5e5, inset 1px -1px 0 0 #e5e5e5, inset -1px -1px 0 0 #e5e5e5;
    -moz-box-shadow: inset 1px 1px 0 0 #e5e5e5, inset -1px 1px 0 0 #e5e5e5, inset 1px -1px 0 0 #e5e5e5, inset -1px -1px 0 0 #e5e5e5;
    box-shadow: inset 1px 1px 0 0 #e5e5e5, inset -1px 1px 0 0 #e5e5e5, inset 1px -1px 0 0 #e5e5e5, inset -1px -1px 0 0 #e5e5e5;
    background: #06f;
    color: #2d2d2d;
    cursor: default;
}
#mailBox .emptyMessage {
    font-style: italic;
    padding: 0 19px 20px 19px;
    background-color: #2d2d2d;
}
#mailBox .subHeadline {
    padding: 15px 0 10px 20px;
    background-color: #2d2d2d;
}
#mailBox .introText {
    padding: 0 19px 0 19px;
    background-color: #2d2d2d;
}
#userProfilePage #fMessage {
    position: relative;
    background-color: #2d2d2d;
    border: 1px solid #99d8f3;
    margin: 0 0 10px 0;
    padding: 19px 19px 29px 19px;
}
#userProfilePage #fMessage .message {
    margin: 20px 0 10px 0;
    padding: 19px 19px 29px 19px;
    color: white;
    background-color: #2d2d2d;
    border: 1px solid #99d8f3;
    font-size: 12px;
}
#userProfilePage #fMessage .subHeadline {
    color: #0696ff;
    font-size: 14px;
    font-weight: bold;
    line-height: 26px;
}
#batchActions {
    border: 1px solid #b2e2f6;
    background-color: #2d2d2d;
    padding-top: 8px;
    padding-bottom: 8px;
    padding-left: 19px;
    padding-right: 19px;
    font-size: 12px;
    font-weight: bold;
}
.Button.Button__flat {
    color: white;
    line-height: 36px;
    text-align: center;
    font-weight: bold;
    letter-spacing: -0.3px;
    background-color: #2d2d2d;
}
.landing.module .mod.full {
    width: 100%;
    background-color: #2d2d2d;
}
.landing.module .blogAutor .autorName {
    font-family: 'Bree Serif';
    font-size: 17px;
    color: white;
    position: absolute;
    top: 5px;
    left: 60px;
}
#contentCreation .content {
    position: relative;
    height: 133px !important;
    background-color: #2d2d2d !important;
}
#sfSimpleForum table#fora td.category_footer {
    background-color: #2d2d2d;
    font-size: 12px;
}
.Accordion.Accordion__indented .Accordion-navItem {
    padding-left: 30px;
    background-color: #8d8319;
}
/*Fenster abdunkeln (Kleiner Bereich)*/

#wrapper {
    margin: 0 auto;
    overflow-x: visible;
    width: 1166px;
    padding: 0 10px 0 20px;
    background-color: black;
}
/*Fenster abdunkeln (Großer Bereich)*/

html,
body {
    background: #e5e5e5;
    font-family: Arial, Helvetica, sans-serif;
    font-size: 15px;
    line-height: 19px;
    color: black;
    min-height: 100%;
    background-color: black;
}
/*Fenster abdunkeln (Kleinster Bereich)*/

#main {
    max-width: 66.4em;
    padding-left: 1.067em;
    padding-right: 1.067em;
    margin-left: auto;
    margin-right: auto;
    background-color: black;
}
/*Fenster abdunkeln (Kleinster Bereich 2)*/

#page {
    width: 67.733em;
    background: #e5e5e5;
    padding: 1.333em .667em;
    background-color: black;
}
/*Gestellte Frage abdunkeln*/

.QuestionListing {
    *zoom: 1;
    background-color: #2d2d2d;
}
/*Nach XY Themen gefilltert*/

.InterestTagsFilter {
    padding: 20px;
    border-bottom: 1px solid #e5e5e5;
    background-color: #2d2d2d;
}
/*Nur unbeantwortete Fragen anzeigen*/

.Form-item.Form-item__first {
    margin-top: 0;
    background-color: #2d2d2d;
}
/*Nur unbeantwortete Fragen anzeigen Hintergrund*/

.FilterBar {
    position: relative;
    padding: 15px 20px;
    line-height: 16px;
    font-weight: bold;
    border-bottom: 1px solid #e5e5e5;
    background-color: #2d2d2d;
}
/*Fragen für Dich*/

.Tabs.Tabs__flat .Tabs-navItem.is-active > a {
    color: white;
    background: white;
    border-right: 1px solid #e5e5e5;
    border-left: none;
    background-color: #de1515;
}
/*Alle Fragen*/

.Tabs.Tabs__flat .Tabs-navItem > a {
    font-family: Arial;
    font-weight: bold;
    color: white;
    background: #eee;
    padding: 15px 15px 15px 15px;
    text-align: center;
    background-color: #154292;
}
/*Benutzer und Punkteanzahl Hintergrund & Rahmen*/

.UserCockpit-header {
    border: #2d2d2d;
    background-color: #2d2d2d;
}
/*Mein Profil / Profil-Einstellungen Hintergrund*/

.UserCockpit-footer {
    background: #2d2d2d;
    border-top: 1px solid #e5e5e5;
    color: #e5e5e5;
    padding: 7px 16px 8px 16px;
}
/*Mitteilungen, Nachrichten - Danke für deine Antworten*/

.UserCockpit-statistics > li > .item,
.UserCockpit-leaflet > li > .item,
.UserCockpit-myPosts > li > .item,
.UserCockpit-statistics > li a,
.UserCockpit-leaflet > li a,
.UserCockpit-myPosts > li a {
    display: block;
    padding: 8px 16px 9px 16px;
    background-color: #2d2d2d;
}
/* V Meine Beiträge*/

.Accordion-navItem.Accordion-navItem__arrowLeft > a {
    padding-left: 41px;
    background-color: #2d2d2d;
}
/*Zwischenbereich von Danke für deine Antworten & Meine Beiträge*/

.UserCockpit {
    background: #2d2d2d;
}
/*Rand von Mitteilung - Merkzettel mit Ausnahme Meine Beiträge*/

.UserCockpit-statistics,
.UserCockpit-leaflet,
.UserCockpit-myPosts {
    border: 4px solid #2d2d2d;
    border-top: 0;
}
/*Rand von V Meine Beiträge*/

.UserCockpit .Accordion {
    border: 4px solid #2d2d2d;
    border-bottom: 0;
}
/*Dir fehlen noch YYY Punkte, um in die Top 5 zu kommen. Hintergrund*/

.Block-footer.Block-footer__small-sidebar {
    padding: 10px 20px 30px 20px;
    background-color: #2d2d2d;
}
/*Tophelfer des Tages*/

.Block-headline-sidebar.Block-headline_topUser {
    font-size: 18px;
    line-height: 28px;
    height: 50px;
    padding-left: 20px;
    padding-top: 11px;
    padding-bottom: 11px;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
    background-color: #2d2d2d;
}
/*Schriftliche abdunkelung von Tophelfer*/

.TopUsers .TopUsers-CenteredContent-vertically {
    display: table-cell;
    vertical-align: middle;
    background-color: #2d2d2d;
}
/*Nummer abdunkelung von Tophelfer*/

.TopUsers .TopUsers-User-rank {
    float: left;
    text-align: center;
    font-size: 15px;
    font-weight: bold;
    line-height: 80px;
    width: 50px;
    height: 80px;
    background-color: #2d2d2d;
}
/*Tophelfer des Tages abdunkelung rechte Seite*/

.TopUsers .TopUsers-User-score {
    float: right;
    max-width: 160px;
    text-align: right;
    padding-right: 20px;
    background-color: #2d2d2d;
}
/*Tophelfer des Tages abdunkelung der Trennungslinie & Zwischenbereich von Name und Punktanzahl*/

.TopUsers .TopUsers-User {
    position: relative;
    height: 80px;
    border-bottom: 1px solid #2d2d2d;
    background-color: #2d2d2d;
}
/*Abdunkelung von Themen der Woche - gutefrage.net in Zahlen*/

.Block-body.Block-body__small,
.Block-body__small.SubNav {
    padding: 24px 20px 35px 30px;
    background-color: #2d2d2d;
}
/*... mehr gute Fragen von Gute Fragen von heute*/

.Block-footer.Block-footer__small {
    border-top: 1px solid #e5e5e5;
    padding: 10px 20px 30px 20px;
    background-color: #2d2d2d;
}
/*gutefrage.net in Zahlen Hintergrund*/

.Table td {
    border-top: 2px solid #e5e5e5;
    line-height: 18px;
    font-size: 14px;
    padding: 10px 0;
    background: #2d2d2d;
    color: white;
}
/*Benachrichtigungskugel (Farbe)*/

.Badge.Badge__highlight {
    background: #f00;
}
/*Themen der Woche Punkt*/

.Paginator-pip.is-active {
    color: #00ff0c;
}
/*Bilderrahmen*/

.Block-body.Block-body__tiny,
.Block-body__tiny.SubNav {
    padding: 4px;
    background-color: #2d2d2d;
}
/*Gute Fragen von heute Daumen Hoch*/

.Block-badge.Block-badge__highlight {
    background: #00ff0c;
    color: white;
}
/*Themen der Woche Umrundungen*/

.TagList-button {
    background: #08151b;
    border-radius: 1em;
    color: #00628b;
    cursor: pointer;
    display: inline-block;
    font-family: Arial, Helvetica, sans-serif;
    font-size: 15px;
    font-weight: normal;
    padding: 6px 15px 5px 15px;
}
/*Gutefrage.net 10 Jahre Inline*/

.MainNav-level2 > ul > li > a {
    background: #2d2d2d;
    margin: 2px 0 0 2px;
    display: block;
    height: 36px;
    line-height: 36px;
    padding: 0 13px;
    text-decoration: none;
    -webkit-transition-property: color;
    -moz-transition-property: color;
    -o-transition-property: color;
    transition-property: color;
    -webkit-transition-duration: .1s;
    -moz-transition-duration: .1s;
    -o-transition-duration: .1s;
    transition-duration: .1s;
    -webkit-transition-timing-function: ease-in-out;
    -moz-transition-timing-function: ease-in-out;
    -o-transition-timing-function: ease-in-out;
    transition-timing-function: ease-in-out;
    -webkit-transition-delay: 0;
    -moz-transition-delay: 0;
    -o-transition-delay: 0;
    transition-delay: 0;
}
/*Home - Frage stellen - Fragen - Antworten - Aktionen - Themen A-Z*/

.MainNav > ul > li > a {
    background: #2d2d2d;
    border-left: 2px solid #2d2d2d;
    border-right: 2px solid #2d2d2d;
    border-top: 2px solid #2d2d2d;
    color: white;
    display: block;
    font-family: "Bree Serif", Georgia, serif;
    height: 36px;
    line-height: 32px;
    padding: 0 15px;
    position: relative;
    text-decoration: none;
    -webkit-font-smoothing: subpixel-antialiased;
}
/*Gutefrage.net 10 Jahre Tafel*/

#header {
    background: #2d2d2d;
    position: relative;
}
/*Ausloggen & Suchen*/

.GenericNav a.Button {
    background: #2d2d2d;
    border-left: 1px solid #2d2d2d;
    color: #ab2222;
}
/*Inline vervollständigung*/

.MainNav-level2 {
    background: #2d2d2d;
    border: 2px solid #942325;
    display: none;
    margin-top: -2px;
    padding-bottom: 8px;
    padding-right: 2px;
    padding-top: 6px;
}
/*Inline Aktuelle Position*/

.MainNav-level2 > ul > li.is-active > a,
.MainNav-level2 > ul > li.is-active > a:hover {
    color: #9f2121;
    background: rgb(45, 45, 45);
}
/*Fragen neuladen*/

.Button-refresh {
    font-size: 28px;
    color: #e0a600;
}
/*Titel gestellter Fragen*/

.QuestionListing-question-title {
    font-family: Arial, Helvetica, sans-serif;
    font-weight: bold;
    font-size: 18px;
    color: white;
}
/*Benutzer*/

.UserCockpit-userName {
