style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://www.moo.do/app/#") {

  html:not(.phone) .paneScroller {
    background-color: black;
    color: white;
  }

  html:not(.phone) .pComplete:not(.overviewItem)::before {
    background-color: black;
  }
  
  .collapser {
    color: #333;
  }

  html:not(.phone) .item.hovered:not(.overviewItem) .collapser {
    color: #555;
  }
  
  .item:not(.overviewItem) .collapser::before {
    background-color: black;
  }
  
  html:not(.phone) .item.hovered:not(.overviewItem) .collapser::before {
    background-color: #2b2b2d;
  }
  
  .item.pHeader:not(.overviewItem) {
    color: white;
  }

  .theme-light .pComplete * {
    color: #202020 !important;
  }

  html:not(.phone) .pane:not(.paneGmail) .pComplete.hovered {
    background: #2b2b2d !important;
  }
  
  html:not(.phone) .item.hovered:not(.overviewItem):not(.selected) {
    background-color: #2b2b2d;
  }

  .textSelected {
    background-color: #82a8cd;
  }

  .windowBlurred .textSelected {
    background-color: #a4a4a4;
  }

  html:not(.phone) .caret {
    background-color: yellowgreen;
    width: 2px;
  }
}
