style.css:
@-moz-document url-prefix("http://flightrising.com/main.php?p=ah&tab=skins") {
div[style="position: relative;z-index: 99999;overflow: auto;height: 130px;"]
{height: auto! important;}

div.cluetip-outer{
    height: auto !important;
    overflow: hidden!important;
}


div.cluetip-inner.ui-cluetip-content div div img[height="150"]{
    height: 350px !important;
    width: 350px !important;
}

div.cluetip-inner.ui-cluetip-content div div {
    width: 350px !important;
    
    background-position-x: 90px;
    background-repeat-x: no-repeat;
}

#cluetip {
    width: 370px !important;
}

div[style="position:absolute; right:6px; top:6px;"]
{right: -280px !important;}
}
