style.css:
@-moz-document domain("zhihu.com") {
.zu-main-sidebar,.zm-side-section,.zm-item-answer-author-info, .QuestionHeader,.Question-sideColumn,.CornerButtons  {
    display: none !important;
}
 
#zh-question-meta-wrap, #zh-question-detail, .zh-answers-title, .zu-edit-button, .zh-backtotop{display: none !important}

.PostIndex-recommendZone,.Tipjar,.PostIndex-contributes
{display: none !important}
.zm-item-meta.answer-actions, .zm-editable-editor-wrap, .zh-footer{display: none !important}

body {background-color: #565656 !important;}
.zu-main-content-inner, .zu-main-content {
    margin-right: 15px !important;;
    margin-left: 15px !important;;
    background-color: #FFFFFF !important;
}

body {background-color: #FFFFFF !important;}
.Question-mainColumn{
    -moz-box-shadow: 0px -10px 30px #BBB;
    -webkit-box-shadow: 0px -10px 30px #BBB;
    box-shadow: 0px -10px 30px #BBB;
    margin: auto !important; 
       padding-top:80px;
    padding-left:60px;
    padding-right:60px;
    padding-bottom:30px; 
}
.Layout-main{
    -moz-box-shadow: 0px -10px 30px #BBB;
    -webkit-box-shadow: 0px -10px 30px #BBB;
    box-shadow: 0px -10px 30px #BBB;
    margin: auto !important; 
    padding-top:80px;
    padding-left:60px;
    padding-right:60px;
    padding-bottom:30px;  
}
.PostIndex-footer,.PostComment{display:none !important}
.zm-item-vote,.zm-votebar {left:-55px !important; opacity:0;}
.zm-item-vote:hover,.zm-votebar:hover {opacity:1;}
.Question-mainColumn, .Layout-main
{
    width: 980px !important;
	
   
    background-color: #FFFFFF !important;
}

.zu-top,.Navbar {opacity:0;}
.zu-top:hover,.Navbar:hover {opacity:1;}
.zg-wrap{padding-top:0px !important;}


.AppHeader,.ContentItem-actions {opacity:0;}
.AppHeader:hover,.ContentItem-actions:hover {opacity:1;}



}
