@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("mail.google.com") {

/* List View Row Background - Hover and Checked */
.yO:hover td, .zE:hover td, .MT:hover td {background-image: -moz-linear-gradient(top, #C9DFE5, #9EAEB7 2px, #C4D9E1) !important;}
.x7 td, .x7:hover td {background-image: -moz-linear-gradient(top, #EEEFF0, #DADCE0 2px, #BFC2C7) !important;}
}
