style.css:
@-moz-document url-prefix("http://user.qzone.qq.com/313721293/infocenter") {
.bg-body {
    background: #fefefe !important
}
#leftMenu {
    border: none !important;
}
#layBackground {
    background: none;
}
span.qz_qzone_lv.qz_qzone_lv_3.qz_qzone_no_2,
a.ui-icon.icon-qzphone.qz-app-flag {
    display: none !important;
}
div {
    border-radius: 0 !important;
}
.layout-page .col-main .col-main-feed {
    padding-left: 0;
}
.qz-poster-ft .btn-post,
.qz-composer-plugin-ft .btn-post,
.qz-poster-ft .btn-dismiss-post {
    width: 22px;
}
.qz-poster .qz-inputer div.textarea,
.qz-poster .qz-inputer textarea {
    box-shadow: none;
}
.mod-comments {
    background: #fff;
    border-top: #dedede solid 1px;
    padding: 12px 10px !important;
}
.f-single.f-s-s {
    border: none;
    margin-bottom: 200px;
}
/*//////////forceHidden/////////////*/
#qz_notification,
.f-single.feed-hot,
#div_corner_ad_container,
.feed-bar-normal ~ div,
.feed-bar-normal,
li[data-feed="视频推荐"],
.feed-hot-bar + .f-wrap,
.feed-hot-bar,
#top_head_title,
.mod-commnets-poster.feedClickCmd.comment_default_inputentry,
.ui-avatar,
span.ui-mr8.state:not(:first-child),
span.state.ui-mr10,
.col-main-sidebar,
.head-detail-name,
.top-fix-bar,
.f-detail,
.f-like._likeInfo,
#like_button_pannel,
#QZ_Shop_Items_Container,
.qz-app-flag.wing-fly,
.item.bor3.bg4.item-other,
.item.bor3.bg4.item-funny,
.fn-feed-control-v2,
.qzaddons,
a.sync-qq.evt_click,
a.sync-weibo.evt_click,
#leftMenu {
    display: none !important;
}
}
