style.css:
@-moz-document domain("youtube.com") {
#alientube .preserved .vote:not(.liked) .arrow.up,
#alientube .signedout .vote:not(.liked) .arrow.up,
#alientube .preserved .vote:not(.disliked) .arrow.down,
#alientube .signedout .vote:not(.disliked) .arrow.down {
	visibility: hidden;
}

#alientube .hidden {
	display: inherit;
}

#alientube {
	overflow: visible;
}

#at_overflow ul {
	list-style: none;
	padding: 0;
	margin-top: 43px;
}
}
