style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vk.com"){
  
  /* Исправление аватарок */
  .module_body .people_cell_img, .post_img, .wall_module .copy_post_img, .wall_module .reply_img, .audio_play, .audio_page_player .audio_page_player_play .icon, .ow_ava.ow_ava_small, .group_row_photo, .group_row_img, .friends_photo_img, .right_list_img, .ow_ava.ow_ava_comm, .fans_fan_img, .nim-peer .nim-peer--photo-w, .page_list_module .cell_img, .page_list_module .thumb, .apps_feed_user_photo img, .mv_author_img, .fans_idolph_wrap, .top_notify_cont .feedback_img, .feedback_sticky_img, .feedback_img, .feedback_sticky_icon, .like_tt_image, .poll_tt_image, .search_item_img, .bp_img, .fave_link_thumb, .market_item_owner_img, .wddi_img, .like_share_ava.wdd_imgs, .bd_cell_img, .owner_photo_crop_wrap .tag_frame_inner, .owner_photo_preview50, .owner_photo_preview100, .group_u_photo_img, .group_l_photo, .group_l_photo_img, .group_al_thumb, .ui_ownblock_img, .profile_career_img, .flist_item_thumb, .group_box_image_wrap, .group_box_image_link, img.settings_bl_photo, .feedback_group_photo_img, .nim-peer .nim-peer--photo .im_grid > img, .nim-peer .nim-peer--photo > img, .nim-peer.nim-peer_small .im_grid > img, .ui_zoom_outer, .ui_zoom_inner, .mail_box_single_ava .mail_box_single_ava_img, .post_field_user_image, .submit_post_box .thumb_off, .submit_post_box .thumb_on, .group_contact_image_wrap, .mention_tt_img, .mention_tt_person_img, .friends_import_icon, .friends_import_box_icon, .friends_common_img, .olist_item_photo, .im-member-item .im-member-item--photo img, .im-member-item .im-member-item--photo, .videoplayer_end_info_author_photo, .mv_author_photo, .feedback_img::after, .friends_photo_img::after{
    border-radius: 2px !important;}
  .notifier_image, .top_profile_img, .top_profile_arrow, .chat_tab_img, a.fc_msgs_img, img.fc_contact_photo, .ts_contact_img{
    border-radius: 10% !important;}

  /* Исправление значков */
  .online.mobile::after{
    background-image: none !important;
    height: 10px !important;
    width: 6px !important;}
  .online::after, .friends_user_row .online::after{
    bottom: -3% !important;
    right: -3% !important;}
  .online::after{
    background-color: rgb(120, 170, 235) !important;}
  .friends_user_row .online.mobile::after{
    height: 12px !important;
    width: 7px !important;
   background-image: none !important;}
  .slider.slider_size_1 .slider_slide{
    height: 4px !important;}

/* Непрочитанные сообщения */
  .nim-dialog.nim-dialog_unread-out.nim-dialog_muted:not(.nim-dialog_failed) .nim-dialog--unread, .nim-dialog.nim-dialog_unread-out:not(.nim-dialog_failed) .nim-dialog--unread, .nim-dialog.nim-dialog_unread.nim-dialog_unread-out:not(.nim-dialog_failed) .nim-dialog--unread{
    background: rgba(147, 173, 200, 0.2) none repeat scroll 0 0 !important;
    border-radius: 0 !important;
    height: 30px !important;
    width: 430px !important;}
  .nim-dialog.nim-dialog_classic.nim-dialog_unread-out:not(.nim-dialog_failed) .nim-dialog--unread{
    bottom: 10px !important;}
  
/* Лойсы и комментировать */
  .post_full_like_wrap .post_like, .post_full_like_wrap .post_share {
    float: right !important;}
  .post_full_like_wrap .reply_link_wrap{
    float: left !important;}
  .wall_module .post_like_icon, .wall_module .post_share_icon {
    float: right !important;
    margin: 2px 0 1px 6px !important;}
}
