style.css:
@-moz-document regexp("http(s)://vk\\.com/(?!im(\\?|$)).*") {
/* Шапка-хуяпка */

.top_home_link .top_home_logo { background:url('data:image/png;base64,');
    width: 122px;
    height: 40px;
    margin: 0;
}

div#utils {
    position: absolute;
    height: 72px;
    overflow: hidden;
    background: #507299;
    width: 100%;
}

#utils>iframe,#utils>div>iframe {
    display: none;
}

#stl_bg {
    padding-top: 20px;
}

#page_header_cont {
    position: absolute;
}

.top_audio_player {
    max-width: 350px;
}

.ui_search.ui_search_field_empty.friends_search_cont.ui_search_custom._wrap.ui_search_fixed {
    top: 0px !important;
}

.side_bar_inner {
    width: 115px;
    padding: 60px 0 10px;
    /* position: relative !important; */
    padding-top: 60px;
    position: unset !important;
    bottom: unset !important;
    margin-top: unset !important;
    margin-left: unset !important;
}

#side_bar ol li {
    margin-left: 0px;
}

#side_bar .left_icon {
    width: 0px;
}

div#page_body {
    width: 830px !important;
}

#ads_left {
    margin-left: -30px;
}

.narrow_column {
    width: 205px;
}

.narrow_column_wrap {
    width: 205px;
}

.wide_column {
    width: 105%;
}

.post_videos .page_post_sized_thumbs a.page_post_thumb_last_row, .post_videos .page_post_sized_thumbs span.page_post_thumb_last_row {
    width: 574px !important;
    height: 320px !important;
}

.page_post_sized_thumbs.clear_fix {
    width: 576px !important;
}

.video_thumb_small .feed_video_thumb {
    width: 282px;
    height: 155px;
}

.feed_video_thumb {
    width: 574px;
    height: 320px;
}


/* Диалоги */

.body_im #side_bar {
    position: relative;
}

.im-right-menu.ui_rmenu {
    top: 42px;
}

.im-page.im-page_classic .im-page--header {
    position: relative;
    top: 0px;
}

#im--page {
    min-height: 0px !important;
}

.im-page.im-page_classic .im-page--header-chat {
    top: 15px;
}

.people_module .module_body {
    padding: 6px 0px 8px;
}

.page_action_left {
    position: relative;
    width: 145px;
}

.page_list_module .line_cell .desc_info, .page_list_module .line_cell .extra_info, .page_list_module .line_cell .info {
    width: 128px;
}

.album_module .page_album_thumb_wrap {
    width: 182px;
}

.video_module .video {
   width: 185px;
}

.left_menu_nav_wrap {
    margin-left: -30px;
    display: none;
}

#ads_left {
    margin-left: -30px;
    display: none !important;
}

.photos_period_delimiter_fixed {
    margin: 0px 0 0 -20px;
}

.audio_rows_header.fixed, .page_block .ui_search.ui_search_fixed {
    top: 0px !important;
}

.audio_friend_name, .audio_friend_name_now {
    width: 130px;
}

.fl_l.dev_left_nav_wrap {
    margin-left: -130px !important;
}

.profile_msg_split .cut_left {
    width: 146px;
}

.module_body {
    padding: 12px 10px 15px;
}

.page_photo {
    padding: 10px;
}

.module_body .people_cell {
    width: 67px;
}

.audios_module .module_body {
    padding: 6px 10px 18px;
}

.page_avatar_img {
    max-width: 185px;
    height: unset !important;
}

.profile_gifts_cont .profile_gift_img {
    width: 60px;
    height: 60px;
    margin-right: 1px;
}

.page_photos_module {
    height: 140px;
}

.page_square_photo {
    height: 138px;
    width: 138px;
}

div#narrow_column.fixed {
    position: static;
}

.page_media_link_img {
    height: 100%;
}

.quick_login input.dark {
    width: 125px;
}
/*[[kruglyashi]]*/

}

