style.css:
@-moz-document domain("youtube.com") {
/*

	This Style is created by Dave Tucker
	Version: 1.10.1
	More Styles -> https://userstyles.org/users/351088

*/

#body-container, #page {
	background-image: url("/*[[bgimg]]*/") !important;
    background-repeat: no-repeat;
    background-size: cover;
    background-attachment: fixed;
    background-position: center;
}

#masthead-search-term::-webkit-input-placeholder {
	transition: ease-in-out 0.5s;
}
#masthead-search-term:focus::-webkit-input-placeholder {
	text-indent: -100%;
    opacity: 1;
}

.ytp-red2 .ytp-swatch-background-color, .ytp-red2 .ytp-swatch-background-color-secondary, #progress, .guide-item.yt-uix-sessionlink.yt-valign.spf-link.guide-item-selected, .ytp-red2 .ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox, .ytp-red2 .ytp-settings-button.ytp-hd-quality-badge:after, .ytp-red2 .ytp-settings-button.ytp-4k-quality-badge:after, .ytp-red2 .ytp-settings-button.ytp-5k-quality-badge:after, .ytp-red2 .ytp-settings-button.ytp-8k-quality-badge:after, .ytp-red2 .ytp-settings-button.ytp-3d-badge:after, .guide-badge-live .yt-valign-container, .exp-invert-logo .resume-playback-progress-bar, .exp-invert-logo .yt-uix-button-subscribe-branded, .exp-invert-logo .yt-uix-button-subscribe-branded[disabled], .exp-invert-logo .yt-uix-button-subscribe-branded[disabled]:hover, .exp-invert-logo .yt-uix-button-subscribe-branded[disabled]:active, .exp-invert-logo .yt-uix-button-subscribe-branded[disabled]:focus, .exp-invert-logo .sb-notif-on .yt-uix-button-content, .exp-invert-logo .guide-item.guide-item-selected, .exp-invert-logo .guide-item.guide-item-selected:hover, .exp-invert-logo .guide-item.guide-item-selected .yt-deemphasized-text, .exp-invert-logo .guide-item.guide-item-selected:hover .yt-deemphasized-text, .ytp-red2 .ytp-chrome-controls .ytp-button[aria-pressed]:after {
	background-color: #9c0000;		/* darkred */
}
.ytp-red2 .ytp-swatch-color, .exp-invert-logo li.guide-section h3, .exp-invert-logo li.guide-section h3 a, .like-button-renderer-like-button:active .yt-uix-button-content, .like-button-renderer-like-button.yt-uix-button-toggled .yt-uix-button-content, #yt-masthead-multilogin-channel-switcher-item {
	color: #9c0000;
}
.channel-switcher-button.selected .highlight, .exp-invert-logo a.yt-uix-button-epic-nav-item:hover, .exp-invert-logo a.yt-uix-button-epic-nav-item.selected, .exp-invert-logo a.yt-uix-button-epic-nav-item.yt-uix-button-toggled, .exp-invert-logo a.yt-uix-button-epic-nav-item.partially-selected, .exp-invert-logo a.yt-uix-button-epic-nav-item.partially-selected:hover, .exp-invert-logo button.yt-uix-button-epic-nav-item:hover, .exp-invert-logo button.yt-uix-button-epic-nav-item.selected, .exp-invert-logo button.yt-uix-button-epic-nav-item.yt-uix-button-toggled, .exp-invert-logo .epic-nav-item:hover, .exp-invert-logo .epic-nav-item.selected, .exp-invert-logo .epic-nav-item.yt-uix-button-toggled, .exp-invert-logo .epic-nav-item-heading, .exp-invert-logo .yt-gb-shelf-item-thumbtab.yt-gb-selected-shelf-tab::before {
	border-color: #9c0000;
}

/*[[menubar]]*/

/*[[like]]*/
  
.ytp-large-play-button.ytp-button.ytp-red2:hover {
	background: url('http://7iu.de/stylish/yt/dt/large_play_button_hover_ringo-vfl7vEehF.png') center center 100% no-repeat;
}
.ytp-spinner-circle {
	border-color: #9c0000 #9c0000 transparent;
}

#theater-background {
	opacity: 0;
}

.video-extras-sparkbar-likes {
	background-color: #9c0000;
}

.logo.masthead-logo-renderer-logo.yt-sprite {
	padding-right: 5px;
}
.content-region {
	visibility: hidden;
}
.content-region:before {
	content: 'DT v1.10.0';
	visibility: visible;
}
.copyright::after {
	content: 'DT v1.10.0';
    padding: 0 0 0 20px;
}
#yt-sidebar ul::after {
    position: absolute;
    margin: 20px 0 0 5px;
	content: 'v1.10.0';
}

.logo.yt-sprite:not([usemap]), .footer-logo.yt-sprite:not([usermap]), .exp-invert-logo #header:before, .exp-invert-logo .ypc-join-family-header .logo, .exp-invert-logo #footer-logo .footer-logo-icon, .exp-invert-logo #yt-masthead #logo-container .logo, .exp-invert-logo #masthead #logo-container, .exp-invert-logo .admin-masthead-logo a, .exp-invert-logo #yt-sidebar-styleguide-logo #logo {
    background-image: url("http://7iu.de/stylish/yt/dt/logo_small_dt.png") !important;
}

@media only screen and (max-width: 498px) and (min-width: 0px), only screen and (max-width: 704px) and (min-width: 499px) {
    .exp-invert-logo.exp-responsive #yt-masthead #logo-container {
		background-image: url("http://7iu.de/stylish/yt/dt/yt_play_logo_dt.png") !important;
	}
}

/* Begin AdBlocker */
#ad_creative_1, .video-ads, #watch7-sidebar-ads, .pyv-afc-ads-container, .ytp-ad-progress-list, #video-masthead {
	display: none;
}
/* End AdBlocker */

/* About YouTube - DT */
.nav-container a[href*='about']::after, .footer-primary-info a[href*='about']::after, .yt-microsite-features h1::after, #footer-links-primary li a[href*=about]::after, #yt-sidebar a[href*='index.html']::after, .yt-nav.yt-nav-dark li a[href*=about]::after, .yt-footer__copyright:after {
	content: ' - DT';
}

}
