style.css:
@-moz-document domain(vk.com) {

/*** Global tweaks ***/
#page_header {
  width: 1100px !important;
}

#page_layout {
  width: 1100px !important;
}

#page_body {
  width: 935px !important;
}

#wide_column {
  width: 750px;
  margin-right: -50px;
}

#content {
  width: 950px;
}

.narrow_column_wrap {
  margin-right: -45px;
}

.head_nav {
  margin-right: -67.5px;
}

/*** Ads ***/
#ads_page_wrap3 {
  width: 1200px;
  margin-left: -200px;
}

/*** Developers docs ***/
#dev_page_wrap3 {
  width: 1100px;
  margin-left: -100px;
}

/*** Public ***/
/* Cover support*/
#public > div.page_block {
  width: 995px !important;
}

.page_block > .page_cover {
  width: 995px !important;
}

.page_block > .page_cover_info {
  width: 975px !important;
}


/*** Messages tweaks ***/
.im-right-menu.ui_rmenu {
  margin-left: 720px;
}

.im-page.im-page_classic {
  width: 700px;
}

/* Seach bar in classic messages*/
.im-page_classic > * > .ui_search.ui_search_field_empty._im_dialogs_search_input.im-page--dialogs-search.ui_search_custom._wrap {
  width: 699px;
}

/* Peers in classic messages */
.im-page.im-page_classic .im-page--dcontent {
  width: 700px;
}

/* Dialogs footer in classic messages */
.im-page.im-page_classic .im-page--dialogs-footer {
  width: 699px;
}

/* Headers in classic messages */
.im-page.im-page_classic .im-page--header-chat {
  width: 702px;
}

.im-page.im-page_classic .im-page--header {
  width: 699px;
}

/* Chat messages in classic messages */
.im-page.im-page_classic .im-mess-stack {
  max-width: 700px;
}

.im-page.im-page_classic .im-mess:not(.im-mess_fwd) {
  max-width: 700px;
}

/* Chat input in classic messages */
.im-page.im-page_classic .im-page--chat-input {
  width: 700px;
}

.im-chat-input.im-chat-input_classic .im-chat-input--textarea {
  width: 525px;
}

/*** Videos tweaks ***/
.videocat_row {
  width: 950px;
}

.video_item {
  width: 220px;
}

.video_item .video_item_thumb {
  width: 220px;
}

.videocat_inline_player a,
.videocat_featured_big {
  width: 655px;
}

.videocat_featured_playlists {
  width: 930px !important;
}

.videocat_featured_playlist {
  width: 455px;
}

}
