style.css:
@-moz-document domain("youtube.com") {
   
/* Barre de lecture */    /* Video progress bar */
.html5-play-progress, .ytp-play-progress {
   background: #ED0000;
   background: -moz-linear-gradient(top, #ED0000 0%, #ED0000 35%, #ffffff 65%, #ED0000 100%);
   background: -webkit-linear-gradient(top, #ED0000 0%,#ffffff 35%,#ffffff 65%,#ED0000 100%);
   background: linear-gradient(to bottom, #ED0000 0%,#ffffff 24%,#ffffff 65%,#ED0000 100%);
   box-shadow: 0px 0px 10px #ED0000, 0px 0px 10px #ED0000, 0px 0px 10px #ED0000 !important;
}
   
/* Barre de chargement */    /* Video load bar */
.html5-load-progress, .ytp-load-progress {
   background: #ED0000;
   background: -moz-linear-gradient(top, rgba(070, 120, 255, 0.3) 0%, rgba(070, 120, 255, 0.3) 45%,  rgba(255, 255, 255, 0.3) 55%, rgba(070, 120, 255, 0.3) 100%);
   background: -webkit-linear-gradient(top, rgba(070, 120, 255, 0.3) 0%, rgba(255, 255, 255, 0.3) 45%, rgba(255, 255, 255, 0.3) 55%,rgba(070, 120, 255, 0.3) 100%);
   background: linear-gradient(to bottom, rgba(070, 120, 255, 0.3) 0%, rgba(255, 255, 255, 0.3) 40%, rgba(255, 255, 255, 0.3) 55%,rgba(070, 120, 255, 0.3) 100%);
   box-shadow: 0px 0px 6px rgba(255, 70, 70, 0.3), 0px 0px 6px rgba(255, 70, 70, 0.3), 0px 0px 6px rgba(255, 70, 70, 0.3) !important;
}
   
/* Bouton de scroll */    /* Scrubber button */
/*.html5-scrubber-button, .ytp-scrubber-button {
   background: rgba(255, 255, 255, 0)  !important;
   border-color: rgba(255, 255, 255, 0)  !important;
   box-shadow: 0px 0px 0px rgba(255, 255, 255, 0), 0px 0px 0px rgba(255, 255, 255, 0), 0px 0px 0px rgba(255, 255, 255, 0) !important;
   transition: box-shadow 0.0s, background 0.0s !important;
}
/* Boutton de scroll — hover */    /* Scrubber button — hover */
.html5-scrubber-button, .ytp-scrubber-button, .html5-scrubber-button:hover, .ytp-scrubber-button:hover {
   background: #FFFFFF !important;
   background: -moz-radial-gradient(center, ellipse cover, #ffffff 0%, #ffffff 20%, #ED0000 100%) !important;
   background: -webkit-radial-gradient(center, ellipse cover, #ffffff 0%,#ffffff 20%,#ED0000 100%) !important;
   background: radial-gradient(ellipse at center, #ffffff 0%,#ffffff 20%,#ED0000 100%) !important;
   border-color: #ffffff !important;
   box-shadow: 0px 0px 10px #ED0000, 0px 0px 12px #ED0000, 0px 0px 14px #ED0000 !important;
}
   
/* Réglage volume */    /* Volume control bar */
.ytp-volume-slider-track {
   background: #ED0000 !important;
   background: -moz-linear-gradient(top, #ED0000 0%, #ED0000 35%, #ffffff 65%, #ED0000 100%) !important;
   background: -webkit-linear-gradient(top, #ED0000 0%,#ffffff 35%,#ffffff 65%,#ED0000 100%) !important;
   background: linear-gradient(to bottom, #ED0000 0%,#ffffff 24%,#ffffff 65%,#ED0000 100%) !important;
   box-shadow: 0px 0px 10px #ED0000, 0px 0px 10px #ED0000, 0px 0px 10px #ED0000 !important;
}
.ytp-volume-slider-handle {
	position:absolute;
	top:50%;
	width:3px;
	height:14px;
	margin-top:-6.5px;
   background: #ED0000 !important;
   background: -moz-linear-gradient(left, #ED0000 0%, #ED0000 35%, #ffffff 65%, #ED0000 100%) !important;
   background: -webkit-linear-gradient(left, #ED0000 0%,#ffffff 35%,#ffffff 65%,#ED0000 100%) !important;
   background: linear-gradient(to right, #ED0000 0%,#ffffff 24%,#ffffff 65%,#ED0000 100%) !important;
   box-shadow: 0px 0px 10px #ED0000, 0px 0px 10px #ED0000, 0px 0px 10px #ED0000 !important;
}
.ytp-big-mode .ytp-volume-slider-handle {
	width:5px;
	height:20px;
	margin-top:-10px
}
   
/* Badge HD-4k-5k-8k-3D */
.ytp-settings-button.ytp-hd-quality-badge::after, .ytp-settings-button.ytp-4k-quality-badge::after, .ytp-settings-button.ytp-5k-quality-badge::after, .ytp-settings-button.ytp-8k-quality-badge::after {
   width:4px;
   height:20px;
 	 top:8px;
 	 right:16px;
   background: #ED0000 !important;
   background: -moz-linear-gradient(left, #ED0000 0%, #ED0000 35%, #ffffff 65%, #ED0000 100%) !important;
   background: -webkit-linear-gradient(left, #ED0000 0%,#ffffff 35%,#ffffff 65%,#ED0000 100%) !important;
   background: linear-gradient(to right, #ED0000 0%,#ffffff 24%,#ffffff 65%,#ED0000 100%) !important;
   box-shadow: 0px 0px 10px #ED0000, 0px 0px 10px #ED0000, 0px 0px 10px #ED0000 !important;
   content:''
}
.ytp-big-mode .ytp-settings-button.ytp-hd-quality-badge:after,.ytp-big-mode .ytp-settings-button.ytp-4k-quality-badge:after,.ytp-big-mode .ytp-settings-button.ytp-5k-quality-badge:after,.ytp-big-mode .ytp-settings-button.ytp-8k-quality-badge:after,.ytp-big-mode .ytp-settings-button.ytp-3d-badge-grey:after,.ytp-big-mode .ytp-settings-button.ytp-3d-badge:after {
   width:3px;
   height:28px;
 	 top:10px;
 	 right:23px;
   content:'';
   background-image:none;

}
.ytp-settings-button.ytp-3d-badge::after {
   width:4px;
   height:20px;
 	 top:8px;
 	 right:16px;
   background: #00ED00 !important;
   background: -moz-linear-gradient(left, #00ED00 0%, #00ED00 35%, #ffffff 65%, #00ED00 100%) !important;
   background: -webkit-linear-gradient(left, #00ED00 0%,#ffffff 35%,#ffffff 65%,#00ED00 100%) !important;
   background: linear-gradient(to right, #00ED00 0%,#ffffff 24%,#ffffff 65%,#00ED00 100%) !important;
   box-shadow: 0px 0px 10px #00ED00, 0px 0px 10px #00ED00, 0px 0px 10px #00ED00 !important;
   content:''
}
.ytp-big-mode .ytp-settings-button.ytp-3d-badge::after {
   width:3px;
   height:28px;
 	 top:10px;
 	 right:23px;
   content:''
}

   
   
/* Lettres HD dans menu */    /* HD letters in menu */
.ytp-swatch-color {
   color: #ED0000 !important;
}
   
/* Item coché */    /* Checked item */
.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox {
   background: #FFFFFF !important;
   background: -moz-radial-gradient(center, ellipse cover, #ffffff 0%, #ffffff 20%, #FFF 100%) !important;
   background: -webkit-radial-gradient(center, ellipse cover, #ffffff 0%,#ffffff 20%,#FFF 100%) !important;
   background: radial-gradient(ellipse at center, #ffffff 0%,#ffffff 20%,#FFF 100%) !important;
   border-color: #ffffff !important;
   box-shadow: 0px 0px 10px #ED0000, 0px 0px 12px #ED0000, 0px 0px 14px #ED0000 !important;
   content:''
}
.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox:before {
	-moz-transform:translateX(-50px);
	-ms-transform:translateX(-50px);
	-webkit-transform:translateX(-50px);
	transform:translateX(-50px)
}
.ytp-big-mode .ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox:before {
	-moz-transform:translateX(-50px);
	-ms-transform:translateX(-50px);
	-webkit-transform:translateX(-50px);
	transform:translateX(-50px)
}

   
/* Item décoché */    /* Unchecked item */
.ytp-menuitem[aria-checked="false"] .ytp-menuitem-toggle-checkbox {
   background: #FFFFFF !important;
   background: -moz-radial-gradient(center, ellipse cover, #ffffff 0%, #ffffff 20%, #ED0000 100%) !important;
   background: -webkit-radial-gradient(center, ellipse cover, #ffffff 0%,#ffffff 20%,#ED0000 100%) !important;
   background: radial-gradient(ellipse at center, #ffffff 0%,#ffffff 20%,#ED0000 100%) !important;
   box-shadow: 0px 0px 10px #ED0000, 0px 0px 12px #ED0000, 0px 0px 14px #ED0000 !important;
   content:'';
   background-image:none;

}
   
/* Logo 'Visionner sur YouTube' pour autres sites */    /* 'Watch on YouTube' logo for other websites */
.ytp-chrome-controls .ytp-button.ytp-youtube-button:hover:not([aria-disabled="true"]):not([disabled]) .ytp-svg-fill-logo-tube-lozenge {
   fill: #ED0000 !important;
}
   
/* Roue réglages */     /* Settings gear */
.ytp-settings-button svg {
	-moz-transition:-moz-transform .3s cubic-bezier(.5,1,.4,1);
	-webkit-transition:-webkit-transform .3s cubic-bezier(.5,1,.4,1);
	-ms-transition:-ms-transform .3s cubic-bezier(0.5,1,.4,1);
   transition:transform .3s cubic-bezier(.5,1,.4,1)
}
.ytp-settings-button[aria-expanded=true] svg {
	-moz-transform:rotateY(180deg) translateY(-4px);
	-ms-transform:rotateY(180deg) translateY(-4px);
	-webkit-transform:rotateY(180deg) translateY(-4px);
	transform:rotateY(180deg) translateY(-4px);
}
.ytp-big-mode .ytp-settings-button[aria-expanded=true] svg {
	-moz-transform:rotateY(180deg) translateY(-7px);
	-ms-transform:rotateY(180deg) translateY(-7px);
	-webkit-transform:rotateY(180deg) translateY(-7px);
	transform:rotateY(180deg) translateY(-7px);
}

}
