style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("share.dmhy.org") {
  /*四日节目单*/
  .jmd {
    display: none;
  }
  /*七日节目单*/
  .jmd_base {
    display: block !important;
  }
  /*星期*/
  .jmd_base tr th {
    padding: 1px;
    width: 6%;
    background-color: #7E99BE;
    color: white;
  }

  .jmd_base tr {
    background-color: #cdf;
  }
  /*偶数行背景*/
  .jmd_base tr:nth-child(even) {
    background-color: #fff !important;
  }
  /*今日（黄色高亮）*/
  .jmd_base tr.today {
    background-color: yellow !important;
  }
  .jmd_base tr td {
    padding: 1px;
  }
  /*节目链接*/
  .jmd_base a {
    border: 1px solid #ffa500;
    padding: 1px;
    margin: 1px;
    line-height: 20px;
  }
  .jmd_base a:hover {
    color: red;
  }
  /*详情页资源介绍居左*/
  div[class="info resource-info right"] {
    float: left !important;
  }
}
