style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("vk.com"){
/*_____________ ШАПКА - HEADER_______________________*/

/*шапка сайта*/

#page_header_cont .back {
    width: 80%; /*не на весь экран*/
    left: 10%; /*сдвигаем к центру*/
    border-radius: 0px 0px 15px 15px; /*закруглить шапку*/
    background: #4c9d4f !important; border-bottom: 1px solid #37771c;
}

#page_header {
    border-radius: 0px 0px 15px 15px; /*закруглить шапку*/
    background: #4c9d4f !important; border-bottom: 1px solid #37771c;
}

#dev_top_nav_wrap {
    border-radius: 0px 0px 15px 15px; /*закруглить шапку*/
    height: 43px;
}

/*шапка сайта*/

/*лого сайта - свое лого*/
.top_home_link .top_home_logo {
    background: url(http://s019.radikal.ru/i611/1609/10/0847a2b1a6dd.png) no-repeat;
    background-position: -15px 0px;
    height: 42px;
    width: 170px;
    margin: 0;
}

.dev_home_link:before {
    background: url(http://s019.radikal.ru/i611/1609/10/0847a2b1a6dd.png) no-repeat;
    margin: 0;
    height: 42px;
    width: 170px;
}

.dev_home_link:hover, .dev_top_link:hover {
    background-color: #78ce7d;
}

/*лого сайта*/
.header_side_link, .header_side_link a {color: #ebfcec;} /*ссылки у топиков заголовков*/

/*______ПУНКТ ПОИСК_____*/
/*пункт поиск*/
input.text.ts_input {
    -webkit-filter: hue-rotate(268deg)  brightness(1.0) saturate(2.0);} /*иконка лупы и фон - пункт поиск*/
input.text.ts_input:focus {
    background-color: #bddafb; /*подбираем синий так как иконку в зеленый красим*/
    color: #3475bd;} /*фон и текст при получении focus*/
input[type=text], input[type~=text] {background: #baf2be;}
.ts_wrap .input_back_content {color: #6fc948;} /*слово Поиск салатовый*/

/*всплывающее диалоговое окно в пункте поиск*/
.ts_cont_wrap {background: #a3ffa6;box-shadow: 0 1px 4px rgba(0,0,0,.3);} /*фон списка*/
.ts_contact_name {color: #36761b;} /*цвет имени*/
.ts_contact_info {color: #939393;} /*цвет города - без изменений*/
.ts_contact_status {-webkit-filter: hue-rotate(268deg)  brightness(0.5) saturate(9.0);}
.ts_contact.active.write .ts_contact_status:hover {
    -webkit-filter: hue-rotate(268deg)  brightness(0.5) saturate(9.0);} /*подцветка первая часть тега обязательна тут*/
.ts_contact_photo.online:after {border: 2px solid #fff;} /*кружок онлайна бордер без изменений*/
.online:after {background-color: #8ac176;} /*фон кружка онлайна без изменений*/

.clear_fix.write:hover {background: #6cd77a; border: solid 1px #fff;} /*при наведении фон и бордер пункта*/
/*______ПУНКТ ПОИСК_____*/

/*______ПУНКТ УВЕДОМЛЕНИЕ - КОЛОКОЛЬЧИК И ПУНКТ АУДИО______*/
/*колокольчик и значок аудио - смена цвета*/
.top_nav_btn .top_nav_btn_icon {-webkit-filter: hue-rotate(268deg)  brightness(0.7) saturate(5.0);}

/*колокольчик активный*/
.top_nav_btn#top_notify_btn.active .top_nav_btn_icon, 
.top_nav_btn#top_notify_btn.has_notify .top_nav_btn_icon {
    background-position: -5.0px -23px; /*позиционируем так как белый стал серым*/
    -webkit-filter: hue-rotate(28deg)  brightness(5.0);
}
/*значок аудио активный*/
.top_nav_btn#top_audio.active .top_nav_btn_icon {
    background-position: -5.0px -52px;/*позиционируем*/
    -webkit-filter: hue-rotate(28deg)  brightness(5.0);
}

/*кнопки в шапке*/
.top_nav_btn.active, .top_nav_link.active {background-color: #3f9044;} /*активная кнопка*/
.top_nav_btn:hover, .top_nav_link:hover {background-color: #449549;} /*при наведении*/
#top_audio_player:hover { background-color: #449549;} /*подсветка когда аудио играет было */
/*кнопки в шапке*/

/*кружок оповещения на колокольчике*/
#top_notify_btn.has_notify .top_notify_count /*без наведения*/
{border: 2px solid #62cf63;background-color: #006000;} 
#top_notify_btn.has_notify:hover .top_notify_count /*при наведении*/
{border: 2px solid #62cf63;background-color: #4c9d4f;} 

/*топик диалога - фон, бордер, цвет*/
.top_notify_header {background-color: #baf2be; color: #2d7e30; border-bottom: 1px solid #78ce7d;} 

/*псевдоэлемент - треугольник*/
#top_notify_wrap:before {border-bottom-color: #78ce7d;} /*бордер*/
#top_notify_wrap:after {border-bottom-color: #baf2be;} /*фон*/
/*псевдоэлемент - треугольник*/

/*середина -диалога*/
#top_notify_wrap {background: #baf2be;border: 1px solid #78ce7d;box-shadow: 0 1px 3px #6cd77a;} /*общий фон и тень*/

.top_notify_cont .feedback_row:not(.dld).hover,
.top_notify_cont .feedback_row:not(.dld):hover {background-color: #a3ffa6;} /*при наведении*/
.top_notify_cont .feedback_sticky_rows {border-left: 2px solid #78ce7d;} /*бордер у уведомления*/

.feedback_sticky_row.unread {background-color: #baf2be;} /*получено уведомление в колокольчике фон*/
.feedback_sticky_row.hover, .feedback_sticky_row:hover {background-color: #a3ffa6;} /*при наведении*/

.feed_row+.feed_row_fb_hidden .feedback_row:first-child, 
.feed_row~.feed_row .feedback_row, 
.feed_row~.feed_row .feedback_sticky_row, 
.feedback_sticky_rows:not(:empty)+.feed_row .feedback_row {border-top: 1px solid #78ce7d;} /*разделитель*/

.feedback_row_menu_icon {-webkit-filter: hue-rotate(268deg)  brightness(0.7) saturate(5.0)!important;} /*иконка вниз*/
.feedback_sticky_gift_btn {-webkit-filter: hue-rotate(268deg)  brightness(0.7) saturate(5.0)!important;} /*иконка подарка*/
.feedback_photo_icon, .feedback_row_wrap.unread .feedback_photo_icon {background-color: transparent;border: transparent;} /*Убирает фон у иконок на аватарах в уведомлениях*/

/*_____все уведомления_____*/

.feedback_row_clickable:not(.feedback_row_touched):hover {
    background-color: #84d386 !important;
    border-bottom: 1px solid #e7e8ec;
}

.feedback_row_wrap.reply_box_open {
    background-color: rgb(186, 242, 190);
    border-bottom: 1px solid rgb(120, 206, 125);
}
.feedback_row_wrap.unread:not(.feedback_row_touched) {background-color: #a3ffa6;} /*заявка принята*/

/*комментарий к уведомлению*/
/*псевдоэлемент - треугольник*/
.feedback_row_answer::before {border-bottom-color: #78ce7d;}
.feedback_row_answer::after {border-bottom-color: #a3ffa6;}
.feedback_row_wrap.reply_box_open .reply_box::after {border-bottom-color: #a3ffa6;}
.feedback_row_wrap.reply_box_open .reply_box::before {border-bottom-color: #78ce7d;}
/*псевдоэлемент - треугольник*/
.feedback_row_wrap.reply_box_open .submit_post, .feedback_row_wrap.reply_box_open:hover {background-color: #a3ffa6;} 
.feedback_row_answer {background-color: #a3ffa6;border-color:  #78ce7d;} /*фон*/
/*комментарий к уведомлению*/
.feedback_date .post_link {color: #37761c;} /*дата текущего поста*/
.feedback_date {color: #54ab57;}  /*дата постов*/
.page_block #all_shown {border-top: 1px solid #78ce7d;} /*бордер низ*/

.sort_not_rev_icon, .sort_rev_icon {
    -webkit-filter: hue-rotate(268deg)  brightness(0.9) saturate(3.0)!important;} /*иконка стрелка вверх*/

/*Уведомление с фото при нажатии на время*/
.wk_subscribe_icon {-webkit-filter: hue-rotate(268deg)  brightness(0.9) saturate(3.0)!important;} /*галочка*/
.wl_head_wrap {background: #baf2be;}
.wl_post_body_wrap {background: #baf2be;}
.wl_post_actions_wrap {background-color: #baf2be;}
.wl_replies_header {background: #baf2be; color: #2d7e30; border: 1px solid #54ab57;}
.wl_replies_wrap .wall_module {background-color: #a3ffa6;}
/*Уведомление с фото при нажатии на время*/

/*_____все уведомления_____*/

.wall_module .my_like .like_icon, .wall_module .my_share .share_icon {
    -webkit-filter: hue-rotate(268deg) brightness(0.7) saturate(9.0)!important;
}

/*низ -диалога*/
.top_notify_show_all {background-color: #baf2be;border-top: 1px solid #78ce7d;}
.top_notify_show_all:hover {background-color: #84d386; color: #2d7e30;} /*подсветка*/

/***диалог при наведении на иконку вниз***/
/*псевдоэлемент - треугольник*/
.top_notify_cont .feedback_row_menu .ui_actions_menu:before, 
.ui_actions_menu_dummy_wrap.feedback_row_menu .ui_actions_menu:before {border-bottom-color: #78ce7d;} /*бордер*/
.top_notify_cont .feedback_row_menu .ui_actions_menu:after, 
.ui_actions_menu_dummy_wrap.feedback_row_menu .ui_actions_menu:after {border-bottom-color: #a3ffa6;} /*фон*/

.ui_actions_menu {background: #a3ffa6;border: 1px solid #78ce7d;} /*заливка всего диалога*/    
.ui_actions_menu_item:hover {background: #6cd77a; color: #2d7e30;} /*подсветка*/
/***диалог при наведении на иконку вниз***/

/*___ТОПИК С ПЛЕЕРОМ___*/
.audio_page_player {background-color: #a3ffa6; border-bottom: 1px solid #78ce7d;} /*фон плеера*/
.audio_page_player .audio_page_player_play .icon {background-color: #64ad65;} /*иконка плей большая*/
.audio_page_player .audio_page_player_btns .icon {
    -webkit-filter: hue-rotate(268deg)  brightness(0.5) saturate(9.0);} /*иконки вверху справа в аудио*/
.audio_page_player .audio_page_player_next .icon, .audio_page_player .audio_page_player_prev .icon {
    -webkit-filter: hue-rotate(268deg)  brightness(0.5) saturate(9.0);} /*иконки перемотки*/
.audio_page_player .slider .slider_slide {background-color: #75e078;} /*полоска уровня громкости и загрузки*/
/*___ТОПИК С ПЛЕЕРОМ___*/

/*____ПОИСК ПО АУДИОЗАПИСЯМ____*/

.ui_search {border-bottom: 1px solid #78ce7d;background-color: #54ab57;} /*фон поиска по аудиозаписям и нижний бордер*/
.top_audio_layer .audio_layer_rows_cont {border-right: 1px solid #78ce7d;} /*боковой разделитель*/

input.ui_search_field, input.ui_search_field { 
    background: url(http://s017.radikal.ru/i402/1609/b7/d76f855789c5.png) no-repeat; /*иконка лупы*/
    background-position: 0; /*позиционируем иконку*/
    color: #fff;} /*вводимый текст белый*/
input.ui_search_field._field::-webkit-input-placeholder{ color: #8ffa92;} /*поиск по аудио записям - текст*/
input.ui_search_field:focus::-webkit-input-placeholder {color: #71d775;} /*поиск - само сообщение при нажатии, получен focus*/

.ui_suggester_results_list {background-color: #a3ffa6;border: 1px solid #78ce7d;} /*при вводе текста выпадающий список*/
.ui_suggester_results_list ul li.active, .ui_suggester_results_list ul li:hover { /*при наведении и активный список*/
    background: #84d386;color: #37761c;}

/*____диалоговое окно по нажатию на параметры____*/
a.ui_search_fltr_control {color: #baf2be;} /*текст параметры*/
.ui_search_fltr_control { padding: 14px 15px 13px; 
    background: url(http://s020.radikal.ru/i711/1609/05/7aaea994abc5.png) no-repeat;
    background-position: right 21px;} /*arrow - галочка вниз*/
/*псевдоэлемент - треугольник*/
.ui_search_fltr:before {border-bottom-color: #78ce7d;}
.ui_search_fltr:after {border-bottom-color: #baf2be;}
/*псевдоэлемент - треугольник*/
.ui_search_fltr {background: #baf2be; border: 1px solid #78ce7d; color: #2d7e30;} /*фон окошка*/

.ui_search_filters_reset {-webkit-filter: hue-rotate(268deg)  brightness(0.5) saturate(3.0);} /*иконка крестик справа*/
.ui_search_filters_reset:hover {
    -webkit-filter: hue-rotate(268deg)  brightness(0.7) saturate(3.0);} /*иконка крестик при наведении*/
.ui_search_filters_pane {border-top: 1px solid #37761c;} /*разделитель*/

/*элемент всплывающая кнопка - по длительности, по популярности*/
.ui_multiselect_cnt .token, .ui_search_filters_pane .token {background-color: #baf2be;}
.ui_search_filters_pane .token .token_title {color: #2d7e30;}
.ui_multiselect_cnt .token .token_del, .ui_search_filters_pane .token .token_del {
    -webkit-filter: hue-rotate(268deg)  brightness(0.5) saturate(3.0);} /*иконка крестик*/
.ui_multiselect_cnt .token:hover .token_del, .ui_search_filters_pane .token:hover .token_del{
    -webkit-filter: hue-rotate(268deg)  brightness(0.7) saturate(3.0);} /*иконка крестик при наведении*/
/*элемент всплывающая кнопка - по длительности, по популярности*/

.selector_container {background: #a3ffa6;}
.selector_container table.selector_table {border: 1px solid #78ce7d;}
.selector_container td.selector_dropdown {-webkit-filter: hue-rotate(268deg) brightness(0.5) saturate(9.0);} /*arrow*/

.result_list ul {background: #baf2be;}
.result_list {border: 1px solid #78ce7d;}
.result_list ul li.active {background: #86e487;} /*подсветка активного пункта меню*/
/*____диалоговое окно по нажатию на параметры____*/

.audio_hq_label_show .audio_row.hq .audio_hq_label, .audio_search_rows .audio_row.hq .audio_hq_label {
     -webkit-filter: hue-rotate(268deg)  brightness(0.3) saturate(7.0);}
/*____ПОИСК ПО АУДИОЗАПИСЯМ____*/

/*псевдоэлемент - треугольник*/
.eltt.eltt_bottom:after {border-bottom-color: #a3ffa6;}
.eltt.eltt_bottom:before {border-bottom-color: #78ce7d;}
/*псевдоэлемент - треугольник*/
.eltt {background-color: #a3ffa6;border: 1px solid #78ce7d;} /*фон серединки и бордер вокруг*/

/*___СЕРЕДИНКА АУДИО___*/
.audio_play {background: #53a554 url(http://vk.com/images/icons/audio_playpause.png) no-repeat 8px 7px;} /*иконка плей*/
.audio_rows._audio_rows {box-shadow: 0 0 0 1px #78ce7d;} /*боковой разделитель*/

.audio_row.audio_row_current, .audio_row.audio_row_playing, .audio_row.audio_row_current:hover, .audio_row.audio_row_playing:hover {background-color: #baf2be;} /*фон текущей композиции*/
.audio_row._audio_row:hover {background-color: #86e487;} /*фон подсветка записи на которой курсор*/
.audio_row .audio_duration_wrap {color: #61c664;} /*текст даты*/
.audio_row .audio_acts .audio_act>div {
    -webkit-filter: hue-rotate(268deg)  brightness(0.5) saturate(9.0);} /*иконки наушников при наведении на время*/
.audio_row.audio_row_current .audio_duration {color: #478448;} /*текст сколько времени прошло при звучании музыки*/
/*иконка крестик*/
.audio_delete_current_btn, .audio_delete_current_btn:hover {
    -webkit-filter: hue-rotate(268deg)  brightness(0.5) saturate(9.0);}
.audio_menu_btn.audio_upload_btn, .audio_menu_btn.audio_edit_feed_btn {
    -webkit-filter: hue-rotate(268deg)  brightness(0.5) saturate(9.0);}
.audio_album_add_btn .audio_album_btn_icon, .audio_album_add_btn_icon {
    -webkit-filter: hue-rotate(268deg)  brightness(0.5) saturate(9.0);}

.audio_layout {color: #364537;} /*текст с текущей музыкой*/
.olist_item_wrap {border-bottom: 1px solid #78ce7d;} /*бордер в списке друзей*/
/*___СЕРЕДИНКА АУДИО___*/

.audio_layer_bottom {background-color: #a3ffa6;border: 1px solid #78ce7d;} /*фон аудио низ*/

/*менюшка справа*/
.ui_rmenu_item_sel, .ui_rmenu_item_sel:hover {background-color: #81f085; border-left: #54ab57;} /*активный пункт*/
.ui_rmenu_item:hover, .ui_rmenu_subitem:hover {background-color: #86e487;} /*подсветка*/
.ui_rmenu_item_arrow {-webkit-filter: hue-rotate(268deg)  brightness(0.5) saturate(9.0);} /*arrow*/
/*менюшка справа*/

/*__слайдер__*/
.audio_inline_player .slider .slider_amount, .audio_page_player .slider .slider_amount, 
.audio_inline_player .slider .slider_handler, .audio_page_player .slider .slider_handler {
    -webkit-filter: hue-rotate(268deg)  brightness(0.5) saturate(9.0);} /*слайдер цвет*/

.audio_inline_player .slider .slider_back, .audio_page_player .slider .slider_back {
    -webkit-filter: hue-rotate(268deg)  brightness(0.9) saturate(3.0);} /*полоска загрузки*/
.audio_inline_player .slider .slider_slide {background-color: rgb(149, 219, 153);} /*полоска до загрузки слайдера*/

.slider .slider_loading_bar {background-image: url(http://s019.radikal.ru/i635/1609/66/271c5e7c8c43.png);} /*прогресс бар*/

/*всплывающее инфо со временем при наведении на полоску плеера*/
.slider_hint {background: #6cd77a;} /*фон*/
.slider_hint.audio_player_hint {color: #2d7e30;} /*цвет времени*/
.slider_hint:after {border-top-color: #6cd77a;} /*треугольник вниз*/
/*всплывающее инфо со временем при наведении на полоску плеера*/
/*__слайдер__*/

/*список друзей - меню справа*/
.friends_header_load_more a:after {-webkit-filter: hue-rotate(268deg)  brightness(0.9) saturate(7.0);} /*arrow*/
a.audio_friend_name, .audio_friend_name_now {color: #42648b;} /*цвет имени*/
.audio_friends_list {background-color: #a3ffa6;}
.audio_friend:hover {background-color: #86e487;} /*подсветка светлая*/
.audio_friend {font-size: 12px;}
.audio_friend_audios_count, .audio_friend_now {color: #465d06;}
#audio_status_tt .audio_share_cont {background-color: #78ce7d;
    border-top: 1px solid #78ce7d;border-radius: 0px;} /*фон по трансляции аудио иконке*/
.search_filters_minimized_text {
    background: url("http://s013.radikal.ru/i324/1609/92/b28d90398797.png") no-repeat #81f085;} /*иконка поиска*/
div.search_filters_minimized_text {color: #42648b;}
.search_filter_open .arrow, .search_filter_shut .arrow {-webkit-filter: hue-rotate(268deg)  brightness(0.5) saturate(9.0);}
.search_sep {border-top: 1px solid #78ce7d;} /*сепаратор*/

/*выбранный друг*/
.audio_friend.audio_friend_selected {background-color: #6cd77a; border-left: 2px solid #2d7e30;} /*потемнее подсветка*/
/*список друзей - меню справа*/

/*скроллбар*/
.scrollbar_inner {-webkit-filter: hue-rotate(268deg)  brightness(0.8) saturate(10.0)!important; width: 5px;}/*бегунок*/
.scrollbar_inner:hover {-webkit-filter: hue-rotate(268deg)  brightness(0.8) saturate(10.0)!important;}
.scrollbar_cont {width: 7px;background-color: hsl(128, 82%, 82%);} /*фон*/
/*скроллбар*/

.radiobtn:before {background: url(http://s018.radikal.ru/i521/1608/a2/ab35d008e657.png) 0 0 no-repeat;} /*кнопка выбора*/
.checkbox:before {-webkit-filter: hue-rotate(268deg) brightness(0.9) saturate(3.0);} /*иконка checkbox*/

/*______ДИАЛОГОВОЕ ОКНО В АУДИО_____*/


/***____имя в шапке с вложенным меню***/

.top_profile_arrow {background: url(http://s017.radikal.ru/i442/1609/97/ed877eaba054.png) no-repeat;} /*arrow*/
.ui_search_custom_link.idd_wrap .idd_arrow {background: url(http://s017.radikal.ru/i442/1609/97/ed877eaba054.png) no-repeat;
background-position: right 5px;}
/*треугольник - псевдоэлемент*/
#top_profile_menu:before {border-bottom-color: #78ce7d;} /*бордер*/
#top_profile_menu:after {border-bottom-color: #baf2be;} /*фон*/

/*всплывающий диалог шапка*/
#top_profile_menu {background: #a3ffa6;border: 1px solid #78ce7d;}
.top_profile_mrow {color: #2d7e30;}
.top_profile_mrow:hover {background: #6cd77a;}
.top_profile_sep {border-top: 1px solid #75e078;} /*разделитель*/

/***_____имя в шапке с вложенным меню***/

/*_________БЛОК НАСТРОЙКИ - Колокольчик_________*/
.settings_line {border-bottom: 1px solid rgb(120, 206, 125);} /*бордер*/

.settings_block_footer {
    border-top: 1px solid rgb(120, 206, 125);
    background: rgb(132, 211, 134);
}

/*общее*/
/*настройка пунктов меню*/
/*вверх*/
.tabbed_box .summary_tab_sel .summary_tab2 {background-color: #78ce7d;}
.tabbed_box .summary_tab_sel {border-bottom: 2px solid #449549;}
