style.css:
@-moz-document domain('forums.equestriadaily.com'), url-prefix('https://forums.equestriadaily.com') {
	#elUserNav {
    	padding: 4px 6px;
    	background: rgba(0, 0, 0, 0.5);
    	border-radius: 0 0 2px 2px;
    	position: absolute;
    	float: right;
    	margin-top: -10px;
    	margin-right: -27px;
	}

	#ipsLayout_header header {
    	padding: 0 0 124px 0;
    	margin-bottom: -165px;
    	background: url("https://s32.postimg.org/61kzl6kmr/Cloudy.png");
    	background-size: cover;
    	background-repeat: no-repeat;
    	background-position: center 0px;
	}

	#ipsLayout_header .ipsLayout_container img {
    	display: block;
    	margin: 0 auto;
	}

	.ipsNavBar_primary {
    	background: rgba(28, 34, 43, 0.75);
	}

	.ipsUserPhoto img {
    	border-radius: 2px;
	}
}
