[dir] body.new_header_design .TopSearch__input{background-color:var(--color3)!important;color:#fff!important;filter:var(--filt2)saturate(.7)brightness(.7);background-image:url(https://vk.com/images/svg_icons/ic_head_loupe.svg)!important}
[dir] .TopSearch__input::selection{background:#4e5e71!important}
#ts_wrap .input_back .input_back_content{color:#999!important}
.top_nav_btn .top_nav_btn_icon,[dir] body.new_header_design .top_nav_btn#top_audio:not(.active) .top_nav_btn_icon{filter:var(--filt2)saturate(.7)brightness(1.75)!important}
.top_nav_btn:hover .top_nav_btn_icon{filter:var(--filt2)saturate(.9)brightness(2)!important}
[dir] .top_audio_player:hover,[dir] .head_nav_item.fl_r:hover{background-color:rgba(0,0,0,0.5)!important;border-bottom:none!important}

/* плеер в шапке фикс */
.top_audio_player .top_audio_player_title{color:#909090!important}
[dir] body.new_header_design .top_audio_player_btn_icon{filter:var(--filt2)saturate(.9)brightness(1.2)!important}
[dir="ltr"] body.new_header_design .top_audio_player{min-width:90%}

/* обоина */
body:before{content:"";position:fixed;left:0;right:0;top:0;bottom:0;z-index:-1;display:block;
/* ссылка на обои */
  background: #111 url(/*[[wallsetup]]*/) center fixed;background-size:cover;background-repeat:no-repeat;
  width:20000px;height:20000px;
/* фильтры обоев (яркость)*/
  filter:brightness(/*[[wallbrightness]]*/);
}
body[onresize]{background:#111!important}

/* чистим шлак: нафиг ссылки слева в меню, нафиг сердце на фотках, нафиг вскукареки о заполнении инфы, нафиг рекламу рекомендаций из ленты */
.left_menu_nav_wrap,.pv_cont .pv_hh_like,.profile_rate_warning,.mv_chat_reply_author_photo{display:none}
.mv_chat_reply_input_wrap{margin-left:10px}
.mv_chat_reply_submit_btn{padding-right:10px}
.pv_cont .pv_reply_form_wrap .reply_field_wrap{margin:0 4px 0 4px}
.pv_cont .wall_module .reply .dld{padding-left:0px}

/* возможные друзья в ленте новостей */
#feed_filters .friends_possible_block,.feed_friends_recomm{display:none!important}

/* реклама */
._ads_promoted_post_data_w,[class*="_ads_block"]{display:none!important}

/* fuck мылору */
[aria-label="Актуальные новости"]{display:none}

/* лента рекомендаций */
.page_block.feed_to_recomm,.apps_feedRightAppsBlock_collaborative_recommend_apps{display:none!important}

/* нах рекомендации паблосов из ленты */ 
.page_block.feed_groups_recomm{display:none}

/* сплющиваем шапку */
[dir] body.new_header_design #page_header_cont{height:34px}
[dir] body.new_header_design .ts_cont_wrap,#top_notify_btn.active #top_notify_wrap,[dir] #top_audio_layer_place > #audio_layer_tt,[dir] .narrow_column.fixed[style*="top: 48px"]{top:34px!important}
[dir] body.new_header_design{--header-height:33px}
[dir] body.new_header_design .side_bar_inner{margin-top:32px!important}
[dir] body.new_header_design #side_bar.side_bar{top:1px!important}
body.new_header_design .top_nav_btn .top_notify_count{top:2px;left:20px}
[dir] .audio_page_player2.audio_page_player_fixed_shown{transform:translate(0,27px)}

/* убираем имя и авку из шапки */
.top_profile_name,.top_profile_img{display:none}
#top_nav .top_nav_link{padding:0!important;width:28px;height:42px;display:block;content:' ';background:8px 18px/40% url(https://vk.com/images/svg_icons/contour_arrow.svg) no-repeat !important;filter:var(--filt2)saturate(.0)brightness(.7)!important}
[dir] .top_nav_link.active,[dir] .top_nav_link:hover{filter:var(--filt2)brightness(1)saturate(.2)!important;background-color:transparent!important}
#top_nav a#top_switch_lang.top_nav_link{width:auto!important;background:none!important}

/* сворачивать счётчики */
.counts_module {height:0px!important;transition:height 0.7s;border: none;box-shadow:inset 0px 1px 0px 0px #111;background-color:rgba(0,0,0,0.4)}
.profile_more_info_link:not(.info_shown){height:0px!important;opacity:0;transition:height .7s, opacity .7s;pointer-events:none}
#wide_column div:nth-child(1):hover .counts_module{height:70px!important;transition:height 0.7s}
#wide_column div:nth-child(1):hover .profile_more_info_link{height:34px!important;opacity:1;transition:height 0.7s, opacity .7s;pointer-events:all}
