style.css:
@-moz-document url-prefix("https://observatory.rocketmod.net/"), url-prefix("http://observatory.rocketmod.net/") {
.sidebar-panel, .card-header:first-child {
    background-color: #222;
    color: #fff;
}
.sidebar-panel > nav > ul > li > a:hover, .sidebar-panel > nav > ul > li.open > a:hover {
    background-color: #363636;
}
.sidebar-panel > nav ul.nav > li a > *, .sidebar-panel .nav-profile a.dropdown-toggle .user-info, .sidebar-panel .nav-profile > .dropdown-toggle, .sidebar-panel .nav-title {
    color: white;
}
.card, .profile-bio .background {
    background-color: #444;
}
.sidebar-panel .brand {
    background-color: #777;
}
.table-bordered thead th, .table-bordered thead td { 
    background-color: #222;
}
.main-panel > .main-content > .content-view, .table td, .card-block, .content-footer {
    background-color: #333;
    color: white;
}
.tab-content, .nav-tabs .nav-item .nav-link.active {
    background-color: #555;
    color: white;
}
.table-bordered, .card-block, .card, .profile-bio .background {
    border-color: #555;
}
body {
    background-color: #444
}
form, #legal a {
    color: white;
}

*::-webkit-scrollbar {
    width: 10px ! important;
    height: 10px ! important;
    background: #1e1e1e ! important;
    border: 1px solid #252525 ! important;
}

*::-webkit-scrollbar-button {
display: none ! important;
}

*::-webkit-scrollbar-thumb,
*::-webkit-scrollbar-track {
border: 0 ! important;
box-shadow: none ! important;
}

*::-webkit-scrollbar-thumb {
    min-height: 28px ! important;
    background: #444 ! important;
}

*::-webkit-scrollbar-corner,
*::-webkit-scrollbar-track {
background: #1e1e1e ! important;
}
}
