.group_u_photo_img,
.group_l_photo,
.group_l_photo_img,
.group_al_thumb,
.ui_ownblock_img,
.profile_career_img,
.flist_item_thumb,
.group_box_image_wrap,
.group_box_image_link,
img.settings_bl_photo,
.feedback_group_photo_img,
.nim-peer .nim-peer--photo .im_grid > img,
.nim-peer .nim-peer--photo > img,
.nim-peer.nim-peer_small .im_grid > img,
.ui_zoom_outer,
.ui_zoom_inner,
.mail_box_single_ava .mail_box_single_ava_img,
.post_field_user_image,
.submit_post_box .thumb_off,
.submit_post_box .thumb_on,
.group_contact_image_wrap,
.mention_tt_img,
.mention_tt_person_img,
.friends_import_icon,
.friends_import_box_icon,
.friends_common_img,
.olist_item_photo,
.im-member-item .im-member-item--photo img,
.im-member-item .im-member-item--photo,
.videoplayer_end_info_author_photo,
.mv_author_photo,
.feedback_img::after,
.friends_photo_img::after {
    border-radius: 2px !important;
}
.notifier_image,
.top_profile_img,
.top_profile_arrow,
.chat_tab_img,
a.fc_msgs_img,
img.fc_contact_photo,
.ts_contact_img {
    border-radius: 10% !important;
}
/*перенесение коментов к фоткам вниз*/

.pv_cont {
    max-width: 90%; /*max-width: 977px;*/
}
.pv_narrow_column_wrap {
    display: block;
    float: left;
}
.pv_cont .pv_left_wrap {
    float: left;
}
.pv_cont .pv_narrow_column_wrap {
    float: left;
}
#pv_narrow {
    width: 100% !important;
    height: auto !important;
}
#layer,
.pv_light #layer {
    margin-top: 30px !important;
    margin-bottom: 30px !important;
}
#pv_photo {
    margin: auto !important;
    width: 100% !important;
}
.pv_no_commments_placeholder_wrap {
    margin-top: 10px !important;
    margin-bottom: 10px !important;
}
.pv_cont .pv_closed_commments_placeholder,
.pv_cont .pv_no_commments_placeholder {
    height: 120px;
}
.pv_cont .pv_close_btn {
    right: 45px;
}
[dir=ltr] .pv_cont .pv_close_btn {
    right: 22px; /*предыдущий элемент не работает, добавил этот*/
}
.pv_cont .pv_reply_form_wrap {
    width: 100%;
}
.pv_cont .pv_narrow_column_wrap {
    width: 100%;
}
.pv_cont .pv_left_wrap {
    top: auto;
    float: left;
    width: 100%;
}
.pv_author_block {
    border-bottom: 0;
    padding-bottom: 10px;
}
#pv_comments {
    clear: both;
}
.pv_like {
    float: right;
}
.pv_author_block {
    float: left;
}
#pv_desc,
#pv_place,
#pv_tags {
    clear: both;
}
.pv_cont .narrow_column {
    pointer-events: none !important;
}
.pv_cont .narrow_column .pv_author_img,
.pv_cont .narrow_column a,
.pv_cont .narrow_column button,
.pv_cont .narrow_column .pv_like,
.pv_cont .narrow_column .pv_can_edit,
.pv_cont .narrow_column .pv_comments_header,
.pv_cont .narrow_column .reply_image,
.pv_cont .narrow_column .post_actions_wrap,
.pv_cont .narrow_column .reply_box,
.pv_cont .narrow_column .slider,
.pv_cont .narrow_column .audio_info {
    pointer-events: auto !important;
}
.pv_cont .narrow_column .ui_scroll_blocker {
    padding-right: 0px !important;
    overflow: initial !important;
}
.pv_cont .narrow_column .ui_scroll_outer {
    padding-right: 0px !important;
    margin: 0;
}
}
