style.css:
@-moz-document regexp("https?://(www|encrypted).google.(com|([a-z]{2}))(.[a-z]{2})?.*") {
    body, body #appbar {
        background-color: #2A2D2D !important;
        color: #FFFFFF;
    }
    
    #wflyr {
        visibility: hidden;
        background-color: #101010;
    }
    
    /*invisible divs*/
    #rhs .rhsvw, #cnt, .gb_Z:hover .gb_4, .g-blk, .tpo, .mnr-c > .card-section, ._rk, .cwtlwm, .mod .g, ._Z7 .g:last-child, #abar_button_ss, .kp-blk .g:not(.g-blk) {
        background-color: transparent !important;
        box-shadow: none !important;
    }
    
    /*moz default appearance*/
    .ccw_data {
       -moz-appearance: textfield;
    }
    
    .kno-mrg ._iH a.fl {
        text-shadow: none;
    }
    
    ._iH {
        background: rgba(19,19,19,0.75);
    }
   
    /*invert image colors*/
    .ab_icon, .gb_Rb, .lr_dct_spkr, .gb_Vb, ._jug, ._R6g._aeh ._e7g, ._S6g._aeh ._f7g, ._Zgf, .gb_Wb, .tw-menu-btn-image, .gb_ea .gb_b, ._Fnc, #qbi, .act-icon-dark-gray, .tw-menu-btn, .gb_Zb, .gb_b {
       -webkit-filter: invert(1)grayscale(1)!important;
	   filter: invert(1)grayscale(1)!important;
    }
    
    /*nav bar & search div*/
    #hdtbSum, .g, #fbar, .gb_ja, gb_ga,.gb_Kc .gb_ga, .gb_ga, .gb_Ec .gb_ga, ._L6k, .vk_c, .kp-blk, #hdtbMenus, .mnr-c:not(:empty), .hdtbItm, ._Db:hover, .crp, #cdr_opt, .cdr_dlg, ._oc, .hdtbg #hdtbSum,  .rl_item, div.rl_feature, g-inner-card, .sfbgg, .sfbgx, .klbar, #hdtb,.vk_c .vk_c, ._thf ._qgo, .gb_Lc .gb_ha, .gb_Me, .gb_Ie {
        background-color: #242424;
        border: none;
    }
    
    .act-tim-fs-cnt:-moz-full-screen {
        background-color: #242424;
        border: none;
    }
    
    .act-tim-txt-cnt,  .act-tim-txt-cnt > div {
        background-color: #242424 !important;
    }
    
    .gb_Ic .gb_ga, .gb_la, .gb_Ec .gb_ga, .gb_ga, .rl_item, #rhs table.rhsvw, .knowledge-webanswers_table__webanswers-table tr, .sfbgx, #hdtb, .gb_ma, .appbar_b, .sect, .appbar {
        border-bottom: 1px solid #191919;
    }
    
    .gb_eb, .gb_ib, .gb_jb {
        border-bottom-color: #242424;
    }
    
    .gb_kb {
        border-bottom-color: #353535;
    }
    
    #fbar, .xpdopen ._U1i:not(:empty), .kp-blk .xpdopen ._xle, ._qJg, ._P4k, .cdr_sep, .vk_arc, .kp-blk .xpdclose ._wle, .xpdclose ._T1i:not(:empty), .ab_dropdownrule, ._onh, ._sWe ._b1j, ._Vsf, .act-btn-row {
        border-top: 1px solid #191919;
    }
    
    g-section-with-header, ._mip {
       margin: 0;   
    }
    
    ._NId > [data-hveid="200"] {
        margin: 0px -26px;
    }
    
    #subform_ctrl {
        min-height: 10px;
    }
    
    /*shadowed boxes*/
    .g, .kp-blk, .mnr-c:not(:empty), #hdtb, .crp, .cdr_dlg, .vk_c, .gb_Ic .gb_ga, .gb_ga, .klbar, .rl_center, g-inner-card {
       box-shadow: 0px 1px 4px 0px rgba(0,0,0,0.5);
    }
    
    #hdtb, #hdtbMenus, .gb_Me {
        box-shadow: 0px 4px 4px -2px rgba(0,0,0,0.5)
    }
    
    ._oc {
        box-shadow: 0px 1px 4px 0px rgba(0,0,0,0.5) !important;
    }
    
    /*green arrow thing*/
    ._Fmb, #am-b0:hover {
        color: #1CA05C !important;
        background-image: none;
        background-color: #242424 !important;
        border: none;
    }
    
    /*search div*/
    .g, ._yE {
        padding: 7px;
        margin-bottom: 10px; 
    }
    
    /*news*/
    ._baj, ._UIf ._hvg, ._lMn {
        border-right: 1px solid #191919;
    }
    
    ._SMl ._daj, .pla-unit-container {
        border-left: 1px solid #191919;
    }
    
    ._gNj, ._SMl ._gNj {
        border: 1px solid #191919;
        background-image: none;
        background-color: #191919;
        opacity: 0.4;
    }
    
    ._gNj:hover, ._SMl ._gNj:hover, #lst-ib {
        background-image: none;
        background-color: #191919;
        opacity: 1;
    }
    
    /*definition box*/
    .vk_ard, .vk_aru, .cwrb-tpl {
        background-color: #555555;
    } 
    
    .vk_ard::before, .vk_aru::before {
        border-top-color: #555555;
        border-bottom-color: #555555;
    }
    
    .vk_ard::after, .vk_aru::after {
        border-top-color: #262626;
        border-bottom-color: #262626;
    }
    
    .lr_dct_trns_sel, .ccw_unit_selector {
       font-weight: normal !important;
    }
    
    /*map*/
    _NId > ._rk .vk_c {
        margin: 0px !important;
        padding: 4px !important;
    }
    
    /*"sponsored content"*/
    .commercial-unit-desktop-top, .hdtb-ab-o #botabar, .cwlgb-tpl, .cwdgb-tpl, .scrt td, .rl_center, .gb_xb, #cnt > .appbar {
        border: none !important;
    }
    
    ._Sb, #res a, .tw-menu-btn-image:hover {
        background: none !important;
    }
    
    /*movie stuff*/
    ._W6g {
        opacity: 0.7 !important;
    }
    
    ._W6g:hover {
        opacity: 1.0 !important;
    }
    
    /*translate*/
    .tw-menu-btn:hover .tw-menu-btn-image:first-child, .lr_dct_trns_sel:hover {
        border-color: transparent;
        background-color: transparent; 
    }
    
    .tw-lang-selector, .lr_dct_trns_sel, .lr_dct_trns_sel:hover, .ccw_unit_selector, .ccw_unit_selector:hover, .mitem:hover {
        background: #191919;
    }
    
    ._wYb {
        width: 2px;
    }
    
    /*Currency*/
    .ccw_unit_selector {
        padding-top: 0px;
    }
    
    .ccw_selector_bg {
        width: 0px;
    }
    
    /*search bar*/
    #lst-ib, #foot #navcnt .cur, #hdtb .hdtb-mitem a:hover, #hdtb-msb #hdtb-more:hover, .kno-ecr-pt, ._czk, .hdtb-mn-hd:hover, .hdtbItm.hdtbSel, .price, ._xdb .fl, ._rl, ._XWk, .cwtltbl, .sbico, #cdrlnk, ._pk, ._Hu, .cdr_ttl, .cdr_mml, .ktf, .crl:hover, .vk_bk, .gb_ga, ._tvg, .cwlgb-tpl, .cwrb-tpl, .sbsb_i, .knowledge-webanswers_table__webanswers-table th, .sbsb_d:hover, .sbsb_d, ._Lbe, ._wtf, #abar_button_ss:hover, .act-tim-txt-cnt, .lr_dct_lbl_box, .gb_vb, ._Nve ._tHe, .jfk-textinput {
        color: #FFFFFF !important;
    }
    
    #hdtb .hdtb-mitem a, #hdtb-msb #hdtb-more, .hdtb-mn-hd, .cwdgb-tpl {
        color: #8C8C8C;
    }
    
    ._Bs {
        border-color: #8C8C8C transparent !important;
    }
    
    #hdtb-more:hover .mn-dwn-arw, .crl:hover ._Bs {
        border-color: #FFFFFF transparent !important;
    }
    
    .sbib_a, ._Db:active, .cMjHbjVt9AZ__button:active, .cwtlb, .sbibod, .ktf, .rlc__slider, .rlc__slider-page, .mlo-c, .gb_ka, .gb_ha, .gb_la, .jfk-textinput, .gb_wb {
        background-color: #353535;
    }
    
    .sbsb_a, .cdr_dlg .goog-date-picker, .cdr_highl, .cwdgb-tpl, .sbdd_b {
        background-color: #444444;
    }
    
    .sbsb_d:hover, .sbsb_d, ._jAg {
        background: #00ADEE !important;
    }
    
    #sfdiv {
        border-color: #353535;
    }
    
    .sbdd_b, .gb_Z:hover:not(.gb_0) a, ._zF, .tw-lang-selector-wrapper, .lr_dct_trns_sel, .ccw_unit_selector_cnt, .jfk-textinput, .wob_ds {
        border: 1px solid #191919;
    }
    
    ._qhf, .ccw_data {
        border-color: #191919;
    }
    
    .ktf:focus, .srp .sbhcn, .srp .sbfcn, .cwtlbs, .jfk-textinput:focus {
        border-color: #00ADEE !important;
    }
    .srp .sbfcn {
        box-shadow: 0px 0px 6px 0px #00ADEE;
    }
    
    /*buttons*/
    .kpbb , .gb_Ha, #sblsbb, .jhp input[type="submit"], #_x9b, #hdtb-tls:hover, .gb_Ec .gb_rb, .ab_button, .cwbb-tpl, .cdr_go, .gb_lb .gb_rb, .gb_Fa, .gb_Ic .gb_vb, .mslg .ab_button, ._UEg, .gb_Ia, #abar_button_ss, .sbdd_a input, .jfk-button-action, .ksb {
        color: #FFFFFF !important;
        background-color: #00ADEE !important;
        border-radius: 2px;
        background-image: none;
        border-color: #00ADEE;
        cursor: pointer !important;
    }
    
    .gb_Ha:hover, #sblsbb:hover, .jhp input[type="submit"]:hover, #_x9b:hover, #hdtb-msb .hdtb-tl-sel, #hdtb-msb .hdtb-tl-sel:hover, .gb_Ec .gb_rb:hover, .ab_button:hover, .cwbd:hover .cwbb-tpl,.cdr_go:hover, .gb_lb .gb_rb:hover, .gb_Fa:hover, .gb_Ic .gb_vb:hover, .mslg .ab_button:hover, ._UEg:hover, .gb_Ia:hover, .gb_Lc .gb_xb:hover, .ab_button.selected, .ab_button.selected:hover, #abar_button_ss:hover, .sbdd_a input:hover, .jfk-button-action:hover, .ksb:hover, .ksb.ksbs, .ksb.ksbs:hover {
        color: #DDDDDD !important;
        background-color: #00ADEE;
        background-image: -moz-linear-gradient(top,#008CBE, #008ABC);
        border-color: #008CBE;
        border-radius: 2px;    
    }
    
    #abar_button_ss {
        line-height: 28px !important;
        padding: 0 12px !important;
        font-weight: 400 !important;
    }
    
    /*#ab_ctls {
        padding-top: 22px;
    }*/
    
    /*calculator*/
    .cwbtpl {
       background-image: none;
       border-radius: 100px !important;   
    }
    
    .cwtlb {
        border-color: #363636;
    }
    
    .cwtld {
        height: 18%;
    }
    
    .cwlgb-tpl, .act-tim-paused .act-prog {
        background-color: #5c5c5c;
    }
    
    .cwdrsb-tpl, .cwrb-tpl {
        border-color: #363636;
    }
    
    .cwbd:hover .cwlgb-tpl {
        background-image: none;
        background-color: #7a7a7a;
    }
    
    .cwrb-tpl, .sfbgg {
        background-image: none;
    }
    
    /*Timer*/
    .act-timer-section .act-tim-paused .act-tim-txt-cnt {
        border-bottom-color: #444444;
    }
    
    .act-timer-section .act-tim-paused .act-tim-focus, .act-active-tab {
        border-bottom-color: #00ADEE;
    }
    
    .act-prog {
       background-color: #00ADEE;    
    }
    
    :-moz-full-screen .act-btn-row, .act-tim-fs-cnt, div._Mib {
        border-top-color: #191919;
    }
    
    /*dropdown*/
    .ab_icon {
        opacity: 1;
    }
    
    .ab_dropdown, .hdtb-mn-o, .o2ji6GoAIDp__menu, #hdtb.hdtba #hdtbMenus, ._D4o {
