    .ab_dropdownitem.selected, #hdtb-more-mn a:hover, .hdtbItm.hdtbSel:hover, .hdtbItm a:hover, #cdrlnk:hover, .gb_sb, .klitem:hover, .cwdrsb-tpl, .gb_Z:hover:not(.gb_0) a, .gb_xb, ._wYb, .ab_button, .abup, ._iH:hover, ._xk, .lr_dct_lbl_box, .ccw_data, .gb_yb, .wob_ds, .klitem.selected {
