    }
    
    /*text*/
    .s, ._Db, ._Db a:hover, .crp ._At, ._Adb, .sben_a, .gb_Qc, .med, .sh-sr__shop-result-header, ._gah, [class="sbsb_c gsfs"], .tw-text-edit-btn, .kltat, ._nPe, ._cke, ._x2i ._baf, ._wje, ._mgh, ._uxi, ._Hxi, .tw-ta, .gb_Rc, ._OFe, ._QFe, div._Ie div._Vm, div._Ie div._P9, div._qpb, .gb_wb, .sect, ._cnc {
