    /*general links*/
    a:link, #gb#gb a.gb_P, #hdtb .hdtbItm a, #gb#gb a.gb_O, ._Jrh ._Mrh, ._Jrh ._Orh, ._R4k, ._Rsi, .gl a:link, #tw-lsst, .act-active-tab, .tw-lang-selector:hover, .tw-lang-selector, .appbar, .goog-slider li {
