    }
    
    a:hover, #gb#gb a.gb_P:hover, .fbar a:hover, a.ab_dropdownlnk:hover, #hdtb .hdtbItm a:hover, ._R4k:hover, ._Db:hover ._rl, ._Rsi:hover, #tw-lsst:hover, ._x2i ._baf b, .sbpqs_d > .sbpqs_a {
