    }
    
    /*no underline link*/
    ._R4k:hover, #rcnt a:hover, ._Db:hover ._rl, .rl_item .title {
        text-decoration: none !important;
    }
    
    /*small links*/
    a, a.fl:link, ._Mqe, .lr_dct_more_btn, a._pi:link, .w, .rl_item .title, ._vxi, ._V9o, ._R7b {
