style.css:
@-moz-document domain("4chan.org"), domain("4channel.org") {
body, html {
	background-image: none;
	/*background color of whole page*/
	background-color: #2A2D2D;
	/*color of general text*/
	color: #8C8C8C;
}

hr, .cdr_sep, .yotsuba_new #watchList li:first-child {
	/*changes colors of those horizontal lines*/
	border-top-color: #3C3C3C;
}

.postMenuBtn,
a,
a:visited,
.postblock,
.replylink,
#boards a,
#ft li a,
.boxcontent a,
a.button,
.yotsuba_new .postMenuBtn {
	/*color of links*/
	color: #00ADEE;
}

div#absbot a:not(:hover),
a.replylink:not(:hover) {
    color: #00ADEE !important;
}

a:hover,
div.post div.postInfo span.postNum a:hover,
.yotsuba_b_new .backlink a:hover {
    color: red !important;
}

div.pagelist, #ft li, .dd-menu ul, #fp-menu, #disclaimer-dialog {
	/*color of bottom page list*/
	background-color: #242424;
	/*border color of page list*/
	border-color: #191919;
}

.dd-menu li, #fp-menu .fp-menu-sep {
    border-bottom-color: #191919;
}

#ft li.first {
	border-left: 1px solid #191919;
}

#ft ul {
	border-top: 0px;
}

div.boardBanner,
.dateTime {
	/*color of board title and post info*/
	color: #FFFFFF;
}

/*ad shrinking/hiding (still use adblock)*/
.topad > div, .ad-plea, .belowLeaderboard {
	display: none;
}

.fill {
	visibility: hidden;
}

/*main image hidng*/
#logo-fp {
	display: none;
}

.top-box .menubutton {
	background-image: none;
}

/*dropdown*/
/*.yui-skin-sam .yuimenu .bd {
	background: #242424;
	box-shadow: 0px 1px 4px 0px rgba(0,0,0,0.8);
	border: none;
}*/

/*.yuimenuitem:hover,*/ .postblock, div.post div.postInfoM,
.dd-menu li:hover, #fp-menu li:hover {
	background-color: #191919;
}

/*.yui-skin-sam .yuimenu ul,*/ div.post div.postInfoM, .panelHeader {
	border-color: #191919;
}


/*FAQ*/
a[name="basics"], a[name="contribution"], a[name="features"], a[name="administration"], a[name="banishment"], a[name="technical"], a[name="donations"], a[name="events"], a[name="about"]     {
	color: #800;
}
	
dt, 
div.post div.postInfo span.subjectm,
dt.first,
div.post div.postInfo span.subject {
	/*color of post title*/
	color: #376FC8;
}

.nameBlock.capcodeMod span.name, 
span.capcodeMod a span.name, 
span.capcodeMod span.postertrip, 
span.capcodeMod strong.capcode {
	/*mod colors*/
	color: #C400C4 !important;
}

div.post div.postInfo 
span.nameBlock 
span.name {
	/*default name color*/
	color: #1CA05C;
}

div#boardNavDesktop,
div#boardNavDesktopFoot,
div.post div.postInfo span.postNum a,
div#absbot, .boxcontent, div.pagelist, .yotsuba_new #qrFile {
	color: #8C8C8C;
}

div.post div.postInfoM span.postNum a {
	color: #8C8C8C;
}

div#boardNavDesktop a,
div#boardNavDesktopFoot a,
span.button {
	color: #5B63B9;
}

div#boardNavDesktop a:hover,
div#boardNavDesktopFoot a:hover {
	color: #FF0000;
}

div.reply,
.yotsuba_b_new #quote-preview.highlight,
.top-box,
#announce,
.box-outer,
#threadWatcher,
.panel {
	/*background color of thread comment*/
	background-color: #242424;
	box-shadow: 0px 1px 4px 0px rgba(0, 0, 0, 0.5);
	border: none;
	/*colors of comment borders*/
	border-color: #191919;
	margin-top: 10px;
}

.boxbar, div#boardNavMobile {
	background-color: #191919;
	color: red;
}

#qrFile {
	width: 220px;
}

table.flashListing tr:nth-of-type(2n+1),
#quote-preview,
.yotsuba_b_new #quote-preview.highlight,
.reply.highlight,
.reply:target {
	/*background color of highlighted posts*/
	background-color: #353535 !important;
	/*highlighted reply border colors*/
	border: none !important;
	border-color: #2B2B2B !important;
}

blockquote > span.quote {
	/*green text colors*/
	color: #A1FF0A;
}

.quotelink,
.burichan_new .backlink a, 
.yotsuba_b_new .backlink a {
	/*color of quote links*/
	color: #7E949E !important;
}

.deadlink {
	/*color of dead links*/
	color: #44545A !important;
}

.prettyprint {
	/*color of code text background*/
   background-color: #464646;
}

.kwd {
	color: #7C7CFF;
}

.pun {
	color: #DEDE1A;
}

.typ {
	color: #e539e5;
}

.com {
	color: #A1FF0A;
}

.str {
	color: #54FF54;
}

.lit {
	color: #00ADFF;
}

#bannerCnt, .c-thread img, .boxcontent dd, .postblock {
	/*removes border around banner*/
    border: none;
}

div.pagelist div.cataloglink {
	border-left: 1px solid #191919;
}

.pln {
	color: #FFFFFF;
}

/******************************************/
/* All CSS below this point is for 4chanX */
/******************************************/

:root.yotsuba-b .dialog,
:root.yotsuba .dialog {
    /* 4chanX settings */
    background-color: #242424;
    border-color: #191919;
}

:root.yotsuba-b .suboption-list > div:last-of-type,
:root.yotsuba .suboption-list > div:last-of-type {
    background-color: #353535;
}

:root.yotsuba-b .focused.entry,
:root.yotsuba .focused.entry {
    background-color: #191919;
}

:root.yotsuba-b #menu,
:root.yotsuba #menu {
    color: #8c8c8c;
}

:root.yotsuba-b #board-list a, :root.yotsuba-b #shortcuts a,
:root.yotsuba #board-list a, :root.yotsuba #shortcuts a {
    color: #5B63B9;
}

#board-list a, #shortcuts a:not(.entry) {
    padding: 0px;
}

.current {
    font-size: 10pt;
}

#custom-board-list {
    font-size: 9pt;
}

.backlink {
    color: #7E949E;
    font-size: 0.8em;
    text-decoration: underline;
}

:root.yotsuba-b #header-bar,
:root.yotsuba #header-bar {
    color: #7E949E;
}

.disabled:not(.replies-quoting-you), .quotelink.deadlink:not(.forwardlink) {
    opacity: 1;
    text-decoration: line-through !important;
}

:root.yotsuba-b .watch-thread-link,
:root.yotsuba .watch-thread-link {
    
    filter: grayscale(1.0) invert(0.8);
}

/* Custom filter highlights (hl1, hl2, etc) */
:root:not(.werkTyme) .catalog-thread.hl1 .catalog-thumb {
    box-shadow: 0 0 3px 3px #ff000080
}

:root:not(.werkTyme) .catalog-thread.hl2 .catalog-thumb {
    box-shadow: 0 0 3px 3px #00adee80;
}
}
