style.css:
@-moz-document domain("iwatchonline.cr") {

.ipsDataList.ipsDataList_zebra .ipsDataItem:not( .ipsDataItem_selected ):not( .ipsModerated ):not( .ipsDataItem_new ):not( .ipsDataItem_success ):not( .ipsDataItem_warning ):not( .ipsDataItem_error ):not( .ipsDataItem_info ):not( .ipsDataItem_status ):nth-child(even) {
    background: #000000;
    
}

.ipsAreaBackground_reset {
    background: #000000
}

#ipsLayout_contentWrapper {
    width: 100%;
    background-color: #000000;
}

#ipsLayout_header header {
    padding: 5px 0 130px 0;
    margin-bottom: -125px;
    background-color: #000000;
}

.ipsNavBar_secondary {
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    height: 40px;
    z-index: 1000;
    display: none;
    background: #000000;
}

.ipsNavBar_primary {
    position: relative;
    padding: 5px 5px 40px 5px;
    z-index: 2000;
    background: #070133 ;
    border-radius: 2px 2px 0px 0px;
}

.ipsNavBar_secondary > li > a {
    color: rgba(255, 0, 0, 0.6);
    font-size: 13px;
    padding: 12px 18px 8px;
    border-bottom: 2px solid transparent;
}

.ipsNavBar_secondary > li.ipsNavBar_active a {
    font-weight: bold;
    border-color: rgba(51,51,51,0.3);
    color: #ff0000;
}

.ipsType_richText {
    word-wrap: break-word;
    overflow-wrap: break-word;
    line-height: 1.5;
    color: #3252bf;
}

body {
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    font-size: 13px;
    line-height: 18px;
    color: #525252;
    height: 100%;
    background-color: #000000;
}

.ipsAreaBackground_light {
    background: #2f2e2e;
}

.ipsBox:not( .ipsBox_transparent ):not( .ipsModerated ) {
    background-color: #03062b;
}

.ipsType_sectionHead {
    font-size: 20px;
    color: #d00000;
    line-height: 24px;
    font-weight: 400;
    display: inline-block;
    margin: 0;
}

.ipsType_light {
    color: #3252bf;
}

.cUserHovercard .ipsPageHead_special .ipsType_sectionHead, .cUserHovercard .ipsPageHead_special p {
    position: absolute;
    color: #f10505;
    font-weight: 400;
    text-shadow: 0px 0px 4px rgba(0,0,0,0.5);
}

.ipsDataItem {
    width: 100%;
    position: relative;
    border-width: 0 0 1px 0;
    border-style: solid;
    border-color: #e01313;
}

.ipsHovercard {
    background: #000000;
    box-shadow: 0px 7px 20px rgba(0, 0, 0, 0.2), 0px 5px 20px rgba(6, 6, 6, 0.1);
    border: 1px solid #1615c7;
    border-radius: 4px;
    position: relative;
}

.ipsAreaBackground {
    background: #333030;
}

a.ipsType_sectionTitle, .ipsType_sectionTitle a {
    color: #a50d8a;
}

.ipsType_sectionTitle {
    font-size: 15px;
    font-weight: 400;
    padding: 11px 15px 11px 15px;
    margin-top: 0;
    background: #070133;
    color: #1d1a1a;
    border-radius: 2px 2px 0px 0px;
}

.ipsAreaBackground_light {
    background: #000000;
}

.ipsType_richText {
    word-wrap: break-word;
    overflow-wrap: break-word;
    line-height: 1.5;
    color: #747b94;
}

.ipsPip {
    font-size: 10px;
    color: #9e1999;
    display: inline-block;
}

#elUserNav > li > a {
    color: #b321a6;
    font-size: 13px;
    vertical-align: middle;
    line-height: 1;
}

.ipsBreadcrumb > ul > li {
    display: inline-block;
    line-height: 1;
    font-size: 13px;
    font-weight: 400;
    max-width: 500px;
    height: 15px;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    vertical-align: bottom;
    color: #ff00e8;
}

.ipsQuote {
    margin: 0;
    padding: 0px 15px;
    border-width: 1px 1px 1px 2px;
    border-color: #1c1eb1 #a712a9 #b513b1 #b512b1;
    border-style: solid;
    position: relative;
    background: #020523;
    clear: both;
}

.ipsQuote_citation {
    background: #b512b1;
    margin: 0px -15px;
    padding: 5px 15px;
    color: #222;
    font-weight: bold;
    font-size: 13px;
    display: block;
}

.ipsLikeRep {
    background: #2d0c2d;
    border-radius: 2px;
    padding: 3px;
    line-height: 16px;
    display: inline-block;
    position: relative;
}

.ipsApp .ipsButton_alternate {
    background: #000000;
    color: #ff00e9;
}

.ipsApp .ipsButton_veryLight {
    background: #9f00ff;
    border-color: #e8e8e8;
    color: #000000;
}

.ipsApp .ipsButton_light {
    background: #333333;
    border-color: rgba(0,0,0,0.03);
    color: #da0ac8;
}

.ipsAreaBackground {
    background: #460741;
}

.ipsEmbedded_content {
    padding: 9px 7px 7px;
    background-color: #020523;
    min-height: 75px;
}

.ipsEmbedded_headerArea {
    background: #b513b1;
    border-bottom: 1px solid rgba(0,0,0,0.055);
    padding: 10px;
    line-height: 1;
    font-size: 16px;
}

iframe[data-embedContent] {
    width: 100%;
    min-height: 165px;
    margin: 10px 0;
    border: 1px solid rgba(0,0,0,0.1);
    position: relative;
    background-color: #171616;
    background-image: url("https://www.iwatchonline.cr/f/uploads/set_resources_1/cf5a1e7fc191338071d07df134b620d0_large_loading@2x.gif");
    background-repeat: no-repeat;
    background-position: center center;
    background-size: 40px 40px;
}

.ipsAreaBackground_reset {
    background: #0c0d38;
}

.ipsType_sectionTitle {
    font-size: 15px;
    font-weight: 400;
    padding: 11px 15px 11px 15px;
    margin-top: 0;
    background: #070133;
    color: #d620b6;
    border-radius: 2px 2px 0px 0px;
}

.ipsType_pageTitle {
    font-size: 26px;
    font-weight: 400;
    line-height: 1.2;
    margin: 0;
    color: #df09e0;
}






































































































































































