style.css:
@-moz-document url-prefix("http://www.liaoxuefeng.com/") {
body div.x-goto-top {
    background-color: #000;
    border: 1px solid #525354;
}
body div.x-sidebar-right h3 {
    color: #2ec1cd;
}
body div.x-center .x-content h3,
h4 {
    color: #2ec1cd;
}
html {
    background: #28292A;
}
body {
    background: #28292A;
}
.x-sidebar-left .uk-nav-header {
    color: #2ec1cd;
}
.x-sidebar-left .uk-nav-side a {
    color: #2ea6cd;
}
.x-center .x-content pre {
    background: #525354;
    border: 1px solid #686969;
}
.x-center .x-content pre .prompt {
    color: #65d7ef;
}
.x-center .x-content pre .string {
    color: #65d7ef;
}
.x-center .x-content .x-wiki-content h1 {
    color: #fff;
}
.x-sidebar-left h3 {
    color: #2ec1cd;
}
.x-center .x-content .x-wiki-content {
    color: #65d7ef;
}
.x-center .x-content .x-wiki-content code {
    color: #92e3f3;
    background: #525354;
    border: 1px solid #686969;
}
.uk-button {
    background: #3c818f;
    text-shadow: 0 1px 0 #28565f;
}
#x-comment-list .uk-comment .uk-comment-body code {
    color: #d3d3d3;
}
}

@-moz-document url-prefix("http://www.liaoxuefeng.com/discuss") {
.x-center .x-content .uk-alert {
    color: #8cdee5;
    background: #525354;
    text-shadow: 0 1px 0 #000;
}
.x-center .x-content .uk-table tr:hover {
    background-color: #000;
}
.x-center .x-content .uk-comment .uk-comment-body code {
    color: #92e3f3;
}
}
