style.css:
@-moz-document url-prefix("https://discordapp.com/channels/"), url-prefix("https://canary.discordapp.com/channels/"), url-prefix("https://ptb.discordapp.com/channels/") {.dummyclass{display:none}
:root{ --version-content:"2.1"}
body:after{ content:"Current Version: v"var(--version-content);  z-index:10000000000000000000000000;  color:#fff;  background:/*[[Color]]*/;  top:0;  left:50%;  position:absolute;  border-radius:5px;  padding:5px 15px;  -webkit-transform:translate(-50%,-100%);  transform:translate(-50%,-100%);  -webkit-animation:vrsion 3s ease;  animation:vrsion 3s ease;  -webkit-animation-delay:1s;  animation-delay:1s}

body:before{ content:"Thank's for using Quiet!";  z-index:1000000000000000000000000;  color:#fff;  background:/*[[Color]]*/;  top:0px;  left:50%;  position:absolute;  border-radius:5px;  padding:5px 15px;  -webkit-transform:translate(-50%,-100%);  transform:translate(-50%,-100%);  -webkit-animation:vrsion 3s ease;  animation:vrsion 3s ease;  -webkit-animation-delay:4s;  animation-delay:4s}

@-webkit-keyframes vrsion{ from{ -webkit-transform:translate(-50%,-100%);  transform:translate(-50%,-100%)}
 20%{ -webkit-transform:translate(-50%,20%);  transform:translate(-50%,20%) }
 80%{ -webkit-transform:translate(-50%,20%);  transform:translate(-50%,20%) }
 to{ -webkit-transform:translate(-50%,-100%);  transform:translate(-50%,-100%)}
}

@keyframes vrsion{ from{ -webkit-transform:translate(-50%,-100%);  transform:translate(-50%,-100%)}
 20%{ -webkit-transform:translate(-50%,20%);  transform:translate(-50%,20%) }
 80%{ -webkit-transform:translate(-50%,20%);  transform:translate(-50%,20%) }
 to{ -webkit-transform:translate(-50%,-100%);  transform:translate(-50%,-100%)}
}
.help-container{display:none}

.links, 
.chat, 
.typing, 
.content, 
.private-channels, 
.divider-red span, 
.messages-wrapper, 
#voice-connection{background:transparent !important}

