#voice-connection .btn{background:#19191b; box-shadow:none !important}

.title-wrap{border-bottom:#19191b !important; background:#19191b !important}

.chat .new-messages-bar{background-color:/*[[Color]]*/}

.chat .new-messages-bar:hover{background-color:/*[[Hovercolor]]*/}

.chat>.title-wrap>.topic{font-size:14px; margin-top:5px; color:#656565 !important}

