.channel-members h2{ color:/*[[Color]]*/ !important; background-image: -webkit-linear-gradient(left,transparent,transparent), -webkit-linear-gradient(left,transparent,/*[[Color]]*/,transparent); background-image: linear-gradient(90deg,transparent,transparent), linear-gradient(90deg,transparent,/*[[Color]]*/,transparent); background-size:100% 3px; /* This get flipped. */background-position:0 0,0 100%; /* The last argument gets flipped. */background-repeat:no-repeat;  padding-top:8px;  padding-bottom:8px; text-align:center; border-radius:3px;  width:145px;  margin-left:15px}

