.tooltip.tooltip-top:after{ border-top-color:/*[[Color]]*/ !important}
.tooltip.tooltip-right:after{ border-right-color:/*[[Color]]*/ !important}
.tooltip.tooltip-bottom:after{ border-bottom-color:/*[[Color]]*/ !important}
.bot-tag{ background:/*[[Color]]*/;  color:#e8e8e8!important}
