.theme-dark .chat .title-wrap .topic .highlight:hover{ background-color:/*[[Hovercolor]]*/ !important;  color:#fff !important;  opacity:0.9}
.theme-dark .message-group .comment .markup .highlight, .theme-dark .chat .title-wrap .topic .highlight{ -webkit-transition:.3s ease-in-out all;  transition:.3s ease-in-out all;  background:rgba(10,9,9,0.35)}
.guilds .friends-icon:hover{ background-color:/*[[Color]]*/}
.flex-vertical.channels-wrap{ width:230px}
.theme-dark .channel-members .member.popout-open, .theme-dark .channel-members .member:hover{ background:-webkit-linear-gradient(left,#121213 85%,#19191b) !important;  background:linear-gradient(to right,#121213 85%,#19191b) !important}
.user-popout .avatar-wrapper .avatar-popout{ border:2px solid #141417; background-color:/*[[Hovercolor]]*/}
.user-popout .header{background:#141417}
