.user-popout .footer{ background:#0e0e0e;  border-top:2px solid /*[[Hovercolor]]*/}
.member-roles .member-role{ background:/*[[Hovercolor]]*/;  border-color:/*[[Color]]*/;  color:#d8d8d8}
.quick-message{ background:#0b0b0c;  border:2px solid /*[[Hovercolor]]*/;  color:#adadad}
.user-popout .username-wrapper .activity{ color:#c7c7c7}
.protip, .user-popout .body .section .label{ color:#c7c7c7}
.bot-tag.bot-tag-invert{ background:/*[[Color]]*/;  color:#e8e8e8!important}
.guilds-wrapper .guilds .guild.active:first-of-type .guild-inner{ background:/*[[Color]]*/ !important}
.user-popout .username-wrapper.has-nickname .discriminator, .user-popout .username-wrapper.has-nickname .username{    color:#b5b5b5}
#user-profile-modal{opacity:1; position:relative}
 
#user-profile-modal .avatar-wrapper .avatar-profile, 
#user-profile-modal .sub-header, 
#user-profile-modal .scroller-wrap, 
#user-profile-modal .empty, 
#user-profile-modal .scroller{background-color:#141415 !important}
 
#user-profile-modal .header:before{content:""; width:100%; height:100px; background-size:cover; background-position:center center; background-repeat:no-repeat; position:absolute; top:0; right:0; left:0; z-index:-1}
 
#user-profile-modal .header .activity{        margin-left:-50px; margin-top:-4px; padding-top:13px !important}
 
#user-profile-modal .header .discord-tag .username, 
#user-profile-modal .header .activity{text-shadow:1px 1px 1px rgba(0,0,0,0.3)}
 
#user-profile-modal .header .discord-tag .discriminator{display:inline; background:rgba(93,93,93,0.3); font-weight:600; color:#fff; margin-left:8px; padding:3px 5px; border-radius:3px; -webkit-user-select:none; opacity:1; -webkit-transition:opacity 300ms; transition:opacity 300ms}
 
#user-profile-modal .avatar-profile .status{border:4px solid #19191b}
 
#user-profile-modal .avatar-wrapper .avatar-profile{border-color:#19191b; box-shadow:1px 1px 2px rgba(0,0,0,0.3); left:20px}
 
#user-profile-modal .avatar-wrapper .avatar-profile .status{box-shadow:1px 1px 2px rgba(0,0,0,0.3); right:0; bottom:0; left: -3px;}
 
#user-profile-modal .header{background-color:#121213; background-image:none}

#user-profile-modal .tab-bar-container{    border:0!important;     position:absolute;     top:120px;     right:0;     padding:0;     width:80px;     height:100%;     -webkit-box-pack:center;         -ms-flex-pack:center;             justify-content:center;     -webkit-transition:width 500ms;     transition:width 500ms;     -webkit-transition-timing-function:cubic-bezier(0.52,1.64,0.37,0.66);             transition-timing-function:cubic-bezier(0.52,1.64,0.37,0.66);     z-index:1;     background-color:#121213;     background-size:270px 213px}
 
#user-profile-modal .tab-bar-container:before{content:"..."; position:absolute; top:0; left:0; width:10px; height:100%; padding-top:180px; background:transparent; -webkit-writing-mode:vertical-lr; -ms-writing-mode:tb-lr; writing-mode:vertical-lr; color:transparent; font-size:18px; font-weight:700; letter-spacing:15px; line-height:20px; cursor:pointer; -webkit-transition:letter-spacing 300ms,padding 300ms; transition:letter-spacing 300ms,padding 300ms; }
 
#user-profile-modal .tab-bar-container:hover{width:180px}
 
#user-profile-modal .tab-bar-container .tab-bar{background:transparent; border:0!important; -ms-flex-flow:column; flex-flow:column; position:absolute; top:40px; left:35px}
 
#user-profile-modal .tab-bar-container .tab-bar-item{padding:10px 25px; position:relative; font-size:16px; white-space:nowrap; overflow:hidden; text-overflow:ellipsis}
 
#user-profile-modal .tab-bar-container .tab-bar-item, 
#user-profile-modal .tab-bar-container .tab-bar-item:hover, 
#user-profile-modal .tab-bar-container .tab-bar-item.selected{color:transparent!important}
 
#user-profile-modal .tab-bar-container:hover .tab-bar-item{color:#ececec!important; opacity:1; -webkit-transition:color 500ms; transition:color 500ms}
 
#user-profile-modal .tab-bar-container:hover .tab-bar-item:hover, 
#user-profile-modal .tab-bar-container:hover .tab-bar-item.selected{color:#fff!important}
 
#user-profile-modal .tab-bar-container .tab-bar-item:before{content:""; position:absolute; top:7px; left:0; width:18px; height:18px; background-position:center center; background-repeat:no-repeat; background-size:18px 18px; opacity:0.5; -webkit-transition:opacity 500ms; transition:opacity 500ms}
 
#user-profile-modal .tab-bar-container .tab-bar-item:hover:before, 
#user-profile-modal .tab-bar-container .tab-bar-item.selected:before{opacity:1}
 
#user-profile-modal .tab-bar-container .tab-bar-item:nth-child(1):before{
    background-image: url(data:image/svg+xml;utf8;base64,);
}
 
#user-profile-modal .tab-bar-container .tab-bar-item:nth-child(2):before{
    background-image: url(data:image/svg+xml;utf8;base64,);
}
 
#user-profile-modal .tab-bar-container .tab-bar-item:nth-child(3):before{
    background-image: url(data:image/svg+xml;utf8;base64,);
}
 
#user-profile-modal .tab-bar-container .tab-bar-item:first-child{margin-top:90px}
 
#user-profile-modal .tab-bar-container .tab-bar-item:hover, 
#user-profile-modal .tab-bar-container .tab-bar-item.selected{border:0!important}
 
#user-profile-modal .tab-bar-container .tab-bar-item+.tab-bar-item{margin:0}
 
#user-profile-modal .scroller-wrap{height:380px}
 
#user-profile-modal .scroller-wrap .scroller{margin-top:15px; -webkit-animation:sw-fade 300ms ease-in-out; animation:sw-fade 300ms ease-in-out}
 
@-webkit-keyframes sw-fade{    0%{opacity:0}
    100%{opacity:1}
}
 
@keyframes sw-fade{    0%{opacity:0}
    100%{opacity:1}
}
 
 
#user-profile-modal .guilds .section .section-header{color:#6c82ce}
 
#user-profile-modal .guilds .section+.section{border:0!important}
 
#user-profile-modal .guilds .section .note textarea:focus{background-color:rgba(0,0,0,0.15); border:1px solid rgba(0,0,0,0.1); color:#e4e4e4}
 
#user-profile-modal .guilds .section .connected-account .connected-account-name a{color:rgba(0,176,244,0.6); font-weight:700; -webkit-transition:color 500ms; transition:color 500ms}
 
#user-profile-modal .guilds .section .connected-account:hover .connected-account-name a{color:rgba(0,176,244,1)}
 
#user-profile-modal .guilds .guild{color:#c1c1c1; margin:0 10px; -webkit-transition:background 300ms; transition:background 300ms}
 
#user-profile-modal .guilds .guild .avatar-large{box-shadow:1px 1px 2px rgba(0,0,0,0.3)}
 
#user-profile-modal .guilds .guild .avatar-large:empty{border-radius:15px!important}
 
#user-profile-modal .guilds .guild:hover{border-radius:5px; color:#fff; width:300px; background:-webkit-linear-gradient(left,#121213 90%,#141415); background:linear-gradient(to right,#121213 90%,#141415)}
 
#user-profile-modal .guilds .guild .status{border:2px solid rgba(64,68,74,1)}
#user-profile-modal .guilds .guild:hover .status{border:2px solid rgba(133,141,150,0.7)}
 
#user-profile-modal .guilds .guild .guild-name .discriminator{display:none}
#user-profile-modal .guilds .guild:hover .guild-name .discriminator{display:block}
 
 
#user-profile-modal footer .btn.add-friend{-webkit-transition:background 500ms; transition:background 500ms}
 
#user-profile-modal .scroller-wrap .scroller::-webkit-scrollbar{width:0px}
 
#user-profile-modal footer{    border-top:1px solid transparent; background:#141415 !important;     z-index:10}
 
#user-profile-modal .guilds .section{    margin-left:47px;     margin-right:29px;     padding-top:17px;     padding-bottom:25px}
#friends .friends-table .friends-table-header .friends-column, 
#friends .friends-table .friends-row .friends-column-name, 
#friends .friends-table .friends-row .friends-column-status{color:rgba(255,255,255,0.6)!important}

#friends .friends-table{padding-top:10px}

#friends .friends-table .friends-table-header{display:none}

#friends .friends-table .friends-table-body >span{display:-webkit-box; display:-ms-flexbox; display:flex; -ms-flex-flow:row wrap; flex-flow:row wrap; -webkit-box-pack:center; -ms-flex-pack:center; justify-content:center}

#friends .friends-table .friends-row{background:rgba(0,0,0,0.2)!important; box-shadow:1px 1px 2px rgba(0,0,0,0.5); border-radius:5px; margin:15px; padding:0; height:120px!important; -webkit-box-orient:vertical; -webkit-box-direction:normal; -ms-flex-direction:column; flex-direction:column; -ms-flex-preferred-size:300px; flex-basis:300px; position:relative; opacity:0.85!important; -webkit-transition:opacity 300ms ease-ins; transition:opacity 300ms ease-ins}

#friends .friends-table .friends-row:hover{box-shadow:1px 1px 2px rgba(0,0,0,0.5); border-radius:5px; margin:15px; padding:0; opacity:1!important}

#friends .friends-table .friends-row .avatar-small{border-radius:5px; box-shadow:1px 1px 2px rgba(0,0,0,0.3)}

#friends .friends-table .friends-row+.friends-row{border-top:none!important}

#friends .friends-table .friends-row .friends-column{position:absolute}

#friends .friends-table .friends-row .friends-column+.friends-column{margin:0}

#friends .friends-table .friends-row .friends-column-name .avatar-small{top:25px; left:15px; width:85px; height:85px; background-size:85px 85px}

#friends .friends-table .friends-row .friends-column-name .discord-tag{display:block; position:absolute; top:25px; left:115px; text-shadow:1px 1px 1px rgba(0,0,0,0.3)}

#friends .friends-table .friends-row .friends-column-name .discord-tag .discriminator{visibility:visible}

#friends .friends-table .friends-row .friends-column-name .discord-tag span{clear:left; float:left}

