style.css:
/* i really want this to be global */
nav.navigation {
    max-height: 2.5rem;
}
nav .navigation-image {
    height: 4rem;
    padding: 1rem 1rem 2rem 0;
    vertical-align: top;
}
nav .navigation-link {
    height: 2.5rem;
    padding-top: 0.5rem;
}
.notification-image {
    height: 1.8rem;
    margin-top: 0;
}
.main-menu-toggle img {
    margin-top: 0rem;
    width: 2rem;
    height: 2rem;
    border-radius: 50%;
}
.main-menu-toggle .caret,
.main-menu-toggle .dropdown:after {
    top: 25%;
}
.sidebar .link a {
    font-size: 0.6rem;
    padding: 0.5rem;
    line-height: normal;
}
.sidebar-left {
    width: 10%;
}
.sidebar-right {
    width: 22%;
    margin-top: -30px;
}
.col-lg-7,
.container-with-two-sidebar {
    width: 68%;
    padding: 0;
    max-height: 570px;
    overflow-y: auto;
    overflow-x: hidden;
}
.sidebar .link .sublink>a {
    padding: 0.2rem 0.8rem;
}
.sidebar-estimation .estimate-description {
    display: none;
}
.btn,
button,
input[type=button],
.sidebar-workroom .sub-tasks button {
    text-align: center;
    font-size: .7rem;
    font-weight: 600;
    text-transform: uppercase;
    padding: 0.2rem;
    line-height: normal;
}
.sidebar-estimation .estimate-amount button {
    padding-top: 0;
    padding-bottom: 0;
}
#commentsForm textarea,
#commentsForm {
    margin: 0;
    width: 98%;
}
#commentsForm .tabs {
    border-radius: 0;
    border: none!important;
    width: 98%;
}
#commentsForm .tabs .tab {
    border: none!important;
    border-radius: 0;
}
.scroll-area {
    height: 425px!important;
}
.sidebar-workroom h3 {
    margin: 0;
}
.sidebar-workroom .user .user-box {
    padding: 0;
}
.sidebar-workroom .sub-tasks .sub-tasks-state {
    display: none;
}
.m-b-05 {

}
.m-b-1 h3 {
    display: none;
}
.sidebar-workroom .user-link img,
.sidebar-estimation .estimator .avatar img {
    display: none;
}
.sidebar-estimation .estimator {
    padding: 0;
}
.modal-dialog {
    width: 800px;
}
.sidebar-workroom .user .avatar i {
    right: -13.95rem;
}
.sidebar-workroom .project-completed span {
    font-size: 0.7rem;
    line-height: normal;
}
.attachments {
    display: block;
    color: #7c8d99;
    font-size: .7rem;
    line-height: normal;
    max-height: 200px;
    overflow-y: auto;
}
#commentsForm .tabs {
    font-size: .6rem;
}
#commentsForm .tabs .tab {
    padding: 0;
}
.comments .comment .content .message,
.activity-client .details,
.activity-contractor .details {
    font-size: 0.9rem;
    line-height: normal;
    color: #3c444a;
    word-break: break-word;
}
.comments .comment .avatar img {
    height: 2rem;
    width: 2rem;
    border-radius: 50%;
}
.sidebar-estimation .estimate-header {
    color: #fff;
    font-size: 0.8rem;
    text-align: center;
    line-height: normal;
    padding: 0.3rem;
}
.m-b-05,
.sidebar .label-text,
.task-text h6,
.modal-sub-tasks .sub-tasks .sub-task p {
    line-height: normal;
}
