/*
 * VK 2016 New design
 *
 * @authors: Stavrapid
 *
 * @description: this is the custom CSS plug for new.vk.com that addes new messages screen bubble-styled design and fixes some original minor issues as well.
 */


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vk.com") {
/* apply only on vk.com */
    
    @keyframes pulse {
        0% { transform: scale(1); }
        50% { transform: scale(1.2); }
        100% { transform: scale(1); }
    }
    
    @keyframes opacity-pulse {
        0% { opacity: 1; }
        50% { opacity: 0.5; }
        100% { opacity: 1; }
    }
    
    /* outline fix
    -----------------------------------------------------------------------------------*/
    body * {
        outline: none !important;
    }
    
    /* VK logo fix
    -----------------------------------------------------------------------------------*/
    
        .top_home_link .top_home_logo {
        position: relative;
        /*text-transform: uppercase;*/
    }
    
    .top_home_link .top_home_logo::after {
        top: 0;
        left: 33px;
        position: absolute;
        content: '';
        height: 20px;
        width: 77px;
        background:  url('data:image/svg+xml;base64,');
        font-family: Roboto;
        font-size: 15px;
        white-space: pre;
        color: #fff;
    }
    
    /*[[fixed_menu]]*/
    
    /* hide popular music
    -----------------------------------------------------------------------------------*/
    a[id *= "ui_rmenu_audio_popular"] {
        display: none !important;
    }
    
    /*[[hide_mu_suggest]]*/
    
    /*[[hide_mes_info]]*/
    
    /* user avatar fix
    -----------------------------------------------------------------------------------*/
   /* #profile #narrow_column.narrow_column .page_photo {
        padding: 0;
    }
    
    #profile #narrow_column.narrow_column .page_photo #page_avatar {
        height: 230px;
        border-radius: 3px 3px 0 0 ;
    }
    
    #profile #narrow_column.narrow_column .page_photo #page_avatar .page_avatar_img {
        border-radius: 0;
        transform: scale(1.15);
        margin-top: 15px;
    }*/
    
    #profile #narrow_column.narrow_column #owner_photo_wrap #profile_photo_link .page_avatar_img {
        transition-duration: 0.2s;
    }
    
    #profile #narrow_column.narrow_column #owner_photo_wrap {
        transition-delay: 0.2s;
    }
    
    #profile #narrow_column.narrow_column #owner_photo_wrap:hover {
        background: #000;
        transition-delay: 0s;
    }
    
    #profile #narrow_column.narrow_column #owner_photo_wrap:hover #profile_photo_link .page_avatar_img {
        filter: blur(5.5px);
        opacity: 0.75;
    }
    
   /* #profile #narrow_column.narrow_column .page_photo .owner_photo_bubble_wrap,
    #profile #narrow_column.narrow_column .page_photo .owner_photo_bubble {
        width: 230px;
        text-align: center;
    }*/
    
    #profile #narrow_column.narrow_column .page_photo .owner_photo_top_bubble_wrap .ui_thumb_x_button {
        z-index: 1;
    }
    
   /* #profile #narrow_column.narrow_column .page_photo .profile_actions {
        padding: 5px 15px 15px;
    }*/
    
    /*[[dialog_fix]]*/
    
    /*[[hide_dialog_preview]]*/
    
    /*~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
      ~~~~~~~~~~~~~~~~~~~~~~~~~~ MESSAGE BUBBLES STYLE BEGIN ~~~~~~~~~~~~~~~~~~~~~~~~~~
      ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
    
    ._im_peer_history > .im-mess-stack {
        display: inline-block !important;
        width: 100% !important;
    }
    
    .im-mess-stack .im-mess-stack--mess .im-mess:first-child > .im-mess--text {
        margin-top: 0 !important;
    }
    
    .im-mess-stack .im-mess-stack--mess li:last-of-type {
        border: none !important;
    }
    
    .im-mess.im-mess_selected,
    .im-mess.im-mess_selected:hover {
        background: initial !important;
    }
    
    .im-mess.im-mess_light {
        background: none;
    }
    
    .im-mess.im-mess_light ._im_log_body {
        /*filter: brightness(0.75);*/
        animation-name: opacity-pulse;
        animation-duration: 1.2s;
    }
    
    /* messages checker
    -----------------------------------------------------------------------------------*/
    .im-mess .im-mess--check {
        top: 0 !important;
        bottom: 0 !important;
        left: 15px;
        margin: auto !important;
    }
    
    .im-mess.im-mess_selected + .im-mess::before, .im-mess.im-mess_unread + .im-mess::before {
        display: none !important;
    }
    
    .im-mess.im-mess_selected {
        filter: brightness(0.75);
    }
    
    .im-mess.im-mess_selected > .im-mess--fav._im_mess_fav.fl_r, .im-mess.im-mess_selected > .im-mess--check.fl_l {
        filter: brightness(1.5);
    }
    
    /* unread message style
    -----------------------------------------------------------------------------------*/
    .im-mess-stack .im-mess-stack--mess .im-mess.im-mess_unread:first-child .im-mess--marker {
        top: 0;
        bottom: 0;
        position: absolute;
        margin: auto;
    }
    
    .im-mess.im-mess_unread, .im-mess.im-mess_selected:last-child::before, .im-mess.im-mess_unread:last-child::before {
        background: none;
    }
    
    .im-mess.im-mess_unread .im-mess--marker {
        width: 10px;
        height: 10px;
        left: 13px;
        background: #7293B6;
        /*border: 2px solid #fff;
        box-shadow: 0px 0px 0px 2px #7293B6;*/
        /*display: none;*/ /*Commented to fix send error issue*/
    }
    
    .im-mess.im-mess_unread ._im_log_body {
        transition: opacity 0.3s ease;
        opacity: 0.5;
    }
    
    .im-mess  {
        position: relative;
        background: none !important;
    }
    
    .im-mess::after  {
        position: absolute;
        top: 0;
        left: 0;
        content: "";
        z-index: 1;
        height: 100%;
        background: rgba(114, 147, 182, 0.1);
        padding-right: 1px;
        margin-left: 90px;
        margin-right: 90px;
        right: 0;
        border-radius: 15px;
        transition: margin-left 0.75s, opacity 0.75s;
        pointer-events: none;
    }
    
    .im-mess.im-mess:not(.im-mess_unread)::after  {
        margin-left: 100%;
        opacity: 0;
        margin-right: 90px;
    }

    /* failed message style
    -----------------------------------------------------------------------------------*/
    .im-mess.im_out.im-mess_failed .im-mess--marker, 
    .im-mess-stack[data-peer = "/*[[id]]*/"] .im-mess:not(.im-mess_fwd).im-mess_failed .im-mess--marker {
        left: 564px;
    }
    
    /* message favoriter and reply and edit
    -----------------------------------------------------------------------------------*/
    .im-mess .im-mess--fav {
        right: 13px !important;
        top: 16px !important;
        bottom: 0 !important;
        position: absolute !important;
        margin: auto !important;
    }
    
    .im-mess .im-mess--reply {
        right: 13px !important;
        top: -17px !important;
        bottom: 0 !important;
        position: absolute !important;
        margin: auto !important;
    }

    .im-mess .im-mess--edit {
        right: 13px !important;
        top: -17px !important;
        bottom: 0 !important;
        position: absolute !important;
        margin: auto !important;
    }
    
    ._im_history_tooltip.im-reply-tt {
        left: 521px !important;
    }
    
    ._im_history_tooltip.im-reply-tt:after {
        left: 48px !important;
    }
    
    ._im_history_tooltip.im-edit-tt {
        left: 485px !important;
    }
    
    ._im_history_tooltip.im-edit-tt:after {
        left: 84px !important;
    }

    ._im_history_tooltip.im-star-tt_long {
        left: 445px !important;
    }
    
    ._im_history_tooltip.im-star-tt_long:after {
        left: 124px !important;
    }
    
    /* ???
    -----------------------------------------------------------------------------------*/
    .im-mess-stack .im-mess {
        position: relative !important;
        display: inline-block !important;
        width: 100% !important;
        padding: 0 !important;
        margin: -1px 0 !important;
    }
    
    /* income bubble shape
    -----------------------------------------------------------------------------------*/
    .im-mess.im_in > ._im_log_body,
    .im-mess-stack:not([data-peer = "/*[[id]]*/"]) .im-mess:not(.im-mess_fwd) > ._im_log_body {
        display: inline-block !important;
        float: left !important;
        max-width: 398px !important;
        margin: 0 0 0 90px !important;
        border-radius: 13px !important;
        background: #EDEEF0;
        padding: 5px 8px;
        /*color:  !important;*/
    }
    
    /* income bubble tail
    -----------------------------------------------------------------------------------*/
    .im-mess-stack:not([data-peer = "/*[[id]]*/"]) .im-mess-stack--mess .im-mess.im_in:last-of-type > ._im_log_body::after,
    .im-mess-stack:not([data-peer = "/*[[id]]*/"]) .im-mess-stack--mess .im-mess:not(.im-mess_fwd):last-of-type > ._im_log_body::after {
        box-shadow: 1px 9px 0px 4px rgba(0, 0, 0, 0) inset, -6px 3px 0px 2px #EDEEF0 inset, 0px 0px 0px 1px rgba(0, 0, 0, 0);
        left: 63px;
        clip: rect(30px, 33px, 42px, 0px);
        border-radius: 30%;
        bottom: 0;
        content: ' ';
        height: 42px;
        position: absolute;
        width: 35px;
        z-index: -1;
    }
    
    /* outcome bubble shape
    -----------------------------------------------------------------------------------*/
    .im-mess.im_out > ._im_log_body,
    .im-mess-stack[data-peer = "/*[[id]]*/"] .im-mess:not(.im-mess_fwd) > ._im_log_body {
        display: inline-block !important;
        float: right !important;
        max-width: 398px !important;
        margin: 0 90px 0 0 !important;
        border-radius: 13px !important;
        background: /*[[mes_color]]*/;
        padding: 5px 8px;
        /*color: #fff !important;*/
    }
    
    .im-mess > ._im_log_body > div[class *= "_im_msg_media"] > *:not(.page_post_sized_thumbs):not(.im_msg_media_doc) {
        border-radius: 10px;
        overflow: hidden;
        /*color: #fff !important;*/
    }
    
    .im-mess > ._im_log_body > div[class *= "_im_msg_media"] > * {
        height: unset !important;
    }
    
    /* outcome messages inner text color
    -----------------------------------------------------------------------------------*/
    .im-mess.im_out ._im_log_body .im_msg_text,
    .im-mess.im_out ._im_log_body > a,
    .im-mess.im_out ._im_log_body .im_browse_msg_images a, 
    .im-mess.im_out ._im_log_body .page_doc_size,
    .im-mess-stack[data-peer = "/*[[id]]*/"] > div > ul > .im-mess:not(.im-mess_fwd) > ._im_log_body, 
    .im-mess-stack[data-peer = "/*[[id]]*/"] .im-mess:not(.im-mess_fwd) ._im_log_body .im_msg_text,
    .im-mess-stack[data-peer = "/*[[id]]*/"] .im-mess:not(.im-mess_fwd) ._im_log_body > a,
    .im-mess-stack[data-peer = "/*[[id]]*/"] .im-mess:not(.im-mess_fwd) ._im_log_body .im_browse_msg_images a,
    .im-mess-stack[data-peer = "/*[[id]]*/"] .im-mess:not(.im-mess_fwd) ._im_log_body .page_doc_size {
        color: #fff;
    }
    
    /* outcome bubble tail
    -----------------------------------------------------------------------------------*/
    .im-mess-stack .im-mess-stack--mess .im-mess.im_out:last-of-type > ._im_log_body::after,
    .im-mess-stack[data-peer = "/*[[id]]*/"] .im-mess-stack--mess .im-mess:not(.im-mess_fwd):last-of-type > ._im_log_body::after {
        box-shadow: -1px 9px 0px 4px rgba(0, 0, 0, 0) inset, 6px 3px 0px 2px /*[[mes_color]]*/ inset, 0px 0px 0px 1px rgba(0, 0, 0, 0);
        right: 63px;
        clip: rect(30px, 33px, 42px, 0px);
        border-radius: 30%;
        bottom: 0;
        content: ' ';
        height: 42px;
        position: absolute;
        width: 35px;
        z-index: -1;
    }
    
    /* bubble inside text
    -----------------------------------------------------------------------------------*/
    .im-mess .im-mess--text:not(:empty), 
    .im-mess ._im_log_body .im_browse_msg_images {
        padding: 5px 8px;
    }

    .im-mess > ._im_log_body > div[class *= "_im_msg_media"] {
        margin-top: -2px;
        margin-left: -5px;
        margin-right: -5px;
        margin-bottom: -2px;
    }
    
    /*.im-mess:not(.im-mess_fwd) > .im-mess--text > .im_msg_text:not(:empty):not(:only-child) {
        display: table-caption;
        width: 100%;
        box-sizing: border-box;
    }*/
    
    .im-mess:not(.im_in):not(.im_out):not(.im-mess_fwd) > ._im_log_body {
        padding: 5px 8px !important;
        box-sizing: padding-box;
    }
    
    /*.im-mess:not(.im_in):not(.im_out):not(.im-mess_fwd) > ._im_log_body > div > * {
        margin: 0px 15px 0px -5px !important;
    }
    
