.im-page--chat-body::before {
        content: "¯\\_(ツ)_/¯";
        color: #797F8A;
        box-sizing: border-box;
        padding: 0;
        text-align: center;
        position: absolute;
        top: 0;
        left: 0;
        z-index: 1;
        background: #fff;
        width: 100%;
        height: 100%;
        opacity: 0.75;
        pointer-events: none;
        transition: opacity 0.3s, font-size 0.4s;
        font-size: 25px;
        font-weight: 400;
        display: flex;
        justify-content: center; /* align horizontal */
        align-items: center;
    }
    
    .im-page-history-w:hover .im-page--chat-body::before {
        font-size: 20px;
        opacity: 0;
    }
    
    .im-page--chat-body {
        position: relative;
    }
    
    .im-page--chat-body-wrap {
        transition: filter 0.3s;
    }
    
