style.css:
@-moz-document domain("e.sfcu.org") {
/* Sanitize 2016 SFCU online banking */
/* ================================= */

/* Remove theme background image */
html {
  background-image: none !important;
}

/* Remove ads, up-selling sidebar and useless navbar */
.navbar.navbar-fixed-top,
.ad-widget-wrapper,
.sidebar.desktop-sidebar {
  display: none !important;
}

/* Remove hardcoded space freed up by navbar*/
.app-container {
  padding-top: 0 !important;
}

/* Remove additional space between navbar and content */
.quicktip-container.main-desktop {
  padding-top: 0 !important;
  min-height: 0 !important;
}

/* Remove space on bottom that was meant for background image */
.main-page-container {
  padding-bottom: 0 !important;
}
}
