style.css:
@-moz-document domain("www.linkedin.com") {
/*REMOVES NAVBAR*/
/*Add connections*/
#spotlight-two-nav-item {
	display: none !important;
}

/*REMOVES FEEDS*/
/*JYMBII Main Feeds*/
.feed-s-update.mh0.Elevation-2dp.relative.feed-s-update--jymbii.jymbii-update.ember-view {
	display: none !important;
}

.nt-feed-card.nt-jymbii-notification.ember-view {
	display: none !important;
}

/*Popular course you may be interested in*/
.feed-s-update--lynda.lynda-update {
	display: none !important;
}

/*People you may know*/
.feed-s-update--aggregated-pymk.aggregated-pymk-update {
	display: none !important;
}

/*X new connections made in your network*/
.feed-s-update--connection.connection-update {
	display: none !important;
}

/*REMOVES SIDEBARS*/
/*Remove Sidebar Footer*/
.nav-footer.right-triggered.ember-view {
    display: none !important;
}
/*Remove Sidebar Footer Bis*/
.region-footer {
    display: none !important;
}

.neptune-grid .right-rail, .neptune-grid .right-rail .right-rail-container {
    box-sizing: inherit;
}

/*"Never lose touch with your contacts"*/
.mn-abi-form.ember-view {
    display: none !important;
}

/*Remove stats left sidebar*/
.feed-container-theme .feed-s-identity-module__widgets .entity-list-item .stat-container {
    display: none !important;
}
/*Line under "Who's view your profile"*/
.feed-identity-module__entity-list .entity-list .entity-list-item:nth-of-type(1) {
    border-bottom: none !important;
}

/*Remove "Connections" button*/
.feed-identity-module__entity-list .entity-list .entity-list-item:nth-of-type(2) {
    display: none !important;
}

/*You have more contacts to connect with*/
.mn-connections-summary__no-top-border-radius.p0 {
    display: none !important;
}

/*Who to follow*/
.feed-s-follows-module {
    display: none !important;
}

/*Ghost*/
.ad-banner-container.ghost {
    display: none !important;
}



/*STYLE*/
/*Static Sidebars*/
.left-rail>div>div {
    position: static !important;
}

.right-rail>div>div {
    position: static !important;
}

/*"Who's view your profile" Button*/
.feed-container-theme .feed-s-identity-module__widgets .entity-list.row .entity-list-item:nth-child(1) .content-container .identity-container .widget-route .description.headline.attributed-text.ember-view {
	background-color: #0084bf !important;
	border-radius: 2px !important;
	color: #fff !important;
    padding: 5px 8px !important;
}

.feed-container-theme .feed-s-identity-module__widgets .entity-list.row .entity-list-item:nth-child(1) .content-container .identity-container .widget-route .description.headline.attributed-text.ember-view:hover {
    background-color: #006097 !important;
}

.feed-container-theme .feed-s-identity-module__widgets {
    padding: 12px 12px 0px 12px;
}

/*Delete Button*/
.pe-form-footer__action--delete.button-secondary-medium-muted {
	background-color: #d82b17 !important;
	border-radius: 2px !important;
	color: #fff !important;
}

/*Top Back Background*/
#extended-nav {
    background: #062138;
}

#outside-nav-typeahead-wormhole.jobs-home-search-nav {
    background: #1c4b72;
}

/*Jobs Background*/
.jobs-search-alerts {
    background: #173651;
}

/*Nav Items No Text*/
.nav-item__title,
.nav-item__dropdown-trigger--icon.svg-icon-wrap {
    display: none !important;
}

.nav-item__icon {
    top: 5px !important;
}

.nav-item__icon.nav-item__icon--inbug {
    top: 0px !important;
}

.nav-item__badge {
    top: 0px !important;
}

.nav-item__link--underline::after {
    display: none;
}

/*ADS*/
/*Main Sidebar & Top*/
.ad-banner-container.ember-view {
    display: none !important;
}
	/*Fixes*/
    .mn-manage-contacts-link {
        padding-top:15px !important;
    }
	.core-rail,
	.left-rail,
	.right-rail {
        padding-top:12px !important;
    }

	.feed-container-theme .stuck .left-rail-container, .feed-container-theme .stuck .right-rail-container {
        margin-top: 0px !important;
    }

	.stuck .nt-content__rail-container {
        margin-top: 0px !important;
    }

/*Groups Sidebar*/
.region-ad {
    display: none !important;
}

/*Sponsored Recommendations*/
.feed-s-update.mh0.Elevation-2dp.relative.feed-s-update--is-sponsored.feed-s-update--share.share-update.ember-view {
    display: none !important;
}



/*TRY PREMIUM*/
/*Top Bar*/
.nav-item__link.nav-item__try-premium.ember-view {
    display: none !important;
}
/*Me Dropdown*/
.nav-dropdown__item.nav-settings__premium-upsell {
    display: none !important;
}
/*Rigth Sidebar*/
.feed-s-identity-module__anchored-widget--premium-upsell {
    display: none !important;
}
/*Unlock profile views*/
.me-wvmp-premium-upsell.me-wvmp-profile-view-card.ember-view {
    display: none !important;
}
/*Get ahead with Premium Career*/
.jobs-upsell__container--reset.jymbii-banner-upsell-container.ember-view {
    display: none !important;
}
/*See jobs where youâ€™d be a top applicant*/
.jymbii-top-applicant-jobs-upsell {
    display: none !important;
}
/*Get exclusive insights on 450,000+ companies*/
.org-premium-container.org-premium-upsell-module.module.ember-view {
    display: none !important;
}
/*Want to better focus your search?*/
.search-facet__upsell.ember-view {
    display: none !important;
}
/*Get ahead with Premium Career*/
.careers-content-module.container.container-premium.jobs-upsell__container--reset.jss-upsell.ember-view {
    display: none !important;
}
/*Job Position*/
.jobs-box.jobs-box--fadein.container-premium.jobs-upsell__container--reset.insights-upsell.ember-view {
    display: none !important;
}
/*Career interests*/
.careers-content-module.jobs-box.container-premium.jobs-upsell__container--reset.jss-upsell.ember-view {
    display: none !important;
}
/*Search Jobs*/
.container-premium.jobs-search-upsell__container-premium.ember-view {
    display: none !important;
}
/*Profile raid card*/
.feed-identity-module__anchored-widget--premium-upsell {
    display: none !important;
}
/*Container*/
.container-premium {
    display: none !important;
}
}
