style.css:
@-moz-document domain("twitter.com") {
/*MAIN FEEDS*/
.wtf-module { 
	display: none !important;
}

.WtfLargeCarouselStreamItem {
	display: none !important;
}

/*You might like*/
.DismissibleModule {
	display: none !important;
}

/*SIDEBARS*/
.dashboard-left {
        display: none !important;
}

.dashboard-right {
        display: none !important;
}

.WhoToFollow {
    display: none !important;
}


.Trends {
    display: none !important;
}

/*FOOTERS*/
.ScrollBump--recentlyFollowed {
        display: none !important;
}

.stream-footer {
        display: none !important;
}


.Footer {
        display: none !important;
}

/*STYLE*/
body  {
    background: #FFF !important;
}

#page-outer {
    background: #FFF !important;
}

#page-container {
    background: #FFF !important;
}

.ProfileHeading-content {
    border: none !important;
}

.stream-item:not(.no-header-background-module) {
    border: none !important;
}

.tweet {
    border-bottom: 1px solid #bababa !important;
}

.Icon--bird {
	display: none !important;
}

/*FIXING*/
#timeline {
	float: none !important;
    margin: auto !important;
}
}
