style.css:
@-moz-document domain("www.gaiaonline.com") {
#content .gaia_navigation_tabs .tab {
	text-align: center;
	display: block;
	margin: -1 -1 -1 0px;
	height: 28px;
	float: left;
	border: 1px solid white;
	background: lightblue;
}
#content .gaia_navigation_tabs .tab.current_tab {
	background-color: lightblue;
	color: white;
	border-bottom: 1px solid white;
}
#content .gaia_navigation_tabs .tab a {
	color: lightblue;
	font-weight: normal;
	line-height: 28px;
	text-decoration: none;
	display: block;
	white-space: nowrap;
}
#content .gaia_navigation_tabs .tab_end {
	display: block;
	float: left;
	background: lightblue url(http://s.cdn.gaiaonline.com/images/gaia_global/body/tabs/rs_secondnav_gradient_113x30.gif);
	width: 110px;
	height: 0px;
}
#content .gaia_navigation_tabs .tab .yuimenu ul {
	margin: 0;
	padding: 0;
	border: 2px solid lightblue;
	border-top: 0;
}
#content .gaia_navigation_tabs .yuimenu li {
	list-style-type: none;
	margin: 0;
	padding: 0;
	background: lightblue;
	border-bottom: 2px solid lightblue;
}
#content .gaia_navigation_tabs .yuimenu li a,
#content .gaia_navigation_tabs .yuimenu li a:hover {
	display: block;
	font: 85% arial, helvetica, clean, sans-serif;
	background-color: lightblue;
	background-image: none;
	color: #3e53a2;
	float: none;
	text-align: left;
	margin: 0;
	padding: 4px;
	font-weight: normal;
	text-indent: 0;
	height: 12px;
	line-height: 12px;
	min-width: 100px;
	width: auto;
	_width: 100px;
	border: 0;
}
.cta-button-xsm.disabled span,
.cta-button-sm.disabled span,
.cta-button-md.disabled span,
.cta-button-lg.disabled span,
.cta-button-xlg.disabled span,
.cta-dropdown-sm.disabled span,
.cta-dropdown-md.disabled span,
.cta-dropdown-sm-label.disabled,
.cta-dropdown-md-label.disabled {
	color: blue;
}
#content a.cta-button-xsm,
#content a.cta-button-sm,
#content a.cta-button-md,
#content a.cta-button-lg,
#content a.cta-button-xlg,
#content a.cta-dropdown-sm,
#content a.cta-dropdown-md,
#content a.cta-dropdown-sm-label,
#content a.cta-dropdown-md-label,
#gaia_content #bd a.cta-button-xsm,
#gaia_content #bd a.cta-button-sm,
#gaia_content #bd a.cta-button-md,
#gaia_content #bd a.cta-button-lg,
#gaia_content #bd a.cta-button-xlg,
#gaia_content #bd a.cta-dropdown-sm,
#gaia_content #bd a.cta-dropdown-md,
#gaia_content #bd a.cta-dropdown-sm-label,
#gaia_content #bd a.cta-dropdown-md-label {
	color: blue;
}
.cta-button-sm.disabled,
.cta-button-sm.blue-button {
	background-position: right -284px;
}
}
