style.css:
/* Hackaday Whitewash*/
/* License: CC0 Public Domain (creativecommons.org/publicdomain/zero/1.0/) */
/* Version: 0.3b (03.02.2017) by stupid939 (userstyles.org/styles/XXXX) */
/* AGENT_SHEET */
@-moz-document domain("hackaday.com") {

/* Remove Unnecessary Ads & Sidebar Items */
.sidebar-ads-holder {display:none;}
h1.widget-title {display:none;}
#hackadaysubscribe-2, #hackadaysubscribe-3 {display:none;}
#hackadayrecentposts-2, #hackadayrecentposts-3 {display:none;}
#categories-2 {display:none;}
#recent-comments-2 {display:none;}
#hackadayfeed-2 {display:none;}
.sidebar-store-ad, .content-ads-holder, .ads-holder {display: none;}
#hackadayfollowus-2, #hackadayfollowus-3 {display:none;}
.site-info {display:none;}
#hackaday_calendar-2 {display:none;}
#leaderboard {display:none;}
#text-11 {display:none;}

/* Re-design the Page Header */
.site-branding .site-title {text-align: left; margin-left: 95px; }
.site-branding .site-title a {color: black; font-size: 8rem;}
.site-branding .logo-img {-webkit-filter: invert(1); filter: invert(1); width: 56px; top: 12px; }
.main-navigation a {color: #1a1a1a;}
.main-navigation .current_page_item>a, .main-navigation .current-menu-item>a, .main-navigation .current_page_ancestor>a {color: #999999;}
.main-navigation {border-bottom: 5px solid #f2f2f2; }
#masthead {width: 50em;}
#site-navigation a:hover {color: #595959; }
.main-navigation li:first-child {border-left: none; }
.main-navigation li {margin-right: 0px; padding-right: 10px; padding-left: 10px; border-left: 2px solid #cccccc;}

/* Background/Layout Changes */
body {background-color: #FFF; background-image: url("http://cdn.backgroundhost.com/backgrounds/subtlepatterns/light_wool.png"); color: black; }
.blog article {background-color: #FFF; border: 2px solid #EEE;}
#main article {background-color: #FFF; border: 2px solid #EEE;}
#content { width: 50em; }
#primary { width: 100%; margin-right: 0px;}
pre {background-color: #eee; }

/* Blog Post Changes */
.blog .entry-header h1 a {color: black;}
.entry-date a {color: #737373;}
h1, h1>a, h2, h2>a {color: black;}
article.post { padding-left: 10px; padding-right: 10px; padding-top: 10px;}
.blog .entry-content {border-top: 1px solid #EEE; margin-top: 10px; }
#main article {padding-right: 10px; padding-left: 10px; }

/* Search Bar */
#secondary {position: absolute; top: 10px; margin-left: 10px; display: inline; margin-top: 0px;}
#secondary .widget_search .search-field { background: #f2f2f2; color: black; }
button, input[type="button"], input[type="reset"], input[type="submit"] { background-color: #cccccc; color: black; }

/* Change Link Colors */
article a, .nav-links a, #content a {color: #28ADE6;}
article a:hover, .nav-links a:hover, #content a:hover {color: #28ADE6; text-decoration: underline;}
article a:visited, .nav-links a:visited, #content a:visited {color: #1586b7;}
.entry-title a {color: black !important; }
.entry-title a:hover { color: #595959 !important; text-decoration: none !important;}
footer.comment-meta {color: white;}
blockquote {background-color: #fff; }
code {background-color: #eee; }

/* Comments/Footer Changes */
.comments-area {margin-top: 50px; }
footer.comment-meta {color: black; }
article.comment-body {padding-left: 10px; padding-right: 10px; }
.site-main .comment-navigation, .site-main .paging-navigation, .site-main .post-navigation {margin-top: -20px; margin-bottom: 0px; }
footer img {-webkit-filter: invert(1); filter: invert(1);}
#colophon {background: white; border-top: 2px solid #EEE;}
#colophon a {color: black; }
#hdTrack {display: none;}
#wpstats {display: none;}

/* Home Page Changes*/
.site-main .widget_recent_entries h2 a, .trending-project a h2 {color: black; }
.site-main .widget_recent_entries h2 a:hover, .trending-project a h2:hover {color: #595959; }
.store-item, .trending-project {background: white; }
.store-item a {color: black; }
.store-item a h2:hover {color: #595959; text-decoration: none;}
.featured-slides-callout a, .featured-slides-callout p {color: black; }
.featured-slides-callout p:hover {text-decoration: underline; color: black; }
.featured-slides-callout a h1:hover {color: #595959;}
.featured-slides a.read_more {color: #28ADE6;}
#content .featured-slides-callout a:hover {text-decoration: none; }

/* Image Changes */
img {background-color: black; }
.wp-caption .wp-caption-text { background-color: #EEE; }

}
