style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tamo.lt") {
  
  a:link {color:/*[[TextColor]]*/ !important;}
  a:visited {color:/*[[TextColor]]*/ !important;}
  a:hover {color:/*[[AccentColor]]*/ !important;}
  a:active {color:/*[[AccentColor]]*/ !important;}
  a:focus {color:/*[[AccentColor]]*/ !important;}
  
  #header, #body, .container > div > table > tbody > tr > td, .fb-item, .alignnone, #footer, .ex_button.white-style, .col-xs-12, .ism-tabs-content, .dalykai, .wday {
    background-color: /*[[BGColor]]*/ !important;
  }
  
  .alignnone {
    background-color: /*[[BGColor]]*/ !important;
    border: 15px solid /*[[BGColor]]*/ !important;
  }
  
  .modal_top {
    background: /*[[AccentColor]]*/ !important;
  }
  
  /*.section {
    background: transparent url("https://static.pexels.com/photos/1852/dawn-landscape-mountains-nature.jpg") no-repeat scroll 0% 0% / cover !important;
    background-attachment: fixed !important;
  }*/
  
  .login, .op-item, .ex_button, .qst > i, .selected {
    background-color: /*[[AccentColor]]*/ !important;
  }
  
  .login:hover, .login:focus, .login:active, .selected, .help_title, .help_list *, .warning_middle *, .login_footer, .selected * {
    color: /*[[BGColor]]*/ !important;
  }
  
  #modal_container_error {
    background: /*[[TextColor]]*/ !important;
  }
  
  ul.help_list li {
    background: url("http://i.imgur.com/LB1OVrP.png") no-repeat left !important;
  }
  
  * {
    color: /*[[TextColor]]*/ !important;
    border-color: /*[[AccentColor]]*/ !important;
  }
}







@-moz-document domain("sistema.tamo.lt"), domain("tamo.15min.lt"), domain("dienynas.tamo.lt") {
  
  a:link {color:/*[[TextColor]]*/ !important;}
  a:visited {color:/*[[TextColor]]*/ !important;}
  a:hover {color:/*[[AccentColor]]*/ !important;}
  a:active {color:/*[[ButtonSelectedColor]]*/ !important;}
  a:focus {color:/*[[ButtonSelectedColor]]*/ !important;}
  
  .container {
    width: 80% !important;
  }
  
  .fa-3x {
    font-size: 12em !important;
  }
  
  /*#scrollable_dienynas {
    width: 70% !important;
  }*/
  
  #s_menu ol li:hover, .weekend, .clickable:hover {
    background-color: /*[[ButtonHoverColor]]*/ !important;
  }

  .is-selected {
   background-color: /*[[ButtonSelectedColor]]*/ !important; 
  }

  .container, .row, .row-table, .top_section_back, .col-md-14, .col-md-14 > div, body, .c_block grey, #c_main, .col-md-3 full_height_col, #s_menu, .col-md-3 full_height_col, .col_right, .menu .column .block, .frozen_column, .list_header_div, #dalykas, #dropDownDalykas, #dropDownLaikotarpis, .c_block c_block clear borderless padL15 padB5 padT5 *, #grupesId, .ui-state-active, .ui-state-default, #GavejoTipas, #Pavadinimas, #Tekstas, .white_space_div, #DalykoId, #DateFilterMode, #DataNuo, #DataIki, #dataNuo, #dataIki, .ui-datepicker-year, .ui-datepicker-header, .ui-datepicker th, .ui-datepicker-calendar, .grey > tr:hover, .ui-dialog, #dialog, .ui-dialog-content, .dalykai, .wday, .tooltip *, #session_time_left, .ui-pnotify, .stack-modal, .brighttheme, .bday_day {
    background-color: /*[[BGColor]]*/ !important;
  }
  
  .status_normalus {
    background-image: none !important;
  }
  
  .c_btn, .c_block .c_btn, .c_btn_input, .s_menu_title, .attention_message, .modal_top, .btn-secondary, .btn-primary, .a1 > div > span[style="color:#F58320;background-color:white"]  {
    background-color: /*[[AccentColor]]*/ !important;
  }
  
  .left_side_banner_section, .right_side_banner_section, #topbanner_section, #bottombanner_section {
    display: none !important;
  }
  
  .container_2 > a {
    pointer-events: none !important;
  }
  
  .c_btn:hover, a.c_btn:hover {
    background-color: /*[[ButtonSelectedColor]]*/ !important;
  }

  #top_section *, #header_section *, .c_tamo_tabs ul.tab_header li a {
    color: /*[[AccentColor]]*/ !important;
  }
  
  .c_tamo_tabs ul.tab_header li.active, #footer, .nolh:hover {
    background-color: /*[[AccentColor]]*/ !important;
  }
  
  .academic_months > a, #infobar, .today  {
    background: /*[[AccentColor]]*/ !important;
  }
  
  .academic_months > a:hover, .white > tr > td > div > input {
    color: /*[[BGColor]]*/ !important;
  }
  
  .header-box > div > div {
    border-left: 1px solid /*[[AccentColor]]*/ !important;
  }
  
  #sysinfo-icon {
    background-image: url("http://i.imgur.com/LB1OVrP.png") !important;
  }
  
  /*[[IconColor]]*/
  
  .c_tamo_tabs ul.tab_header {
    border-bottom: solid 1px /*[[AccentColor]]*/ !important;
  }
  
  .c_btn.utility  {
    border: solid 1px /*[[AccentColor]]*/ !important;
  }
  
  a.block, a.block:visited, a.block:active, a.block:hover {
    border: 3px solid /*[[AccentColor]]*/ !important;
  }
  
  #s_menu {
    border-right: 3px solid /*[[AccentColor]]*/ !important;
  }

  *, .c_tamo_tabs ul.tab_header li i{
    color: /*[[TextColor]]*/ !important;
    border-color: /*[[AccentColor]]*/ !important;
  }
  
  .header-box > div > div > div > a > span[style="color:white;display:inline-block;font-size:11px;position:absolute;right:18px;top:0;background-color:#D63643;padding:1px 5px;border:solid 2px white;border-radius:12px;-moz-border-radius:12px;-webkit-border-radius:12px;"] {
    background-color: /*[[TextColor]]*/ !important;
  }
  
  .col_left {
    background: transparent url("/*[[LoginPicture]]*/") repeat scroll 0% 0% / cover !important;
  }
  
  .main_form_input, .weekend {
    color: /*[[ButtonHoverColor]]*/ !important;
  }
  
  .text_block, .col-md-10 > input, #laikotarpis {
    border: solid 1px /*[[AccentColor]]*/ !important;
    background-color: /*[[BGColor]]*/ !important;
  }

  /*Left menu*/
  
  #s_menu ol li, #s_menu li > a, .weekend {
    color: /*[[TextColor]]*/ !important;
  }
}

