style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pinterest.com") {
  
  /*** Palette ***/
  :root {
    --default-color: #aaaaaa;
    --default-bkgd-color: #282828;
    --brighter-bkgd-color: #343434;
  }

  
  body {
    color: var(--default-color);
    background-color: var(--default-bkgd-color);
  }
  
  /*** pinterest.com ***/
  
  /* Search bar */
  .Header {
    background-color: var(--default-bkgd-color);
  }
  
  .TypeaheadField .tokenizedInputWrapper {
    background-color: #202020 !important;
  }
  
  .TokenizedInput.guided .tokensWrapper .tokenizedItem.inputToken .field:focus {
    color: var(--default-color);
  }
  
  .SearchFormInReact .tokenizedInputWrapper input {
    color: var(--default-color);
  }
  
  /* Search Dropdown */
  .Typeahead {
    background-color: var(--default-bkgd-color);
  }
  
  .Typeahead.typeaheadWithTitles li a.titleItem .typeaheadName {
    color: #d1d1d1 !important;
  }
  
  .Typeahead.typeaheadWithTitles li a {
    color: var(--default-color);
  }
  
  .Typeahead.typeaheadWithTitles li a:hover {
    color: var(--default-color);
    background-color: var(--brighter-bkgd-color) !important;
  }
  
  /* Categories dropdown button */
  .Button.btn.categoriesHeader {
    background-color: var(--default-bkgd-color) !important;
  }
  
  /* Profile button */
  .Header .rightHeaderContent .RightHeader {
    background-color: var(--default-bkgd-color);
  }
  
  .BoardHeader {
    background-color: var(--default-bkgd-color);
  }
  
  /* Notifications/Inbox dropdown - Notifications */
  .Dropdown.newsHubConversationsDropdown {
    background-color: var(--default-bkgd-color);
  }
  
  .newsConversationsTabs {
    background-color: var(--default-bkgd-color);
  }
  
  .newsConversationsTabs .newsConversationsTabButton.activeSegment {
    background-color: var(--brighter-bkgd-color);
  }
  
  .newsConversationsTabs .newsConversationsTabButton {
    background-color: var(--default-bkgd-color);
  }
  
  a.newsHubItem:hover {
    background-color: var(--brighter-bkgd-color);
  }
  
  /* Notifications/Inbox dropdown - Inbox */
  .Button.createConversationTab:focus, .Button.createConversationTab:hover {
    color: var(--default-color);
    background-color: var(--brighter-bkgd-color) !important;
  }
  
  .ConversationListItemReact:hover {
    background-color: var(--brighter-bkgd-color);
  }
  
  /* Notifications/Inbox dropdown - New Message Popup */
  .ConversationHeader .convosHeader, .ConversationHeader .hoverButtonContainer {
    background-color: var(--default-bkgd-color);
  }
  
  .ConversationHeader a {
    color: var(--default-color);
  }
  
  .ConversationPopup .conversationMessagesList, .ConversationMessages .emptyState {
    background-color: var(--default-bkgd-color);
  }
  
  .ConversationMessages .emptyState:hover {
    background-color: var(--brighter-bkgd-color);
  }
  
  .ConversationPopup .Field .content {
    color: var(--default-color);
    background-color: var(--default-bkgd-color);
    border-top-color: var(--default-bkgd-color);
  }
  
  /* Board */
  .BoardInfoBar {
    background-color: rgba(40,40,40,0.95) !important; /* #282828 */
  }
  
  .BoardInfoBar .smallBoardName {
    color: rgba(170,170,170,0.8); /* #aaaaaa */
  }
  
  /* Items Dashboard */
  .Grid.hasFooter {
    background-color: var(--default-bkgd-color);
  }
  
  /* Item Caption Text */
  .Pin.summary .richPinMeta .richPinGridTitle,
  .Pin.summary .pinDescription,
  .Pin.summary .pinCredits > .pinCreditWrapper .creditItem .creditName, 
  .Pin.summary .pinCredits > .pinCreditWrapper .creditItem .creditTitle {
    color: var(--default-color) !important;
  }
  
  /* Right side hovering '+', '?' buttons */
  .Button.footerIcon.borderless {
    background-color: #303030;
  }
  
  /* Popup on clicking '+' button */
  .Modal .modalContent {
    background-color: var(--default-bkgd-color);
  }
  
  .BrowserButtonUpsell .promoWrapper {
    background-color: var(--default-bkgd-color);
    border-color: var(--default-bkgd-color);
  }
  
  .BrowserButtonUpsell .promoHeader {
    color: var(--default-color);
    background-color: var(--default-bkgd-color);
  }
  
  /* Popup on clicking '+' button - Upload a Pin */
  .Button.addPinDropdown:hover {
    background-color: var(--brighter-bkgd-color);
  }
  
  .Button.addPinDropdown:hover span {
    color: var(--default-color);
  }
  
  .ImageUploader {
    background: var(--default-bkgd-color);
  }
  
  .inModal .standardForm>h1 {
    color: var(--default-color);
  }
  
  /* Popup on clicking '+' button - Save from a website */
  .AddPinURL .installBookmarklet {
    background: var(--default-bkgd-color);
  }
  
  .AddPinURL .findPins {
    background-color: var(--default-bkgd-color);
  }
  
  .standardForm input[type="text"], .standardForm input[type="text"]:focus {
    background: var(--default-bkgd-color);
    color: var(--default-color);
  }
  
  /* Help Popup on clicking '?' button */
  .UserEducationGuideDropdown {
    background: var(--default-bkgd-color);
  }
  
  .UserEducationGuideDropdown .helpCenterText {
    color: var(--default-color);
  }
  
  /** Choosen Item Pop-up **/
  
  /* Overlay bkgd */
  .Closeup.canClose {
    background-color: rgba(78,78,78,0.96); /* #4e4e4e */
  }
  
  .Closeup.canClose.brioWhite {
    background-color: rgba(78,78,78,0.96);
  }
  
  /* 'Like', 'Send', 'Save' */
  .PinActionBar, .PinActionBar.sticky {
    background-color: var(--default-bkgd-color); /* rgba(40,40,40,0.95) */
  }
  
  .Pin.detailed .closeupContentSection {
    background-color: var(--default-bkgd-color);
  }
  
  .Button.btn.isBrioFlat {
    background-color: #202020;
    color: var(--default-color);
  }
  
  .Button.btn.isBrioFlat:hover {
    background-color: #3d3d3d;
    color: #d1d1d1;
  }
  
  .Button.btn.isBrioFlat:active, .Button.btn.isBrioFlat:focus, .Button.btn.isBrioFlat.active {
    background-color: #525252;
  }
  
  /* '...' Dropdown - 'Report Pin', 'Embed' */
  .positionModuleElement {
    background-color: #202020;
  }
  
  .PinActionDropdown li .pinActionBarDropdownButton {
    background-color: #202020;
    color: var(--default-color);
  }
  
  .positionModuleCaret {
    filter: invert(88%);
  }
  
  /* Image Lining */
  .PaddedPin .paddedPinLink {
    background-color: var(--default-bkgd-color);
  }
  
  /* Text over Image */
  .CloseupTitleCard, .CloseupTitleCard a, .CloseupTitleCard a {
    color: #4e4e4e;
  }
  
  .CloseupTitleCard:hover, .CloseupTitleCard a:hover, .CloseupTitleCard a:active {
    color: var(--default-color);
  }
  
  /* Image left-right edges */
  .PaddedPin .paddedPinLink {
    background: var(--default-bkgd-color);
  }
    
  /* While Image Loading */
  .Closeup .transitionTitle, .Closeup .transitionImage {
    background: var(--default-bkgd-color);
  }
  
  /* Bottom part */
  .Pin.detailed .closeupContentSection {
    background: var(--default-bkgd-color);
  }
  
  
  /*** pinterest.com/<profile> ***/
  
  /* White band over profile pic */
  .BrioProfileHeaderWrapper .fixedHeader {
    background-color: var(--default-bkgd-color);
  }
  
  /* Profile Pic */
  .UserProfileHeader.profileHeader .header {
    background-color: var(--default-bkgd-color);
  }
  
  /** Nut, Plane, Ellipsis buttons **/
  /* But button -> pinterest.com/settings -> 'Account Basics' */
  .UserSettingsPage .toc {
    background-color: var(--default-bkgd-color);
  }
  
  .standardForm .formSection {
    background-color: var(--default-bkgd-color);
  }
  
  /* '...' button dropdown */
  .bg-white {
    background-color: var(--default-bkgd-color);
  }
  
  .UserDropdown li:hover {
    background-color: var(--brighter-bkgd-color);
  }
  
  /* Plane button dropdown */
  .SocialTypeaheadField.sendShare input, .SocialTypeaheadField.sendShare input:focus {
    background-color: var(--default-bkgd-color);
    color: var(--default-color);
  }
  
  .SharePinBar {
    background-color: var(--default-bkgd-color);
  }
  
  /** 'Boards', 'Pins', 'Likes' buttons */
  .BrioProfileHeaderWrapper .tabBar .tabItem.active {
    background-color: var(--brighter-bkgd-color);
  }
  
  .BrioProfileHeaderWrapper .tabBar .tabItem.active .tabText {
    color: var(--default-color);
  }
  
  /** Secret boards list **/
  .secretBoardsWrapper {
    background-color: #1b1b1b;
  }
  
  /* Images bkgd */
  .bg-super-light-gray {
    background-color: var(--brighter-bkgd-color);
  }
  
  /*** Tip pop-up pane ***/
  .PinCreate {
    background-color: var(--default-bkgd-color);
    color: var(--default-color);
  }
  
  /* left half */
  .PinCreate.TwoPaneModal .left.pane {
    background-color: var(--default-bkgd-color);
  }
  
  /* right half */
  .TwoPaneModal .right.pane {
    background-color: var(--default-bkgd-color);
  }
  
  /* caption */
  .Pin.summary.cloned .pinDescription, .Pin.summary.cloned .pinDescription:hover {
    background-color: var(--default-bkgd-color);
    color: var(--default-color);
  }
  
  /*** pinterest.com/<profile>/<board> ***/
  
  /* 'Edit this Pin' Pop-up pane */
  
  form.standardForm > ul > li {
    background-color: var(--default-bkgd-color);
  }
  
  form.standardForm > .pinFormHeader {
    color: var(--default-color);
  }
  
  #pinFormDescription {
    color: var(--default-color);
    background-color: #333333;
  }
  
  /*** 'Pin it' pop-up window ***/
  
  /* 'Choose board' */
  .top {
    background-color: var(--default-bkgd-color);
  }
  
  /* Search */
  input.Input.Module.name {
    color: var(--default-color);
    background-color: var(--default-bkgd-color);
  }
  
  /* 'Create board' */
  .selectionBody {
    background-color: var(--default-bkgd-color);
  }
  
  /* 'Top Choices', 'All boards' */
  .sectionHeading, .sectionItems {
    background-color: var(--default-bkgd-color);
  }
  
  /* option over which hovered and selected */
  .PinCreateBoardPicker .Picker .item.activeItem, 
  .PinCreateBoardPicker .Picker .item.selected, 
  .PinCreateBoardPicker .Picker .item:hover {
    color: var(--default-color);
    background-color: var(--brighter-bkgd-color);
  }
  
  /* After Successful Save */
  .standardForm, .Module.PinCreateSuccessBoard, .alsoOn, .boardWrapper, .formFooter {
    color: var(--default-color);
    background-color: var(--default-bkgd-color) !important;
  }
  
  /**** Pinterest Firefox/Chrome plugin - on click ****/
  div#bd div#ct span.thumb span.ft {
    background-color: var(--default-bkgd-color);
  }
  
  div#bd div#ct span.thumb span.ft span.ftItem {
    color: var(--default-color);
  }
  
  
}
